(255, 42, 6, 'dis-udaas-raat-jo-poyoon-pahar', 'ڏس اداس رات جو پويون پھر،\r\nويران تنھنجي دل جيئن سارو شھر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(256, 42, 6, 'thirndi-thabrrjaan-tho-tunhinje-gharu-aggyan', 'ٿڙندي ٿاٻڙجان ٿو تنھنجي گهر اڳيان،\r\nننڊ جا جهوٽا اکين ۾ آ  ڳھر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(257, 42, 6, 'kedi-peerraa-lorrh-aen-biyun-kaiftyun', 'ڪيڏي پيڙا لوڙھ ۽ ٻيون ڪيفيتون،\r\nدک پيو ڀوڳي سمورو اڄ دھر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(258, 42, 6, 'ddaat-tunhinje-dukh-men-wayo-daaunu-b', 'ڏات تنھنجي ڏک ۾ ويو ڏانءُ بہ،\r\nڪين قافيو شعر ۾ ڪوبه بحر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(259, 42, 6, 'sobh-maanni-thi-saghyas-na-surkharoo', 'سوڀ ماڻي ٿي سگھيس نہ سرخرو،\r\nسونھن تنھنجيءَ ھو ڪيو سانئڻ سحر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(260, 42, 6, 'waar-veengas-wehjhandi-huaa-chhoriya', 'وار وينگس وھنجندي ھئا ڇوڙيا،\r\nپئي سنڌوءَ جي سير ۾ اڀري لھر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(261, 42, 6, 'deed-tuhnjia-ho-Hassan-deewano-kayo', 'ديد تنھنجيءَ ھو ”حسن“ ديوانو ڪيو،\r\nڪن چيو ڪيڏو ڪرم، ڪن چيو ڪيڏو قھر.', NULL, 'sd', '2023-09-13 13:31:25', '2023-09-14 04:20:13', '2023-09-14 04:20:13'),
(262, 43, 6, 'soohan-saan-pahnjo-jadahin-sahakar-thi-piyo', 'سونھن سان پنھنجو جڏھن سھڪار ٿي پيو،\r\nعقل کان بس نابري انڪار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(263, 43, 6, 'murk-keahnji-mott-men-murkyu-ho-mun', 'مرڪ ڪنھنجي موٽ ۾ مرڪيو ھو مون،\r\nاِئين پنھنجي عشق جو اظھار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(264, 43, 6, 'to-hawelia-je-dari-maan-paee-ditho', 'تو حويليءَ جي دريءَ مان پئي ڏٺو،\r\nمون سڪايل جو تہ بس ديدار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(265, 43, 6, 'band-akhiyan-men-saween-sapna-tunhinjaa', 'بند اکين ۾ سوين سپنا تنھنجا،\r\nاڻ ڏٺو توسان تہ ڄڻ اقرار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(266, 43, 6, 'to-jadahin-sargoshia-men-galhayu', 'تو جڏھن سرگوشيءَ ۾ ڳالهايو،\r\nشرنائيءَ تي سر اُھو ملهار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(267, 43, 6, 'jahin-b-tunhinje-soohan-je-sarah-kai', 'جنھن بہ تنھنجي سونھن جي ساراھ ڪئي،\r\nشاھ، سانگي، سو حمل، منٺار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(268, 43, 6, 'kanhin-chayo-thaa-kandhu-ghurjan-qurb-men', 'ڪنھن چيو ٿا ڪنڌ گھرجن قرب ۾،\r\nسر ”حَسن“ ھٿ تي رکي نروار ٿي پيو.', NULL, 'sd', '2023-09-13 13:34:42', '2023-09-13 13:34:42', NULL),
(269, 44, 6, 'zindagi-to-bin-ean-Aa-be-samar', 'زندگي تو بن ائين آ بي ثمر،\r\nڄڻ بنان پاڻيءَ اڇو ڪوئي ڪڪر.', NULL, 'sd', '2023-09-13 13:38:34', '2023-09-13 13:38:34', NULL),
(270, 44, 6, 'piyaar-jo-pahinje-pohch-khan-thiyo-pare', 'پيار جو پنھنجي پھچ کان ٿيو پري،\r\nسار پر پوءِ بہ ڇڏي ڪين پچر.', NULL, 'sd', '2023-09-13 13:38:34', '2023-09-13 13:38:34', NULL),
(271, 44, 6, 'baah-paee-saari-badan-men-kaa-bbarri', 'باھ پئي ساري بدن ۾ ڪا ٻري،\r\nدل بہ ڄڻ ميڙ ٽانڊن ۾ آڱر.', NULL, 'sd', '2023-09-13 13:38:34', '2023-09-13 13:38:34', NULL),
(272, 44, 6, 'sabh-sitaraa-sanat-men-sumhyu-pawan', 'سڀ ستارا سانت ۾ سمھيو پون،\r\nچنڊ بہ چپ ۾ ڪڇي ڪونہ قمر.', NULL, 'sd', '2023-09-13 13:38:34', '2023-09-13 13:38:34', NULL),
(273, 44, 6, 'maan-b-hiku-majmoouo-chhapainr-tho-ghuraan', 'مان بہ ھڪ مجموعو ڇپائڻ ٿو گھران،\r\nذات تنھنجيءَ تي ھجن شعر مختصر.', NULL, 'sd', '2023-09-13 13:38:34', '2023-09-13 13:38:34', NULL),
