(473, 63, 6, 'shahar-jaanaan-men-jatt-aahen-bitho-tun-tanday-bagay', 'شھر جانان ۾ جت آھين، بيٺو تون ٽنڊي باگي،\r\nاُتي ئي محبتن جي دل، وڃي ھن مير ماڻي آ.', NULL, 'sd', '2023-09-22 13:50:22', '2023-09-22 13:50:22', NULL),
(474, 64, 6, 'ishiq-men-zaat-tasawur-khan-mathe-chho-thi-thyee', 'عشق ۾ ذات تصور کان مٿي ڇو ٿي ٿئي؟\r\nموت ۾ مات تصور کان مٿي ڇو ٿي ٿئي؟', NULL, 'sd', '2023-09-22 14:07:18', '2023-09-22 14:07:18', NULL),
(475, 64, 6, 'jo-phirri-fikar-ta-kangaal-kare-duniya-men', 'جو ڦري فڪر تہ ڪنگال ڪري دُنيا ۾،\r\nجا ڏئي ڏات تصور کان مٿي ڇو ٿي ٿئي؟', NULL, 'sd', '2023-09-22 14:07:18', '2023-09-22 14:07:18', NULL),
(476, 64, 6, 'soch-muhinje-b-rugo-chand-sitaran-taaen', 'سوچ منھنجي بہ رڳو چند ستارن تائين،\r\nڪل ڪائنات تصور کان مٿي ڇو ٿي ٿئي؟', NULL, 'sd', '2023-09-22 14:07:18', '2023-09-22 14:07:18', NULL),
(477, 64, 6, 'hina-banaan-deenhan-jo-anat-na-chherro-koi', 'ھن بنان ڏينھن جو انت نہ ڇيڙو ڪوئي،\r\nھن سا گڏ رات تصور کان مٿي ڇو ٿي ٿئي؟', NULL, 'sd', '2023-09-22 14:07:18', '2023-09-22 14:07:18', NULL),
(478, 64, 6, 'meehan-men-neehan-bhinay-tho-ta-pusse-thi-dil-b', 'مينھن ۾ نينھن ڀِٺي ٿو تہ پُسي ٿي دل بہ،\r\nاھڙي برسات تصور کان مٿي ڇو ٿي ٿئي؟', NULL, 'sd', '2023-09-22 14:07:18', '2023-09-22 14:07:18', NULL),
(479, 64, 6, 'hun-je-galihyun-je-galhyan-manjh-winjyal-aahyan', 'ھُن جي ڳالهين جي ڳلين منجهہ وڃايل آھيان،\r\nھن جي ھر بات تصور کان مٿي ڇو ٿي ٿئي؟!', NULL, 'sd', '2023-09-22 14:07:18', '2023-09-22 14:07:18', NULL),
(480, 65, 6, 'kakri-hui-jaa-beethi-khaali-karn-karn-thi-na-saghe', 'ڪڪري ھئي جا بيٺي خالي ڪڻ ڪڻ ٿي نہ سگھي،\r\nويچاري وئي واپس آخر، سانوڻ ٿي نہ سگھي.', NULL, 'sd', '2023-09-22 14:11:21', '2023-09-22 14:11:21', NULL),
(481, 65, 6, 'khaathu-ta-aahe-khaathu-inahia-je-maath-b-torrien-tun', 'ڪاٺ تہ آھي ڪاٺ، انھيءَ جي ماٺ بہ ٽوڙين تون،\r\nٽيبل آخر ٽيبل ٿي، ڏاڪڻ ٿي نہ سگھي.', NULL, 'sd', '2023-09-22 14:11:21', '2023-09-22 14:11:21', NULL),
(482, 65, 6, 'duniyaa-saari-dard-hazaren-milndi-khani-murki', 'دنيا ساري، درد ھزارين، ملندي کڻي مرڪي،\r\nپياري آھي پياري رھندي پاپڻ ٿي نہ سگھي.', NULL, 'sd', '2023-09-22 14:11:21', '2023-09-22 14:11:21', NULL),
(483, 65, 6, 'to-ta-patyo-ho-parran-khan-jo-balakpanr-bbotto', 'تو تہ پٽيو ھو پاڙن کان جو ٻالڪپڻ ٻوٽو،\r\nجا نم ننڍي ھوندي ماريئي سا وڻ نہ ٿي سگھي.', NULL, 'sd', '2023-09-22 14:11:21', '2023-09-22 14:11:21', NULL),
(484, 65, 6, 'dawa-korry-kamtar-manhun-sangti-koh-kajan', 'دعوى ڪوڙي، ڪمتر ماڻهو، سنگتي ڪوھ ڪجن،\r\nھلي ڪانہ ”حسن“ سان گڏجي، سا ڄڻ ٿي نہ سگھي.', NULL, 'sd', '2023-09-22 14:11:21', '2023-09-22 14:11:21', NULL),
(485, 66, 6, 'kolhiyeen-saan-gadu-kolhiyee-thiyaan', 'ڪولھين سان گڏ ڪولهي ٿيان،\r\nھا ھا ھا، آئون ھولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(486, 66, 6, 'tunhinjo-udryal-ishiq-hujaan', 'تنھنجو اُڊڙيل عشق ھجان،\r\nپنھنجي ڦاٽل جهولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(487, 66, 6, 'waddanrn-jo-maan-virso-thiyaan', 'وڏڙن جو مان ورثو ٿيان،\r\nٻارن جي مان ٻولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(488, 66, 6, 'tun-jo-samund-hujeen-maan-tunhinje', 'تون جو سمنڊ ھجين، مان تنھنجي،\r\nڇو نہ ڀلا پوءِ ڇولي ٿيان؟', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(489, 66, 6, 'obhaaro-maan-ishiq-hujaan', 'اوڀارو مان عشق ھجان،\r\nلھواري مان لولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(490, 66, 6, 'ochangaran-jo-anat-thiyaan', 'اوڇنگارن جو انت ٿيان،\r\nٽھڪن جي مان ٽولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(491, 66, 6, 'mushkil-mushkil-mushkil-thiyaan', 'مشڪل مشڪل مشڪل ٿيان،\r\nسؤلي سؤلي سؤلي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(492, 66, 6, 'lafuzan-je-maan-lajj-rakhan', 'لفظن جي مان لڄ رکان،\r\nگُفتي جي مان گولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(493, 66, 6, 'yaar-husan-jo-shair-thiyaan', 'يار حسن جو شعر ٿيان،\r\nيار حسن جي ٻولي ٿيان.', NULL, 'sd', '2023-09-22 14:15:30', '2023-09-22 14:15:30', NULL),
(494, 67, 6, 'tamana-taak-men-aahe', 'تمنا تاڪ ۾ آھي،\r\nلِڪي توکي ڏسڻ چاھي.', NULL, 'sd', '2023-09-22 14:19:50', '2023-09-22 14:19:50', NULL),
(495, 67, 6, 'musawar-us-maan-aakhir', 'مصور اُس مان آخر،\r\nتنھنجي تصوير ٿو ٺاھي.', NULL, 'sd', '2023-09-22 14:19:50', '2023-09-22 14:19:50', NULL),
(496, 67, 6, 'pharyan-khe-jhurr-jhali-bitho', 'ڦڙين کي جُھڙ جهلي بيٺو،\r\nچوين جنھن وقت برسائي.', NULL, 'sd', '2023-09-22 14:19:50', '2023-09-22 14:19:50', NULL),
(497, 67, 6, 'Hawaa-tunhinje-acharn-taaen', 'ھوا تنھنجي اچڻ تائين،\r\nگلن تي ملڪ ترسائي.', NULL, 'sd', '2023-09-22 14:19:50', '2023-09-22 14:19:50', NULL),
(498, 67, 6, 'agar-aahen-Hassan-aahe', 'اگر آھين ”حسن“ آھي،\r\nسوا تنھنجي صفا ناھي.', NULL, 'sd', '2023-09-22 14:19:50', '2023-09-22 14:19:50', NULL),
(499, 68, 7, 'wathee-har-har-janam-warbo-mithaa-mehran-men-milbo', 'وٺي ھر ھر جنم وربو مٺا مھراڻ ۾ ملبو، \r\nختم اونداه ٿي ويندي چٽيءَ چانڊاڻ ۾ ملبو.', NULL, 'sd', '2023-09-22 21:30:37', '2024-03-21 11:21:30', '2024-03-21 11:21:30'),
(500, 68, 7, 'pahnji-jaa-jang-Aa-jaari-saa-thindi-neth-soobhari', '‫پنهنجي جا جنگ آ جاري سا ٿيندي نيٺ سوڀاري، \r\nمري ويندا مڙيئي ماري سچيءَ سرھاڻ ملبو.', NULL, 'sd', '2023-09-22 21:30:37', '2024-03-21 11:21:30', '2024-03-21 11:21:30'),
(501, 68, 7, 'hujay-urap-yaa-libanon-jite-maaryo-wayo-insaan', '‫هجي يورپ يا لبنان جتي ماريو ويو انسان، \r\nانھي ماڻهو سندي ارمان ۽ رت ڇاڻ ۾ ملبو.', NULL, 'sd', '2023-09-22 21:30:37', '2024-03-21 11:21:30', '2024-03-21 11:21:30'),
(502, 68, 7, 'thiyaa-je-qoam-taan-qurbaan-unhan-jaa-aahin-lakhain-ahsaan', '‫ٿيا جي قوم تان قربان انهن جا آھن لکين احسان، \r\nشهيدن جي ريٽي رت سان رتيءَ  لالاڻ ۾ ملبو.', NULL, 'sd', '2023-09-22 21:30:37', '2024-03-21 11:21:30', '2024-03-21 11:21:30'),
(503, 68, 7, 'wari-warnda-ta-waskara-wasi-pawanda-hi-putt-saaraa', '‫وري ورندا تہ وسڪارا وسي پوندا ھي پٽ سارا، \r\nمارو ٿيندا تہ موچارا مينھن مانڊاڻ ۾ ملبو.', NULL, 'sd', '2023-09-22 21:30:37', '2024-03-21 11:21:30', '2024-03-21 11:21:30'),
(504, 69, 6, 'naen-khe-purarno-kaje-dil-ddijay', 'نئين کي پراڻو ڪجي، دل ڏجي،\r\nسندءِ دل کي ڄاڻو ڪجي، دل ڏجي.', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(505, 69, 6, 'milan-khe-rilli-jiyaan-mathan-odhjay', 'ملڻ کي رليءَ جيان مٿان اوڍجي،\r\nوڇوڙو وڇاڻو ڪجي، دل ڏجي.', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(506, 69, 6, 'sajarn-khan-siwaa-je-sumhanr-sochjai', 'سڄڻ کان سوا جي سمهڻ سوچجي،\r\nپٿر جو وھاڻو ڪجي، دل ڏجي؟', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(507, 69, 6, 'umlah-dil-je-qeemat-ta-kehry-lage', 'املهہ دل جي قيمت تہ ڪھڙي لڳي،\r\nٽڪو تنھنجو ناڻو ڪجي، دل ڏجي', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(508, 69, 6, 'farshte-khudaa-je-zaroort-na-kaa', 'فرشتي، خدا جي ضرورت نہ ڪا،\r\nرڳو خود کي ماڻهو ڪجي، دل ڏجي.', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(509, 69, 6, 'majazan-je-moomal-ragan-men-rakhe', 'مجازن جي مومل رڳن ۾ رکي،\r\nرڳو راڻو راڻو ڪجي، دل ڏجي.', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(510, 69, 6, 'udaaman-khan-aggte-ukrandi-Hassan', 'اُڏامن کان اڳتي اُڪرندي ”حسن“\r\nپکين کان پڇاڻو ڪجي، دل ڏجي.', NULL, 'sd', '2023-09-22 22:03:44', '2023-09-22 22:03:44', NULL),
(511, 70, 6, 'pabban-te-ubho-thi-disaan-yaar-tokhe', 'پٻن تي اڀو ٿي ڏسان يار توکي،\r\nمٿي مٿڀرو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(512, 70, 6, 'kajal-kararro-thi-disaan-yaar-tokhe', 'ڪجل ڪارڙو ٿي ڏسان يار توکي،\r\nسجل سانورو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(513, 70, 6, 'khaddon-khandu-khobbo-darro-aen-dabbu-thi', 'کڏون، کنڊ، کوٻو، دڙو ۽ دٻُو ٿي،\r\nترائي ترو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(514, 70, 6, 'safa-be-wazan-thi-disaan-yaar-tokhe', 'صفا بي وزن ٿي ڏسان يار توکي،\r\n۽ ڳوڙهو ڳرو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(515, 70, 6, 'kino-aen-sutho-thi-disaan-yaar-tokhe', 'ڪِنو ۽ سُٺو ٿي ڏسان يار توکي،\r\nڀلو ۽ ڍلو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(516, 70, 6, 'charyu-aen-kharyo-thi-disaan-yaar-tokhe', 'چريو ۽ کريو ٿي ڏسان يار توکي،\r\nصفا ٺپ انڌو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(517, 70, 6, 'budhrro-budhhal-thi-disaan-yaar-tokhe', 'ٻڍڙو ٻڍل ٿي ڏسان يار توکي،\r\n”حسن“ ڇوڪرو ٿي ڏسان يار توکي.', NULL, 'sd', '2023-09-22 22:08:55', '2023-09-22 22:08:55', NULL),
(518, 71, 6, 'jjamaaroon-guzaare-milaan-yaar-tosaan', 'ڄمارون گذاري، ملان يارَ توسان،\r\nسڀئي وقت واري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(519, 71, 6, 'katiyan-je-kinaare-milaan-yaar-tosaan', 'ڪَتين جي ڪناري، ملان يارَ توسان،\r\n۽ سورج اڀاري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(520, 71, 6, 'sajjo-ddeeh-mun-wati-jo-wetho-hujy', 'سڄو ڏيھہ مون وٽ جو ويٺو ھجي،\r\nسڀن کي اٿاري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(521, 71, 6, 'pakhi-je-pukaray-milaan-yaar-tosaan', 'پکي جي پُڪاري، ملان يارَ توسان،\r\nندي جي نھاري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(522, 71, 6, 'akhiyun-biay-ujaray-milaan-yaar-tosaan', 'اکيون ٻئي اُجاري، ملان يارَ توسان،\r\n۽ سپنن سھاري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(523, 71, 6, 'jabal-te-jhuggia-men-aen-gazan-churan-men', 'جبل تي، جُھڳيءَ ۾، ۽ غازن چُرن ۾،\r\n۽ ڪھڙي سِتاري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(524, 71, 6, 'Hassan-je-ta-hayaati-tolaai-aahe', '”حسن“ جي تہ حياتي، تولاءِ آھي،\r\nجياري ۽ ماري، ملان يارَ توسان.', NULL, 'sd', '2023-09-23 04:36:23', '2023-09-23 04:36:23', NULL),
(525, 72, 6, 'sajji-raat-saari-thi-beethi-rahe', 'سڄي رات ساري، ٿي بيٺي رھي،\r\nلڳي رات ڪاري، ٿي بيٺي رھي.', NULL, 'sd', '2023-09-23 04:41:08', '2023-09-23 04:41:08', NULL),
(526, 72, 6, 'safa-saah-boosattji-tho-piyo', 'صفا ساھ ٻوساٽجي ٿو پيو،\r\nوچو وچ واري، ٿي بيٺي رھي.', NULL, 'sd', '2023-09-23 04:41:08', '2023-09-23 04:41:08', NULL),
(527, 72, 6, 'haqeeqat-men-tasweer-aahe-lagal', 'حقيقت ۾ تصوير آھي لڳل،\r\nدريءَ منجهہ ساري، ٿي بيٺي رھي.', NULL, 'sd', '2023-09-23 04:41:08', '2023-09-23 04:41:08', NULL),
(528, 72, 6, 'puthiyaan-ho-pasham-jo-naram-gotharro', 'پٺيان ھو پشم جو نرم ڳوٺڙو،\r\nاڳيان ڪرٽ آري، ٿي بيٺي رھي.', NULL, 'sd', '2023-09-23 04:41:08', '2023-09-23 04:41:08', NULL),
(529, 72, 6, 'chapan-maan-piyaasi-bikhaari-bannri', 'چپن مان پياسي، بکاري بڻي،\r\nاکين مان اُڃاري، ٿي بيٺي رھي.', NULL, 'sd', '2023-09-23 04:41:08', '2023-09-23 04:41:08', NULL),
(530, 72, 6, 'hali-chho-na-thi-heea-hayaati-Hassan', 'ھلي ڇو نہ ٿي ھيءَ حياتي ”حسن“\r\nٽُٽي پوءِ ساري، ٿي بيٺي رھي.', NULL, 'sd', '2023-09-23 04:41:08', '2023-09-23 04:41:08', NULL),
(531, 73, 6, 'hina-umar-bemisaal-men-bebak-mohabbatu', 'ھن عمر بيمثال ۾، بيباڪ محبتون،\r\nننگا نصيب منھنجا ٿيون، چاڪ محبتون.', NULL, 'sd', '2023-09-23 04:44:03', '2023-09-23 04:44:03', NULL),
(532, 73, 6, 'beethi-badan-mun-b-khani-baah-te-rakhiyo', 'بيٺي بدن مون بہ کڻي باھ تي رکيو،\r\nگهري گناھ نيٺ ڪيون، پاڪ محبتون.', NULL, 'sd', '2023-09-23 04:44:03', '2023-09-23 04:44:03', NULL),
(533, 73, 6, 'karrhay-tattia-jo-dees-tho-aaon-tadahn-ghumaan', 'ڪاڙھي تَتي جو ديس ٿو آئون تڏھن گھمان،\r\nمنجهند جتان ملي بہ وڃن، ٽاڪ محبتون.', NULL, 'sd', '2023-09-23 04:44:03', '2023-09-23 04:44:03', NULL);
