(940, 1, 2, 'ae-daahir', 'ae daahir !\r\nasan jaa soorma\r\ndees tunhinji je\r\ndhartia te\r\nhar roz haanray ” bin qaasim “ jo olaad\r\nnaen sir janam wathee tho.', NULL, 'en', '2023-09-30 23:42:01', '2023-09-30 23:43:17', '2023-09-30 23:43:17'),
(941, 1, 2, 'dees-tunhinji-je', 'dees tunhinji je\r\ndhartia te\r\nhar roz haanray zehan je zameen\r\nkirodh je kirat \r\nhawale kai waji thi', NULL, 'en', '2023-09-30 23:42:01', '2023-09-30 23:43:17', '2023-09-30 23:43:17'),
(942, 1, 2, 'ko-mahabaai-raag', 'ko mahabaai raag\r\nkakray je karatyal jhiibh saan\r\njhoomi ggaaye tho\r\naen andhi ishiq sandai\r\nhaam hannri tho', NULL, 'en', '2023-09-30 23:42:01', '2023-09-30 23:43:17', '2023-09-30 23:43:17'),
