(947, 1, 2, 'ae-daahir-asan-ja-soorma', 'ae daahir!\r\nasan jaa soorma\r\ndees tunhinji je\r\ndhartia te\r\nhar roz haanray ” ghulaami “\r\nbadsoorati apaindi waji thi!', NULL, 'en', '2023-09-30 23:43:17', '2023-09-30 23:43:17', NULL),
(948, 1, 2, 'ae-daahir', 'اي ڏاھر !\r\nاسان جا سُورما\r\nديس تنهنجي جي\r\nڌرتيءَ تي\r\nھر روز ھاڻي ” بن قاسم “ جو اولاد\r\nنئين سِر جنم وٺي ٿو.', NULL, 'sd', '2023-09-30 23:44:01', '2023-10-05 14:16:43', '2023-10-05 14:16:43'),
(949, 1, 2, 'dees-tunhinji-je', 'ديس تنهنجي جي\r\nڌرتيءَ تي\r\nھر روز ھاڻي ذھن جي زمين\r\nڪروڌ جي ڪِرت \r\nحوالي ڪئي وڃي ٿي', NULL, 'sd', '2023-09-30 23:44:01', '2023-10-05 14:16:43', '2023-10-05 14:16:43'),
(950, 1, 2, 'ko-mahabaai-raag', 'ڪو مرحبائي راڳ\r\nڪاڪڙي جي ڪراٽيل ڄِڀَ سان\r\nجهومي ڳائي ٿو\r\n۽ انڌي عشق سندي\r\nحامَ ھڻي ٿو', NULL, 'sd', '2023-09-30 23:44:01', '2023-10-05 14:16:43', '2023-10-05 14:16:43'),
(951, 1, 2, 'ae-daahir-asan-ja-soorma', 'اي ڏاھر!\r\nاسان جا سُورما\r\nديس تنهنجي جي\r\nڌرتيءَ تي\r\nھر روز ھاڻي ” غلامي “\r\nبدصُورتي اپنائيندي وڃي ٿي!', NULL, 'sd', '2023-09-30 23:44:01', '2023-10-05 14:16:43', '2023-10-05 14:16:43'),
(952, 3, 6, 'kudhi-aahyan-safa-kamtar-kaseero-chhaa-kannri-aahyan', 'kudhi aahyan, safa kamtar, kaseero chhaa kannri aahyan,\r\nmagar mursiyal mithi naale, wathan je lai bannri aahyan', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(953, 3, 6, 'wathee-wairag-jaa-rastaa-puchhayan-paee-puhnoo-pahnjo', 'wathee wairag jaa rastaa, puchhayan paee puhnoo pahnjo,\r\nkariyaan dham dham dhamalon thi, kare chham chham chhanri aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(954, 3, 6, 'ali-ai-olaad-tunhinje-te-kariyaan-maan-saah-sir-sadqai', 'ali ai olaad tunhinje te, kariyaan maan saah sir sadqai,\r\nfaqeern aen firaaqan je, mathe jaa maan phanri aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(955, 3, 6, 'ajeeba-odro-kar-tun-dai-kaa-jaai-peran-men', 'ajeeba odro kar tun, dai kaa jaai peran men,\r\nmilo mun saan mithaa mursiyal, wichhoro chho wanri aahyan?', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(956, 3, 6, 'safa-weeran-wattan-te-aahyan-wethe-wichoryal-thi', 'safa weeran wattan te, aahyan wethe wichoryal thi\r\npireen jo piyaar je milando, ta poi maan padmanri aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(957, 3, 6, 'qalandar-laal-jo-kishto-ragan-aen-rooh-jo-rishto', 'qalandar laal jo kishto, ragan aen rooh jo rishto,\r\nwadhayan paee watto tunhinjo, ghatainr khan ghani aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(958, 3, 6, 'nazara-kaj-noor-je-noori-ugharay-aen-abbali-te', 'nazara kaj noor je noori, ugharay aen abbali te,\r\ngunahan men gatal aahyan, bhale chhaa bhe khani aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(959, 3, 6, 'samoora-taaj-talwaroon-kariyaan-sabh-takht-to-sadqai', 'samoora taaj, talwaroon, kariyaan sabh takht to sadqai,\r\nghulaman je ghulaman men, agar je maan ganrai aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(960, 3, 6, 'khudaa-jo-sunhan-ta-muhinje-dil-paee-phaathi-budharn-je-laai', 'khudaa jo sunhan ta muhinje dil, paee phaathi budharn je laai,\r\nrugo chau hina chhoria jo, muhammad sa maan dharni aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
(961, 3, 6, 'nimanri-naaz-aen-naari-bandi-tunhinje-aahyan-bary', 'nimanri naaz aen naari, bandi tunhinje aahyan bary,\r\nakeli ishiq men ahmad, Hassan hikray jarni aahyan.', NULL, 'en', '2023-09-30 23:47:35', '2023-09-30 23:50:08', '2023-09-30 23:50:08'),
