(972, 5, 6, 'waau-men-khushboo-ghulee-wei-hali', 'waau men khushboo ghulee wei hali,\r\nwaas wathandar bin men rulii wei hali.', NULL, 'en', '2023-09-30 23:52:24', '2023-09-30 23:52:24', NULL),
(973, 5, 6, 'tun-alaay-kanhin-kinaare-pai-ruleen', 'tun alaay kanhin kinaare pai ruleen,\r\nhoo~a ache jhoolo jhuli wei hali.', NULL, 'en', '2023-09-30 23:52:24', '2023-09-30 23:52:24', NULL),
(974, 5, 6, 'sik-jo-hee-samund-maatho-thi-wayo', 'sik jo hee samund maatho thi wayo,\r\nchhik je chholi chhuli wei hali.', NULL, 'en', '2023-09-30 23:52:24', '2023-09-30 23:52:24', NULL),
(975, 5, 6, 'to-puchiyo-yaa-maath-men-eee-ochito', 'to puchiyo yaa maath men eee ochito,\r\ngandh galihyun je ghulee wei hali.', NULL, 'en', '2023-09-30 23:52:24', '2023-09-30 23:52:24', NULL),
(976, 5, 6, 'mahik-waangy-tahak-waangy-wikhnrday', 'mahik waangy tahak waangy wikhnrday,\r\nhoo~a ta kamre men huli wei hali.', NULL, 'en', '2023-09-30 23:52:24', '2023-09-30 23:52:24', NULL),
(977, 6, 6, 'kandyun-lodd-saan-hodd-chhaa-hi-hawaaun', 'kandyun lodd saan hodd chhaa hi hawaaun,\r\ntunhinje torr te ghor thi piyun ghataaun.', NULL, 'en', '2023-09-30 23:54:28', '2023-09-30 23:54:28', NULL),
(978, 6, 6, 'tunhinje-moh-je-ddoh-jakri-wadho', 'tunhinje mohh je ddoh jakri wadho,\r\nzanjeeran aen zulfan joon ardyun adaaun.', NULL, 'en', '2023-09-30 23:54:28', '2023-09-30 23:54:28', NULL),
(979, 6, 6, 'aseen-gas-je-was-men-e-na-rahyaasin', 'aseen gas je was men e na rahyaasin,\r\nakhiyan e akhiyan men jo rookyaun raahun.', NULL, 'en', '2023-09-30 23:54:28', '2023-09-30 23:54:28', NULL),
(980, 6, 6, 'uhe-mahik-men-tahak-partaa-awhan-khe', 'uhe mahik men tahak partaa awhan khe,\r\nawhan jaa awhan saan، asan saarn aahyoon.', NULL, 'en', '2023-09-30 23:54:28', '2023-09-30 23:54:28', NULL),
(981, 6, 6, 'kanhin-naar-je-saar-aalyun-akhiyun-kayun', 'kanhin naar je saar aalyun akhiyun kayun,\r\nthiyo chaah، wayo chain، man men chittaun.', NULL, 'en', '2023-09-30 23:54:28', '2023-09-30 23:54:28', NULL),
(982, 6, 6, 'watho-raat-aen-ddaat-karraka-kayaa', 'watho raat aen ddaat karraka kayaa,\r\ngadu mayat aen gita joon guzar gaahon.', NULL, 'en', '2023-09-30 23:54:28', '2023-09-30 23:54:28', NULL),
(983, 10, 6, 'jahin-b-jorron-aahin-jorroyun-raat-joon', 'jahin b jorron aahin jorroyun raat joon,\r\ntahan Hawaa men mekhun khorryun raat joon.', NULL, 'en', '2023-10-01 00:22:22', '2023-10-01 00:22:22', NULL),
(984, 10, 6, 'hina-andhari-samund-kaari-men-waji', 'hina andhari، samund kaari men waji,\r\njaag aakhir beerryun borroyun raat joon.', NULL, 'en', '2023-10-01 00:22:22', '2023-10-01 00:22:22', NULL),
(985, 10, 6, 'khawaab-pahnja-konn-tho-jakri-saghe', 'khawaab pahnja konn tho jakri saghe,\r\nninda jaa dhagaa، na norryun raat joon.', NULL, 'en', '2023-10-01 00:22:22', '2023-10-01 00:22:22', NULL),
(986, 10, 6, 'keru-piyo-peecho-sitaran-jo-kare', 'keru piyo peecho sitaran jo kare,\r\nkahkashaun kanhin ta rorroyun raat joon.', NULL, 'en', '2023-10-01 00:22:22', '2023-10-01 00:22:22', NULL),
(987, 10, 6, 'raaz-sabh-chori-kariyun-sansaar-jaa', 'raaz sabh chori kariyun sansaar jaa,\r\nchau bhalaa! torryun tijoryun raat joon.', NULL, 'en', '2023-10-01 00:22:22', '2023-10-01 00:22:22', NULL),
(988, 10, 6, 'badalan-je-rith-te-beehi-Hassan', 'badalan je rith te beehi Hassan\r\nrokyun moon neth ghooryun raat joon.', NULL, 'en', '2023-10-01 00:22:22', '2023-10-01 00:22:22', NULL),
(989, 11, 6, 'toofaan-je-kulhay-te-hiku-chhokri-charhyal-Aa', 'toofaan je kulhay te، hiku chhokri charhyal Aa\r\nsik soaz hoo~a sikhyal Aa، panj paas hoo~a parhyal Aa.', NULL, 'en', '2023-10-01 00:25:47', '2023-10-01 00:25:47', NULL),
(990, 11, 6, 'moomal-jiyaan-majaazi-Aa-ishiq-men-ubhi-hooa', 'moomal jiyaan majaazi، Aa ishiq men ubhi hoo~a,\r\nman moj mohh mariyal، kanhin kaak je karrhiyal Aa.', NULL, 'en', '2023-10-01 00:25:47', '2023-10-01 00:25:47', NULL),
(991, 11, 6, 'tasweer-jarn-lagal-Aa-dil-je-frame-men-hooa', 'tasweer jarn lagal Aa، dil je frame men hoo~a,\r\nnijj naaz jo nageeno، mundi men jarn marhyal Aa.', NULL, 'en', '2023-10-01 00:25:47', '2023-10-01 00:25:47', NULL),
(992, 11, 6, 'muhinje-badan-pany-te-har-kaa-likhyain-ibaarat', 'muhinje badan pany te، har kaa likhyain ibaarat,\r\npencil jiyaan pyaari، taazi safa ghiryal Aa.', NULL, 'en', '2023-10-01 00:25:47', '2023-10-01 00:25:47', NULL),
(993, 11, 6, 'wejho-rahe-b-party-aen-ddor-hoondy-ody', 'wejho rahe b party aen ddor hoondy ody,\r\nmeelap aen judaai men، jehanjo juso juryal Aa.', NULL, 'en', '2023-10-01 00:25:47', '2023-10-01 00:25:47', NULL),
(994, 11, 6, 'hina-dars-husan-aahe-kehry-kes-kachahri', 'hina dars” husan“ aahe kehry kes kachahri,\r\nhee~a chhokri ubhi chho aen ishiq te arryal Aa.', NULL, 'en', '2023-10-01 00:25:47', '2023-10-01 00:25:47', NULL),
(995, 12, 6, 'sunraan-jatt-roz-hanrkaroon-wachheran-jo-watan-aahe', 'sunraan jatt roz hanrkaroon, wachheran jo watan aahe,\r\nghuman jatt ghoat aen ghorra, waderan jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(996, 12, 6, 'bhittai-jatt-halyu-haklyu-dithaain-ddeeh-saari-khe', 'bhittai jatt halyu haklyu, dithaain ddeeh saari khe,\r\npireen jatt pandh kayo, tunhinje e peran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(997, 12, 6, 'aahe-masroor-gadu-mun-saan-sadaain-sindh-sarri-men', 'aahe masroor gadu mun saan, sadaain sindh sarri men,\r\nsaman sekhat unarn aen deeran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(998, 12, 6, 'tapayu-lohh-loharan-murri-masood-saan-gadji', 'tapayu lohh loharan, murri masood saan gadji,\r\nduniya deenar taalpur khan saweran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(999, 12, 6, 'ali-qaazi-sabhai-gadji-kariyun-aabad-basteen-khe', 'ali qaazi sabhai gadji, kariyun aabad basteen khe,\r\ntawanai kutib anryun matheran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(1000, 12, 6, 'waseelo-hath-kariyun-pahnjo-thyee-har-ko-sikhyu-sahnjo', 'waseelo hath kariyun pahnjo, thyee har ko sikhyu sahnjo,\r\nkuchhi thi roz hee~a keenjhar, machheran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(1001, 12, 6, 'thadhi-taalhi-suthi-champa-wadaa-barr-waah-te-bithal', 'thadhi taalhi, suthi champa, wadaa barr waah te bithal,\r\nbabar, nim, naazbo, motyo aen beeran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(1002, 12, 6, 'aseen-kujhh-aaryaa-aen-lok-aslooka-kinaran-jaa', 'aseen kujhh aaryaa aen lok aslooka kinaran jaa,\r\ndarawar dost pahnjan aen dileeran jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(1003, 12, 6, 'Hassan-heea-sindhri-suharni-asanji-jind-saan-rahndi', 'Hassan hee~a sindhri suharni, asanji jind saan rahndi,\r\nna meeran jo na eran jo na gairan jo watan aahe!', NULL, 'en', '2023-10-01 00:28:36', '2023-10-01 00:36:11', '2023-10-01 00:36:11'),
(1004, 13, 6, 'hiku-dekh-yaadeegri-hiku-shaam-jo-tasawur', 'hiku dekh yaadeegri, hiku shaam jo tasawur,\r\nkhirndar gulab aahe, hiku naam jo tasawur.', NULL, 'en', '2023-10-01 00:30:17', '2023-10-01 00:30:17', NULL),
(1005, 13, 6, 'khilndi-khumaar-jeke-kheekar-kan-khiyaali', 'khilndi khumaar jeke, kheekar kan khiyaali,\r\njurndo unhan akhiyan maan, Aa jaam jo tasawur.', NULL, 'en', '2023-10-01 00:30:17', '2023-10-01 00:30:17', NULL),
