(1150, 28, 6, 'udaaniun-ddor-paee-aarryoon-samo-kanhin-saar-waangy-ho', 'udaaniun ddor paee aarryoon, samo kanhin saar waangy ho,\r\nlaarreo paee laar taan sooraj, muhaanry baaru waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1151, 28, 6, 'gichia-men-mun-huyu-paato-aen-seeny-saarn-ho-laato', 'gichia men mun huyu paato aen seeny saarn ho laato,\r\nmunhinjo mahboob kalh munkhe, hathan men haar waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1152, 28, 6, 'hujooman-aen-hushman-heeklaai-khe-jadahin-janmyu', 'hujooman aen hushman, heeklaai khe jadahin janmyu,\r\nditho moon rooh pahinje khe, andhaari gaar waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1153, 28, 6, 'wanan-te-kaang-haa-wethal-galliea-men-ggaun-paee-aayun', 'wanan te kaang haa wethal, galliea men ggaun paee aayun,\r\npurarno goth ho lekhakan, pireen je paar waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1154, 28, 6, 'jadahin-jhampeer-men-muhinje-jawani-mott-khaainde', 'jadahin jhampeer men muhinje, jawani mott khaainde,\r\nkhilee chawndien khajia khe tun, tikho talwar waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1155, 28, 6, 'hawaon-te-halyas-aaon-nazara-kujhh-konn-thi-aayo', 'hawaon te halyas aaon, nazara kujhh konn thi aayo,\r\nsajarn rasto sajjo tunhinjo, sanihia kanhin taar waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1156, 28, 6, 'machhi-je-akh-maan-wethe-ditho-mun-har-deharay-khe', 'machhi je akh maan wethe, ditho mun har deharay khe,\r\nlarratyal wakt men lamhoo, lagal kanhin jaarr waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1157, 28, 6, 'safa-sunsaan-mandir-men-jadahin-sanjhee-mile-mun-saan', 'safa sunsaan mandir men, jadahin sanjhee mile mun saan,\r\nute aawaz ishiq jo, sanharo kanhin aar waangy ho.', NULL, 'en', '2023-10-01 01:07:39', '2023-10-01 01:07:39', NULL),
(1158, 29, 6, 'pakhi-pardes-jaa-sooraj-paran-poyaan-larry-tho-piyo', 'pakhi pardes jaa sooraj paran poyaan larry tho piyo,\r\nwararr un waatt te wethal, aggyan jatt rinr rarri tho piyo.', NULL, 'en', '2023-10-01 01:10:28', '2023-10-01 01:10:28', NULL),
(1159, 29, 6, 'lage-wai-laar-saan-Aa-laoon-awhan-khe-biyo-bhalaa-chhaa-chaoon', 'lage wai laar saan Aa laoon, awhan khe biyo bhalaa chhaa chaoon,\r\nasan khe ishiq sareen jian, chandi phookay charray tho piyo.', NULL, 'en', '2023-10-01 01:10:28', '2023-10-01 01:10:28', NULL),
(1160, 29, 6, 'umar-je-aangurayn-maan-dis-phulan-waangy-chanroon-thaa-piyaa', 'umar je aangurayn maan dis, phulan waangy chanroon thaa piyaa,\r\nsajarn cigarette sassti jian, sajjo manhun sarry tho piyo.', NULL, 'en', '2023-10-01 01:10:28', '2023-10-01 01:10:28', NULL),
(1161, 29, 6, 'puraarni-ishiq-porrhi-je-wajjatt-narr-phook-maan-nikti', 'puraarni ishiq porrhi je, wajjatt narr phook maan nikti,\r\nmilan hi meer muhammad jo, jawanian khe jarri tho piyo.', NULL, 'en', '2023-10-01 01:10:28', '2023-10-01 01:10:28', NULL),
(1162, 29, 6, 'awhan-murki-mile-aakhir-halyaa-wiya-shahar-pahinje-daanh', 'awhan murki mile aakhir, halyaa wiya shahar pahinje daanh,\r\nasan je galh je ggall taan, ajaan gorho garri tho piyo.', NULL, 'en', '2023-10-01 01:10:28', '2023-10-01 01:10:28', NULL),
(1163, 29, 6, 'watan-men-be-watan-mashooq-aashiq-jo-milan-kehro', 'watan men be watan mashooq, aashiq jo milan kehro,\r\nzamaano zor te tarr taan, tamachian khe tarre tho piyo.', NULL, 'en', '2023-10-01 01:10:28', '2023-10-01 01:10:28', NULL),
(1164, 30, 6, 'lahar-thi-jaagy-kare-bi-lahar-men', 'lahar thi jaagy kare bi lahar men,\r\nchho sumandar chho bhalaa sumheen natho?', NULL, 'en', '2023-10-01 01:11:48', '2023-10-01 01:11:48', NULL),
(1165, 30, 6, 'raat-je-dei-men-jaggan-thaa-janam', 'raat je dei men jaggan thaa janam,\r\nghind mandir chho bhalaa sumheen natho?', NULL, 'en', '2023-10-01 01:11:48', '2023-10-01 01:11:48', NULL),
(1166, 30, 6, 'zindagi-kedi-zalalat-shikastoon', 'zindagi, kedi zalalat, shikastoon,\r\ntun sikander chho bhalaa sumheen natho?', NULL, 'en', '2023-10-01 01:11:48', '2023-10-01 01:11:48', NULL),
(1167, 30, 6, 'bahar-keddo-ajj-musaafir-meehan-Aa', 'bahar keddo ajj musaafir meehan Aa,\r\nmuhinje andar, chho bhalaa sumheen natho?', NULL, 'en', '2023-10-01 01:11:48', '2023-10-01 01:11:48', NULL),
(1168, 30, 6, 'jaag-je-bairri-ta-purzaa-thi-chukki', 'jaag je bairri ta purzaa thi chukki,\r\nshah bandar chho bhalaa sumheen natho?', NULL, 'en', '2023-10-01 01:11:48', '2023-10-01 01:11:48', NULL),
(1169, 30, 6, 'gharu-ta-bhaatiyan-saan-suto-Aa-bharkien', 'gharu ta bhaatiyan saan suto Aa bharkien,\r\nkharkandar dar, chho bhalaa sumheen natho?', NULL, 'en', '2023-10-01 01:11:48', '2023-10-01 01:11:48', NULL),
(1170, 31, 6, 'naag-b-sochiyo-hoondo-dil-men-maanhua-je-mann-kehry-aahe', 'naag b sochiyo hoondo dil men, maanhu~a je mann kehry aahe?\r\nmaanhu~a sochiyo dil je daanry jehri kaa karn kehry aahe?', NULL, 'en', '2023-10-01 01:13:13', '2023-10-01 01:13:13', NULL),
(1171, 31, 6, 'wehi-gadu-wirheen-tho-wetho-ghumandi-gadu-chirren-tho-chariyaa!', 'wehi gadu wirheen tho wetho, ghumandi gadu chirren tho chariyaa!\r\nishiq asan saan tunhinje aakhir ahre ann banr kehry aahe?', NULL, 'en', '2023-10-01 01:13:13', '2023-10-01 01:13:13', NULL),
(1172, 31, 6, 'meehan-asan-khe-neehan-agaiee-aahe-bhajayu-barsi-barsi', 'meehan asan khe neehan agaiee, aahe bhajayu barsi barsi,\r\nhi dis man men moti Aa man tunhinje karn karn kehry aahe?', NULL, 'en', '2023-10-01 01:13:13', '2023-10-01 01:13:13', NULL),
(1173, 31, 6, 'wanu-thorrha-thiyaa-ghityun-sorrhyoon-husan-dars-aggai-piyaa-lorryoon', 'wanu thorrha thiyaa, ghityun sorrhyoon، “hassan dars” aggai piyaa lorryoon,\r\nto bin taangh asan je tarrapi, bi kaa pan chanr kehry aahe?', NULL, 'en', '2023-10-01 01:13:13', '2023-10-01 01:13:13', NULL),
(1174, 32, 6, 'duko-neth-daakhil-kayo-mun-b-dil-jo', 'duko neth daakhil kayo mun b dil jo,\r\nlakhain mann muhabat biyan je buddi wai.', NULL, 'en', '2023-10-01 01:13:59', '2023-10-01 01:13:59', NULL),
(1175, 32, 6, 'pakhi-wanu-je-tarrie-men-tabdeel-thi-wayo', 'pakhi wanu je tarrie men tabdeel thi wayo,\r\nuhaa wanu je taari, utaan khan adday wai.', NULL, 'en', '2023-10-01 01:13:59', '2023-10-01 01:13:59', NULL),
(1176, 32, 6, 'rugo-hina-uthee-kar-morrbyo-subuh-jo', 'rugo hina uthee kar morrbyo subuh jo,\r\nchawan thaa ta dharti b thori dhuddi wai.', NULL, 'en', '2023-10-01 01:13:59', '2023-10-01 01:13:59', NULL),
(1177, 32, 6, 'saween-popatan-je-muhaafiz-jithay-saan', 'saween popatan je muhaafiz jithay saan,\r\ngullan saan kande guftagu kaa gaddi wai.', NULL, 'en', '2023-10-01 01:13:59', '2023-10-01 01:13:59', NULL),
(1178, 32, 6, 'larri-raat-lokan-jaa-leekaa-latarry', 'larri raat lokan jaa leekaa latarry,\r\nache piyaar je peengh men kaa ludday wai.', NULL, 'en', '2023-10-01 01:13:59', '2023-10-01 01:13:59', NULL),
(1179, 33, 6, 'muhabat-je-manzil-jaa-mariyal-musaafir', 'muhabat je manzil jaa mariyal musaafir,\r\ndini aen na warti aseen aahyoon puhtaa.', NULL, 'en', '2023-10-01 01:14:44', '2023-10-01 01:15:43', '2023-10-01 01:15:43'),
(1180, 33, 6, 'rusan-thaa-ta-rirn-men-e-rahjay-wanjun-thaa', 'rusan thaa ta rirn men e rahjay wanjun thaa,\r\npireen neth party aseen aahyoon puhtaa.', NULL, 'en', '2023-10-01 01:14:44', '2023-10-01 01:15:43', '2023-10-01 01:15:43'),
