(1189, 34, 6, 'deenhan-je-wanu-maan', 'deenhan je wanu maan,\r\nraatyun rorray tho.', NULL, 'en', '2023-10-01 01:18:48', '2023-10-01 01:18:48', NULL),
(1190, 34, 6, 'mun-khe-motaai', 'mun khe motaai,\r\nmun khe morray tho.', NULL, 'en', '2023-10-01 01:18:48', '2023-10-01 01:18:48', NULL),
(1191, 34, 6, 'be-had-bitho-Aa', 'be had bitho Aa,\r\nkeddo dorray tho.', NULL, 'en', '2023-10-01 01:18:48', '2023-10-01 01:18:48', NULL),
(1192, 35, 6, 'pathar-b-mathan-wighri-aawaz-thi-pawe', 'pathar b mathan wighri, aawaz thi pawe,\r\naawaz neth tabdeel thi, saaz thi pawe.', NULL, 'en', '2023-10-01 01:20:08', '2023-10-01 01:20:08', NULL),
(1193, 35, 6, 'aaon-jo-auratun-saan-kapraa-dhakay-ghumaan', 'aaon jo auratun saan kapraa dhakay ghumaan,\r\naen deewata ughaarro, naraz thi pawe.', NULL, 'en', '2023-10-01 01:20:08', '2023-10-01 01:20:08', NULL),
(1194, 35, 6, 'anjaam-ishiq-tunhinjo-kehro-b-thi-saghe-tho', 'anjaam ishiq tunhinjo kehro b thi saghe tho,\r\nakhryan saan hiku dafo biyo, aghaas thi pawe.', NULL, 'en', '2023-10-01 01:20:08', '2023-10-01 01:20:08', NULL),
(1195, 35, 6, 'mun-laai-ta-har-muhabat-madfoon-shahar-Aa', 'mun laai ta har muhabat madfoon shahar Aa,\r\nmun laai ta har masilo, tho raaz thi pawe.', NULL, 'en', '2023-10-01 01:20:08', '2023-10-01 01:20:08', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1196, 35, 6, 'wethe-jo-maan-wanran-khe-bharsaan-weharayan', 'wethe jo maan wanran khe, bharsaan weharayan,\r\nbeehan ta Hawaa jhooto, tho baaz thi pawe.', NULL, 'en', '2023-10-01 01:20:08', '2023-10-01 01:20:08', NULL),
(1197, 36, 6, 'shaamil-haal-ko-ta-hoondo-ho', 'shaamil haal ko ta hoondo ho,\r\nmunhinjo dildar mot hoondo ho.', NULL, 'en', '2023-10-01 01:20:57', '2023-10-01 01:20:57', NULL),
(1198, 36, 6, 'hina-hayati-je-sasuai-seejay-lutti', 'hina hayati je sasuai seejay lutti,\r\nkanhin zamane men hott hoondo ho.', NULL, 'en', '2023-10-01 01:20:57', '2023-10-01 01:20:57', NULL),
(1199, 36, 6, 'dard-daim-ache-kayo-dero', 'dard daim ache kayo dero,\r\npehreen mottoyo ho po ta hoondo ho.', NULL, 'en', '2023-10-01 01:20:57', '2023-10-01 01:20:57', NULL),
(1200, 36, 6, 'munhinjo-andar-paee-wirrhyu-mun-saan', 'munhinjo andar paee wirrhyu mun saan,\r\nmun khan daadho b chho ta hoondo ho.', NULL, 'en', '2023-10-01 01:20:57', '2023-10-01 01:20:57', NULL),
(1201, 36, 6, 'biyaa-bhagaa-ho-na-par-bhago-bharsaan', 'biyaa bhagaa, ho na par bhago bharsaan,\r\nhiku Hassan yaar so ta hoondo ho.', NULL, 'en', '2023-10-01 01:20:57', '2023-10-01 01:20:57', NULL),
(1202, 37, 6, 'gharu-matia-jo-ghaaat-ghittia-men', 'gharu mittia jo ghaaat ghitti~a men,\r\ntunhinjo kehro kam khattia men?', NULL, 'en', '2023-10-01 01:22:17', '2023-10-01 01:22:17', NULL),
(1203, 37, 6, 'nerrotia-jo-naam-niraalo', 'nerrotia jo naam niraalo,\r\nrang ubharay phatt pattia men,', NULL, 'en', '2023-10-01 01:22:17', '2023-10-01 01:22:17', NULL),
(1204, 37, 6, 'wanu-jian-dhoonri-dhoonri-dil-khe', 'wanu jian dhoonri dhoonri dil khe,\r\nman chhulkaai dis mittia men.', NULL, 'en', '2023-10-01 01:22:17', '2023-10-01 01:22:17', NULL),
(1205, 37, 6, 'haanr-Hassan-deewano-nahey', 'haanr Hassan deewano nahey,\r\nchawndien chawndien neth chatthia men.', NULL, 'en', '2023-10-01 01:22:17', '2023-10-01 01:22:17', NULL),
(1206, 38, 6, 'sabh-kujhh-hujy-zaroor-magar-piyaar-tho-khape', 'sabh kujhh hujy, zaroor magar piyaar tho khape,\r\nmaai jo sutho saroor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1207, 38, 6, 'imaan-dinu-hoondo-dozakh-aen-jantoon', 'imaan, dinu hoondo, dozakh aen jantoon,\r\nkhaalis khudaa jo noor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1208, 38, 6, 'olaad-nokri-aen-wahnwar-wakt-jo', 'olaad, nokri aen wahnwar wakt jo,\r\nhaa haa hujy hazoor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1209, 38, 6, 'danish-aqul-je-duniyaa-aahe-daleel-saan', 'danish aqul je duniyaa, aahe daleel saan,\r\naen ilim te uboor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1210, 38, 6, 'gham-tho-kaje-gazhab-yaa-afsos-tho-kaje', 'gham tho kaje gazhab, yaa afsos tho kaje,\r\ngusso kaje ghurorr, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1211, 38, 6, 'kanhin-chandu-te-tharron-yaa-sooraj-mathan-bbaroon', 'kanhin chandu te tharron yaa sooraj mathan bbaroon,\r\nduniyaa khan waji dor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1212, 38, 6, 'insaan-saan-muhabat-insaan-tho-kare', 'insaan saan muhabat, insaan tho kare,\r\ngulman aen na hoor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1213, 38, 6, 'waparu-piyaar-nahey-nuqsaan-aen-nafao', 'waparu, piyaar nahey, nuqsaan aen nafao,\r\npachhi bache na morr, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1214, 38, 6, 'hikray-Hassan-hayaati-wende-winjaaijay', 'hikray \"Hassan\" hayaati, wende winjaaijay,\r\nzayah thiyaan zaroor, magar piyaar tho khape.', NULL, 'en', '2023-10-01 01:37:38', '2023-10-01 01:37:38', NULL),
(1215, 39, 6, 'hina-umar-be-misal-men-be-baak-mohabbatu', 'hina umar be misal men, be baak mohabbatu,\r\nnanga naseeb munhinjaa, thiyun chaak mohabbatu.', NULL, 'en', '2023-10-01 01:38:51', '2023-10-01 01:38:51', NULL),
(1216, 39, 6, 'beehi-badan-mun-b-khani-saah-te-rakhiyo', 'beehi badan mun b, khani saah te rakhiyo,\r\nghure gunah neth kayun paak mohabbatu.', NULL, 'en', '2023-10-01 01:38:51', '2023-10-01 01:38:51', NULL),
(1217, 39, 6, 'karrhay-tatai-jo-dees-tho-aaon-tadahn-ghumaan', 'karrhay tatai jo dees tho aaon tadahn ghumaan,\r\nmanjhand jiyaan mile b waji taak mohabbatu.', NULL, 'en', '2023-10-01 01:38:51', '2023-10-01 01:38:51', NULL),
(1218, 39, 6, 'mun-saarn-muhnjoon-mosmoon-gadjyoon-piyo-ghuman', 'mun saarn muhnjoon mosmoon gadjyoon piyo ghuman,\r\nrab jo qasam ta rooh joon rolak mohabbatu.', NULL, 'en', '2023-10-01 01:38:51', '2023-10-01 01:38:51', NULL),
(1219, 39, 6, 'mun-laai-pireen-piyaar-Aa-tunhinjo-banau-thiyo', 'mun laai pireen piyaar Aa tunhinjo banau thiyo,\r\nmuhnjyun ta thaki neth thiyun thaak mohabbatu.', NULL, 'en', '2023-10-01 01:38:51', '2023-10-01 01:38:51', NULL),
(1220, 39, 6, 'panj-e-shayun-naseeb-atham-piyo-tadahn-taggan', 'panj e shayun naseeb atham, piyo tadahn taggan,\r\nnarmi gullan je, gita, Hawaa, maak mohabbatu.', NULL, 'en', '2023-10-01 01:38:51', '2023-10-01 01:38:51', NULL),
(1221, 40, 6, 'samund-khushki-men-faraq-thyee-disjaain', 'samund khushki men faraq thyee, disjaain\r\ndil jo ko goraab garak thyee, disjaain', NULL, 'en', '2023-10-01 01:39:51', '2023-10-01 01:39:51', NULL),
