(1389, 63, 6, 'hayaati-ishiq-tho-khotti-muhabat-thi-mitti-ggohay', 'hayaati ishiq tho khotti, muhabat thi mitti ggohay,\r\nasan e waatt jaa wars, asan e khaaq chhanri Aa.', NULL, 'en', '2023-10-01 03:44:22', '2023-10-01 03:44:22', NULL),
(1390, 63, 6, 'bhale-keddo-khiwanr-khaaun-muhabat-jaa-bukhyaa-aahyoon', 'bhale keddo khiwanr khaaun, muhabat jaa bukhyaa aahyoon,\r\nmilan mahboob jo maani, asan laai piyaar paarni Aa.', NULL, 'en', '2023-10-01 03:44:22', '2023-10-01 03:44:22', NULL),
(1391, 63, 6, 'kinara-aen-dhandhun-pahnjun-kandhyoon-qaaim-rahan-daim', 'kinara aen dhandhun pahnjun, kandhyoon qaaim rahan daim,\r\npakhiyan aen piyaar je toli, utaan har kaa uddanri Aa.', NULL, 'en', '2023-10-01 03:44:22', '2023-10-01 03:44:22', NULL),
(1392, 63, 6, 'shahar-jaanaan-men-jatt-aahen-bitho-tun-tanday-bagay', 'shahar jaanaan men jatt aahen, bitho tun tanday bagay,\r\nute e muhabatan je dil, waji hina meer maanni Aa.', NULL, 'en', '2023-10-01 03:44:22', '2023-10-01 03:44:22', NULL),
(1393, 64, 6, 'ishiq-men-zaat-tasawur-khan-mathe-chho-thi-thyee', 'ishiq men zaat tasawur khan mathe chho thi thyee?\r\nmot men maat tasawur khan mathe chho thi thyee?', NULL, 'en', '2023-10-01 03:47:24', '2023-10-01 03:47:24', NULL),
(1394, 64, 6, 'jo-phirri-fikar-ta-kangaal-kare-duniya-men', 'jo phirri fikar ta kangaal kare duniya men,\r\njaa dai ddaat tasawur khan mathe chho thi thyee?', NULL, 'en', '2023-10-01 03:47:24', '2023-10-01 03:47:24', NULL),
(1395, 64, 6, 'soch-muhinje-b-rugo-chand-sitaran-taaen', 'soch muhinje b rugo chand sitaran taaen,\r\nkul kaainaat tasawur khan mathe chho thi thyee?', NULL, 'en', '2023-10-01 03:47:24', '2023-10-01 03:47:24', NULL),
(1396, 64, 6, 'hina-banaan-deenhan-jo-anat-na-chherro-koi', 'hina banaan deenhan jo anat na chherro koi,\r\nhina saa gadu raat tasawur khan mathe chho thi thyee?', NULL, 'en', '2023-10-01 03:47:24', '2023-10-01 03:47:24', NULL),
(1397, 64, 6, 'meehan-men-neehan-bhinay-tho-ta-pusse-thi-dil-b', 'meehan men neehan bhinay tho ta pusse thi dil b,\r\nahre barsaat tasawur khan mathe chho thi thyee?', NULL, 'en', '2023-10-01 03:47:24', '2023-10-01 03:47:24', NULL),
(1398, 64, 6, 'hun-je-galihyun-je-galhyan-manjh-winjyal-aahyan', 'hun je galihyun je galhyan manjh winjyal aahyan,\r\nhina je har baat tasawur khan mathe chho thi thyee?', NULL, 'en', '2023-10-01 03:47:24', '2023-10-01 03:47:24', NULL),
(1399, 65, 6, 'kakri-hui-jaa-beethi-khaali-karn-karn-thi-na-saghe', 'kakri hui jaa beethi khaali karn karn thi na saghe,\r\nwechari wai wapas aakhir, sawanr thi na saghe.', NULL, 'en', '2023-10-01 23:14:05', '2023-10-01 23:14:05', NULL),
(1400, 65, 6, 'khaathu-ta-aahe-khaathu-inahia-je-maath-b-torrien-tun', 'khaathu ta aahe khaathu, inahia je maath b torrien tun,\r\ntable aakhir table thi، dakanr thi na saghe.', NULL, 'en', '2023-10-01 23:14:05', '2023-10-01 23:14:05', NULL),
(1401, 65, 6, 'duniyaa-saari-dard-hazaren-milndi-khani-murki', 'duniyaa saari، dard hazaren, milndi khani murki,\r\npyaari aahe pyaari rahndi papanr thi na saghe.', NULL, 'en', '2023-10-01 23:14:05', '2023-10-01 23:14:05', NULL),
(1402, 65, 6, 'to-ta-patyo-ho-parran-khan-jo-balakpanr-bbotto', 'to ta patyo ho parran khan jo balakpanr bbotto,\r\njaa nim nandhi hoondy marieyie saa wanu na thi saghe.', NULL, 'en', '2023-10-01 23:14:05', '2023-10-01 23:14:05', NULL),
(1403, 65, 6, 'dawa-korry-kamtar-manhun-sangti-koh-kajan', 'dawa korry، kamtar manhun, sangti koh kajan,\r\nhali kaan Hassan saan gadji, saa jarn thi na saghe.', NULL, 'en', '2023-10-01 23:14:05', '2023-10-01 23:14:05', NULL),
(1404, 66, 6, 'kolhiyeen-saan-gadu-kolhiyee-thiyaan', 'kolhiyeen saan gadu kolhiyee thiyaan,\r\nhaa haa haa، aaon holi thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1405, 66, 6, 'tunhinjo-udryal-ishiq-hujaan', 'tunhinjo udryal ishiq hujaan,\r\npahinje phhatal jholee thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1406, 66, 6, 'waddanrn-jo-maan-virso-thiyaan', 'waddanrn jo maan virso thiyaan,\r\nbaaran je maan boli thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1407, 66, 6, 'tun-jo-samund-hujeen-maan-tunhinje', 'tun jo samund hujeen, maan tunhinje,\r\nchho na bhalaa poi chholi thiyaan?', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1408, 66, 6, 'obhaaro-maan-ishiq-hujaan', 'obhaaro maan ishiq hujaan,\r\nlahwari maan loli thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1409, 66, 6, 'ochangaran-jo-anat-thiyaan', 'ochangaran jo anat thiyaan,\r\ntahakan je maan toli thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1410, 66, 6, 'mushkil-mushkil-mushkil-thiyaan', 'mushkil mushkil mushkil thiyaan,\r\nsowali sowali sowali thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1411, 66, 6, 'lafuzan-je-maan-lajj-rakhan', 'lafuzan je maan lajj rakhan,\r\nguftay je maan goli thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1412, 66, 6, 'yaar-husan-jo-shair-thiyaan', 'yaar husan jo shair thiyaan,\r\nyaar husan je boli thiyaan.', NULL, 'en', '2023-10-01 23:15:04', '2023-10-01 23:15:04', NULL),
(1413, 67, 6, 'tamana-taak-men-aahe', 'tamana taak men aahe,\r\nliki tokhe disarn chahe.', NULL, 'en', '2023-10-01 23:16:40', '2023-10-01 23:16:40', NULL),
(1414, 67, 6, 'musawar-us-maan-aakhir', 'musawar us maan aakhir,\r\ntunhinje tasweer tho thahy.', NULL, 'en', '2023-10-01 23:16:40', '2023-10-01 23:16:40', NULL),
(1415, 67, 6, 'pharyan-khe-jhurr-jhali-bitho', 'pharyan khe jhurr jhali bitho,\r\nchaween jahin wakt barsaai.', NULL, 'en', '2023-10-01 23:16:40', '2023-10-01 23:16:40', NULL),
(1416, 67, 6, 'Hawaa-tunhinje-acharn-taaen', 'Hawaa tunhinje acharn taaen,\r\ngullan te mulku tarsaai.', NULL, 'en', '2023-10-01 23:16:40', '2023-10-01 23:16:40', NULL),
(1417, 67, 6, 'agar-aahen-Hassan-aahe', 'agar aahen \"Hassan\" aahe,\r\nsiwaa tunhinje safa nahey.', NULL, 'en', '2023-10-01 23:16:40', '2023-10-01 23:16:40', NULL),
(1418, 68, 7, 'wathee-har-har-janam-warbo-mithaa-mehran-men-milbo', 'wathee har har janam warbo mithaa mehran men milbo,\r\nkhatam oondah thi wende chitti chandarn men milbo.', NULL, 'en', '2023-10-01 23:17:35', '2023-10-01 23:17:35', NULL),
(1419, 68, 7, 'pahnji-jaa-jang-Aa-jaari-saa-thindi-neth-soobhari', 'pahnji jaa jang Aa jaari saa thindi neth soobhari,\r\nmari windaa mirrhai maari sachia surhhanr milbo.', NULL, 'en', '2023-10-01 23:17:35', '2023-10-01 23:17:35', NULL),
(1420, 68, 7, 'hujay-urap-yaa-libanon-jite-maaryo-wayo-insaan', 'hujay urap yaa libanon jite maaryo wayo insaan,\r\ninhi manhun sandai armaan aen ratt chhaanr men milbo.', NULL, 'en', '2023-10-01 23:17:35', '2023-10-01 23:17:35', NULL),
(1421, 68, 7, 'thiyaa-je-qoam-taan-qurbaan-unhan-jaa-aahin-lakhain-ahsaan', 'thiyaa je qoam taan qurbaan unhan jaa aahin lakhain ahsaan,\r\nshaheedan je retti ratt saan ratayai  lalaanr men milbo.', NULL, 'en', '2023-10-01 23:17:35', '2023-10-01 23:17:35', NULL),
(1422, 68, 7, 'wari-warnda-ta-waskara-wasi-pawanda-hi-putt-saaraa', 'wari warnda ta waskara wasi pawanda hi putt saaraa,\r\nmarroo thindaa ta mochara meehan mandarn men milbo.', NULL, 'en', '2023-10-01 23:17:35', '2023-10-01 23:17:35', NULL),
(1423, 69, 6, 'naen-khe-purarno-kaje-dil-ddijay', 'naen khe purarno kaje, dil ddijay,\r\nsandai dil khe jaanru kaje, dil ddijay.', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1424, 69, 6, 'milan-khe-rilli-jiyaan-mathan-odhjay', 'milan khe rilli jiyaan mathan odhjay,\r\nwichhorro wichharun kaje, dil ddijay.', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1425, 69, 6, 'sajarn-khan-siwaa-je-sumhanr-sochjai', 'sajarn khan siwaa je sumhanr sochjai,\r\npathar jo wihanro kaje, dil ddijay?', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1426, 69, 6, 'umlah-dil-je-qeemat-ta-kehry-lage', 'umlah dil je qeemat ta kehry lage,\r\nttako tunhinjo nanro kaje, dil ddijay', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1427, 69, 6, 'farshte-khudaa-je-zaroort-na-kaa', 'farshte، khudaa je zaroort na kaa,\r\nrugo khud khe manhun kaje, dil ddijay.', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1428, 69, 6, 'majazan-je-moomal-ragan-men-rakhe', 'majazan je moomal ragan men rakhe,\r\nrugo ranro ranro kaje, dil ddijay.', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1429, 69, 6, 'udaaman-khan-aggte-ukrandi-Hassan', 'udaaman khan aggte ukrandi \"Hassan\"\r\npakhiyan khan puchhanro kaje, dil ddijay.', NULL, 'en', '2023-10-01 23:18:43', '2023-10-01 23:18:43', NULL),
(1430, 70, 6, 'pabban-te-ubho-thi-disaan-yaar-tokhe', 'pabban te ubho thi disaan yaar tokhe،\r\nmathe mathbhro thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1431, 70, 6, 'kajal-kararro-thi-disaan-yaar-tokhe', 'kajal kararro thi disaan yaar tokhe,\r\nsajal sanwro thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1432, 70, 6, 'khaddon-khandu-khobbo-darro-aen-dabbu-thi', 'khaddon, khandu, khobbo, darro aen dabbu thi,\r\ntaraai taro thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1433, 70, 6, 'safa-be-wazan-thi-disaan-yaar-tokhe', 'safa be wazan thi disaan yaar tokhe,\r\naen gorho ggaro thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1434, 70, 6, 'kino-aen-sutho-thi-disaan-yaar-tokhe', 'kino aen sutho thi disaan yaar tokhe,\r\nbhalo aen dhillo thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1435, 70, 6, 'charyu-aen-kharyo-thi-disaan-yaar-tokhe', 'charyu aen kharyo thi disaan yaar tokhe,\r\nsafa thup andho thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1436, 70, 6, 'budhrro-budhhal-thi-disaan-yaar-tokhe', 'budhrro budhhal thi disaan yaar tokhe,\r\nHassan chokro thi disaan yaar tokhe.', NULL, 'en', '2023-10-01 23:19:52', '2023-10-01 23:19:52', NULL),
(1437, 71, 6, 'jjamaaroon-guzaare-milaan-yaar-tosaan', 'jjamaaroon guzaare, milaan yaar tosaan,\r\nsabhai wakt ware, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1438, 71, 6, 'katiyan-je-kinaare-milaan-yaar-tosaan', 'katiyan je kinaare, milaan yaar tosaan,\r\naen sooraj ubhaary, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1439, 71, 6, 'sajjo-ddeeh-mun-wati-jo-wetho-hujy', 'sajjo ddeeh mun wati jo wetho hujy,\r\nsabhan khe uthaaray, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1440, 71, 6, 'pakhi-je-pukaray-milaan-yaar-tosaan', 'pakhi je pukaray, milaan yaar tosaan,\r\nnadi je nihaaray, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1441, 71, 6, 'akhiyun-biay-ujaray-milaan-yaar-tosaan', 'akhiyun biay ujaray, milaan yaar tosaan,\r\naen sapnan saharo, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1442, 71, 6, 'jabal-te-jhuggia-men-aen-gazan-churan-men', 'jabal te, jhuggia men, aen gazan churan men,\r\naen kehry sitaray, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1443, 71, 6, 'Hassan-je-ta-hayaati-tolaai-aahe', '\"Hassan\" je ta hayaati, tolaai aahe,\r\njiyaaray aen maari, milaan yaar tosaan.', NULL, 'en', '2023-10-01 23:21:04', '2023-10-01 23:21:04', NULL),
(1444, 72, 6, 'sajji-raat-saari-thi-beethi-rahe', 'sajji raat saari, thi beethi rahe,\r\nlage raat kaari, thi beethi rahe.', NULL, 'en', '2023-10-01 23:22:52', '2023-10-01 23:22:52', NULL),
(1445, 72, 6, 'safa-saah-boosattji-tho-piyo', 'safa saah boosattji tho piyo,\r\nwicho wich ware, thi beethi rahe.', NULL, 'en', '2023-10-01 23:22:52', '2023-10-01 23:22:52', NULL),
(1446, 72, 6, 'haqeeqat-men-tasweer-aahe-lagal', 'haqeeqat men tasweer aahe lagal,\r\ndari manjh saari, thi beethi rahe.', NULL, 'en', '2023-10-01 23:22:52', '2023-10-01 23:22:52', NULL),
(1447, 72, 6, 'puthiyaan-ho-pasham-jo-naram-gotharro', 'puthiyaan ho pasham jo naram gotharro,\r\naggyan kartt aari, thi beethi rahe.', NULL, 'en', '2023-10-01 23:22:52', '2023-10-01 23:22:52', NULL),
(1448, 72, 6, 'chapan-maan-piyaasi-bikhaari-bannri', 'chapan maan piyaasi, bikhaari bannri,\r\nakhiyan maan unjari, thi beethi rahe.', NULL, 'en', '2023-10-01 23:22:52', '2023-10-01 23:22:52', NULL),
(1449, 72, 6, 'hali-chho-na-thi-heea-hayaati-Hassan', 'hali chho na thi hee~a hayaati Hassan\r\ntutti poi saari, thi beethi rahe.', NULL, 'en', '2023-10-01 23:22:52', '2023-10-01 23:22:52', NULL),
(1450, 73, 6, 'hina-umar-bemisaal-men-bebak-mohabbatu', 'hina umar bemisaal men, bebak mohabbatu,\r\nnanga naseeb munhinjaa thiyun, chaak mohabbatu.', NULL, 'en', '2023-10-01 23:24:04', '2023-10-01 23:24:04', NULL),
(1451, 73, 6, 'beethi-badan-mun-b-khani-baah-te-rakhiyo', 'beethi badan mun b khani baah te rakhiyo,\r\nghure gunah neth kayun, paak mohabbatu.', NULL, 'en', '2023-10-01 23:24:04', '2023-10-01 23:24:04', NULL),
(1452, 73, 6, 'karrhay-tattia-jo-dees-tho-aaon-tadahn-ghumaan', 'karrhay tattia jo dees tho aaon tadahn ghumaan,\r\nmanjhand jitaan mile b wajan, taak mohabbatu.', NULL, 'en', '2023-10-01 23:24:04', '2023-10-01 23:24:04', NULL),
(1453, 73, 6, 'mun-saarn-muhnjoon-mosmoon-gadjyoon-piyun-ghuman', 'mun saarn muhnjoon mosmoon gadjyoon piyun ghuman,\r\nrab jo qasam ta rooh joon, rolak mohabbatu.', NULL, 'en', '2023-10-01 23:24:04', '2023-10-01 23:24:04', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1454, 73, 6, 'mun-lay-pireen-piyaar-Aa-tunhinjo-panah-thiyo', 'mun lay pireen piyaar Aa tunhinjo panah thiyo,\r\nmuhnjoon ta thaki neth thiyun, thaak mohabbatu.', NULL, 'en', '2023-10-01 23:24:04', '2023-10-01 23:24:04', NULL),
(1455, 73, 6, 'panj-e-shayun-naseeb-atham-piyo-tadahn-ghumaan', 'panj e shayun naseeb atham piyo tadahn ghumaan,\r\nnarmi gullan je, gita, Hawaa aen maak mohabbatu.', NULL, 'en', '2023-10-01 23:24:04', '2023-10-01 23:24:04', NULL),
(1456, 74, 6, 'pukaray-ninda-maan-thi-hooa', 'pukaray nindd maan thi hooa,\r\nutaari ninda maan thi hooa.', NULL, 'en', '2023-10-01 23:25:23', '2023-10-01 23:25:23', NULL),
(1457, 74, 6, 'kiryas-maan-jaag-je-jar-men', 'kiryas maan jaag je jar men,\r\nukaari ninda maan thi hooa.', NULL, 'en', '2023-10-01 23:25:23', '2023-10-01 23:25:23', NULL),
(1458, 74, 6, 'array-motya!-utho-jaaggo', 'array motya! utho jaaggo,\r\nucharay ninda maan thi hooa.', NULL, 'en', '2023-10-01 23:25:23', '2023-10-01 23:25:23', NULL),
(1459, 74, 6, 'akhiyan-maan-kian-pakhiyan-waangy', 'akhiyan maan kian pakhiyan waangy,\r\nudaari ninda maan thi hooa.', NULL, 'en', '2023-10-01 23:25:23', '2023-10-01 23:25:23', NULL),
(1460, 74, 6, 'khulyal-Aa-khawaab-je-kharrki', 'khulyal Aa khawaab je kharrki,\r\nnihaaray ninda maan thi hooa.', NULL, 'en', '2023-10-01 23:25:23', '2023-10-01 23:25:23', NULL),
(1461, 75, 6, 'premoo-kolheeia-khan-puchnden-ta-tokhe-hoo-budhaindo', 'premoo kolheeia khan puchnden ta tokhe hoo budhaindo,\r\nakhiyun kehrryoon, pireen kehra, sajarn! dil jo chikanr kehro!', NULL, 'en', '2023-10-01 23:26:35', '2023-10-01 23:26:35', NULL),
(1462, 75, 6, 'siyaaro-phooh-jawaania-men-na-to-sochiyo-ta-kian-likhjay', 'siyaaro phooh jawaani~a men, na to sochiyo ta kian likhjay,\r\npyaalo purr, akhiyun chhulkan, asan jo biyo sikanr kehro!', NULL, 'en', '2023-10-01 23:26:35', '2023-10-01 23:26:35', NULL),
(1463, 75, 6, 'sittooon-uddarn-athan-dharkan-wajjan-thiyun-saaz-waangy-je', 'sittooon uddarn, athan, dharkan, wajjan thiyun saaz waangy je,\r\nlajjaraa lafuzu daaman men, bhalaa tin khan likanr kehro!', NULL, 'en', '2023-10-01 23:26:35', '2023-10-01 23:26:35', NULL),
