(1680, 115, 8, 'baaran-jo-Aa-shorr-jite', 'baaran jo Aa, shorr jite\r\nranga rangi Aa, morr ute\r\ntoto boll budhae tho\r\nsabh je dil windraai tho\r\nhanj pakhi aen badkoon achhyoo\r\npaarni men rangeen machhyun\r\nnachi kudi piyo tho baandr\r\nacho ghumoon sabh chirrya gharu', NULL, 'en', '2023-10-02 00:29:33', '2023-10-02 00:29:33', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1681, 115, 8, 'zebro-aahe-patta-patti', 'zebro aahe patta patti\r\ncheeti khan kujhh beeho hatti\r\nhanranr, maangar machh, jhang bbilo\r\nuthh - pakhi hi, hin saan milo\r\nrichh hi jhumar paae tho\r\nbholo diso, cheerrai tho\r\nsuhnro keddo Aa, kachhar\r\nacho ghumoon sabh chirrya gharu', NULL, 'en', '2023-10-02 00:29:33', '2023-10-02 00:29:33', NULL),
(1682, 115, 8, 'acho-ghumoon-sabh-chirrya-gharu', 'acho ghumoon sabh chirrya gharu\r\nwaseel jite dilchasp shahar.', NULL, 'en', '2023-10-02 00:29:33', '2023-10-02 00:29:33', NULL),
(1683, 117, 21, 'jadahin-aahe-milan-mushkil-wabaun-je-zamane-men', 'jadahin aahe milan mushkil, wabaun je zamane men,\r\nbhalaa poe kian sanbhalyun dil, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1684, 117, 21, 'akelo-ko-hali-keseen-andar-je-ret-aali-Aa', 'akelo ko hali keseen, andar je ret aali Aa,\r\npare Aa piyaar jo saahil, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1685, 117, 21, 'tirran-thaa-gul-baraabar-piyaa-na-gajra-keru-poea-tho', 'tirran thaa gul baraabar piyaa, na gajra keru poea tho,\r\nkaraae kaa na Aa qaatil, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1686, 117, 21, 'annag-te-hooe-akeli-hui-chumyu-paee-chandu-baadal-khe', 'annag te hooe akeli hui, chumyu paee chandu baadal khe,\r\nsitaran paee kai jhilmil, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1687, 117, 21, 'pakhiyan-jaa-tahak-sagiyaa-hina-chumi-motyo-piyo-popat', 'pakhiyan jaa tahak sagiyaa hina, chumi motyo piyo popat,\r\nwisaare paarn aahe khill, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1688, 117, 21, 'khiwanr-jean-tun-khani-banho-chaween-haa-khawaab-men-murki', 'khiwanr jean tun khani banho chaween haa khawaab men murki,\r\nmuhinjaa mahboob mun saan mill, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1689, 117, 21, 'kadahn-ean-ko-na-ho-sochiyo-na-milndien-khawaab-men-moonsa', 'kadahn ean ko na ho sochiyo, na milndien khawaab men moonsa,\r\nsajarn! theenden safa sangdil, wabaun je zamane men', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1690, 117, 21, 'maranu-waraa-mari-windaa-piyaa-jeanda-jeyarn-waraa', 'maranu waraa mari windaa, piyaa jeanda jeyarn waraa,\r\nkariyo piyaa paarn khe qaatal, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1691, 117, 21, 'digho-jeewan-jeyarn-kehro-wel-lamhoo-hayaati-ho', 'digho jeewan jeyarn kehro, wel lamhoo hayaati ho,\r\nzamane jo kayo zaail, wabaun je zamane men,', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1692, 117, 21, 'bhare-tun-jaam-neenan-jaa-kande-madhahosh-heean-manro', 'bhare tun jaam neenan jaa, kande madhahosh heean manro,\r\nmachaayu saa kithe mehfil, wabaun je zamane men', NULL, 'en', '2023-10-02 00:31:23', '2023-10-02 00:31:23', NULL),
(1693, 118, 21, 'kadahn-tun-khayaal-waangy-aen-kadahn-tun-khawaab-neenan-men', 'kadahn tun khayaal waangy aen kadahn tun khawaab neenan men\r\nkadahn waria unjayal aan, kadahn selaab neenan men', NULL, 'en', '2023-10-02 00:31:54', '2023-10-02 00:31:54', NULL),
(1694, 118, 21, 'wari-warqan-jiyaan-wapas-asan-uthli-achaun-todday', 'wari warqan jiyaan wapas, asan uthli achaun todday\r\npanaa ware paee parrhjain, asaanja baabu neenan men', NULL, 'en', '2023-10-02 00:31:54', '2023-10-02 00:31:54', NULL),
(1695, 118, 21, 'kare-tun-saath-sawanr-jaa-khili-khiwryan-jiyaan-eendien', 'kare tun saath sawanr jaa, khili khiwryan jiyaan eendien\r\nuhaai unjj manreendien, uho e aab neenan men.', NULL, 'en', '2023-10-02 00:31:54', '2023-10-02 00:31:54', NULL),
(1696, 119, 21, 'shafaq-joon-jhaalroon-jhumki', 'shafaq joon jhaalroon jhumki,\r\njadahin laharn mathan luddandyoon,\r\ntadahn sindhu sajjo muhinjo,\r\nchityun chholyoon jadahin chhulndyoon,\r\ntadahn sargam jiyaan hoondo,\r\nna hoondas maan ta chhaa thi piyo,\r\nchadyaan mun khawaab hina poyaan,\r\nuhe je dhoor rahan men,\r\nuhe sabh khawaab munkhe je,\r\nkadahn sunsaan sanjhia jo,\r\ndinaa \"sann\" je sanam jaani,\r\nsadaa namnaak akhryan men,\r\nsanbhale mun rakhyaa haa je,\r\nawhan taaen pujjainr lay,\r\nkajo sabh muaaf mahboob!', NULL, 'en', '2023-10-02 00:34:09', '2023-10-02 00:46:41', '2023-10-02 00:46:41'),
(1697, 119, 21, 'wajan-muhinjo-wachan-aahe', 'wajan muhinjo wachan aahe,\r\nwanjay tehankhay budhainr lay\r\nachho pahran paae jo,\r\nchitti kanhin chandani jehro,\r\nsajjo mehran paae jo,\r\nwanjay mar marjay murqad men,\r\nsuto ho sindh sareende,\r\nalaa! azaad panchhia jaan,\r\njugan je jind sareende,\r\nbudho sabh hamsafar saathi!\r\nbireh muhinjo badan aahe,\r\nwisal je wati mun warti,\r\nwichhorro thiyo watan aahe,\r\nalaa! aayal kayaan maan kian,\r\nwajan muhinjo wachan aahe,', NULL, 'en', '2023-10-02 00:34:09', '2023-10-02 00:46:41', '2023-10-02 00:46:41'),
(1698, 119, 21, 'iho-sabh-kujhh-budhainr-lay', 'iho sabh kujhh budhainr lay,\r\ninhi aadi juggadia khe,\r\nchhadiya je khawaab to poyaan,\r\nsafa bharpur thi piyaa hin,\r\npireen jo por thi piyaa hin,\r\npiyaaryo madh to jeko,\r\nmuaa! makhmoor thi piyaa hin,\r\nalharr hina dees joon akhrryoon,\r\nnachan piyun naznainan jaan,\r\nnazara jo noor thi piyaa hin,\r\nkajo sabh muaaf mahboobau!\r\nchhade wich raah men haanray,\r\nwajan muhinjo wachan aahe,', NULL, 'en', '2023-10-02 00:34:09', '2023-10-02 00:46:41', '2023-10-02 00:46:41'),
(1699, 119, 21, 'waji-maan-yaar-khe-chawands', 'wanjay maan yaar khe chawands,\r\nalaa! tunhinji ashiqan khan,\r\nihaa manzil pare naahe,\r\ntuttyoon jarn choddasa sanghroon,\r\nubhoo thiyo aajpo aahe.\r\nwajan diyo haanr mahboob!\r\nwanjay huna paar maan chawands,\r\nawhan khan jo natho wisri,\r\nawhanjay yaar khe chawands,\r\nwajan muhinjo wachan aahe,\r\nwajan diyo haanr mahboobau!', NULL, 'en', '2023-10-02 00:34:09', '2023-10-02 00:46:41', '2023-10-02 00:46:41'),
(1700, 120, 12, 'ghulaami-ghulaami-ghulaami-je-zilat!', 'ghulaami ghulaami ghulaami je zilat!\r\nna kaa werrh aahe na aahe baghawat!', NULL, 'en', '2023-10-02 00:36:25', '2023-10-02 00:47:30', '2023-10-02 00:47:30'),
(1701, 120, 12, 'kuhee-wath-zamana-asan-je-bachan-khe', 'kuhee wath zamana asan je bachan khe,\r\nmagar wakt dendo asan wazahat!', NULL, 'en', '2023-10-02 00:36:25', '2023-10-02 00:47:30', '2023-10-02 00:47:30'),
(1702, 120, 12, 'kadahn-neth-tareekh-wathndi-palo-sabh-', 'kadahn neth tareekh wathndi palo sabh,\r\nkadahn wakt tokhe b thindi nadamat!', NULL, 'en', '2023-10-02 00:36:25', '2023-10-02 00:47:30', '2023-10-02 00:47:30'),
(1703, 121, 21, 'kujh-shair-likhan-ghurjan', 'kujh shair likhan ghurjan\r\nkujh piyaar karanu ghurje', NULL, 'en', '2023-10-02 00:36:59', '2023-10-02 00:36:59', NULL),
(1704, 121, 21, 'ko-pahar-hayaati-je', 'ko pahar hayaati je\r\nkheese maan kaje chori\r\ndaryah je kinaare te\r\nkaa der wanjay thori\r\nlaharan joon budhi laatyun\r\nlaharan saan kajan baatyun\r\nkaa gaalh unjay man je\r\ndaryah saan salanr ghurje\r\nkujh piyaar karanu ghurje\r\nkujh shair likhan ghurjan', NULL, 'en', '2023-10-02 00:36:59', '2023-10-02 00:36:59', NULL),
(1705, 121, 21, 'ko-pahar-hayaati-jo', 'ko pahar hayaati jo\r\njhanglan men wanjay ghumjay\r\naen dil je taari taan\r\nke pal pakhiyan jahiraa\r\nbe chen Hawaa wangur\r\nchanchal akhiyan jahiraa\r\npal pal thaa adan jekar\r\npal pal e adanr ghurje\r\nkujh piyaar karanu ghurje', NULL, 'en', '2023-10-02 00:36:59', '2023-10-02 00:36:59', NULL),
(1706, 121, 21, 'kujh-shair-likhan-ghurjan', 'kujh shair likhan ghurjan \r\nko pahar hayaati jo\r\nsahra saan wanjay miljay\r\nware je wichhai te\r\nkaa raat wanjay rahjay\r\nbhitt bhitt je seeny men\r\nko raaz wanjay rakhjay\r\nkujh megh mundun dei\r\nkaa piyaas wathan ghurje\r\nkujh piyaar karanu ghurje\r\nkujh shair likhan ghurjan', NULL, 'en', '2023-10-02 00:36:59', '2023-10-02 00:36:59', NULL),
(1707, 121, 21, 'ko-pahar-hayaati-jo', 'ko pahar hayaati jo\r\nkhud daanh b waranr ghurje\r\npe keru ralyu mun men\r\nmaan kanhin saan milyo aahiyan\r\nho keru runo mun men\r\nmaan kanhin saan khulyu aahyan\r\nkujh khud khe disarn ghurje\r\nkujh khud khan puchhanr ghurje\r\nkujh piyaar karanu ghurje\r\nkujh shair likhan ghurjan', NULL, 'en', '2023-10-02 00:36:59', '2023-10-02 00:36:59', NULL),
(1708, 122, 21, 'ajoko-deenhan-aggyan-muhinji', 'ajoko deenhan aggyan muhinji\r\nkharro Aa aainai waangy\r\ndisaan piyo pahinje chahry khe\r\nsunjatal shakhs ko aahe\r\nmilyo ho kujhh ddahaka agg\r\niho e aqas ko aahe\r\nakhiyun aabad galhyan jaan\r\nbharyal bharpur chahran saan\r\nbbah tte gorhaa hujarn saan gadu\r\nsathyal siwaai rang sapnan saan\r\najoko deenhan\r\nhujarn aen na hujarn wich men\r\npakki deewar waangy Aa\r\nkadahn ulko andar jo Aa\r\nkadahn kanhin piyaar waangy Aa\r\najooki deenhan je aangur\r\njhali wapas wraan jekar\r\nwanjay nandhpirn je nadiyan men\r\nbuddan aen piyo taraan jekar\r\nwari nandhpirn je wattan te\r\nblooran saan wanjay girrgaan\r\njhalyaan kaa ddor baanhan je\r\nphiran bbehaar lattan saan\r\najoko deenhan olaalnr\r\nulaaso goth jo sawanr\r\njadahin kagar je bairri te\r\nghumyaasin paarn huwa wanu wanu\r\npattanr bhe paarn thiyaa huwasin\r\nkadahn patwar bhe huwasin\r\njhalyoon seen chuhnb saan jharkyoon\r\nwado ko jaarr bhe huwasin\r\nnindhh hoondy wadaa huwasin\r\nwadaa thi ajj nandhaa thiyaa hoan\r\nnandhyun raatyun، waddyun sochoon\r\nalaay chhaa maan chhaa thiyaa hoan\r\nkadahn aahe yakin jehro\r\nkadahn kanhin waswase waangy\r\najoko deenhan aggyan muhinji\r\nkharro Aa aainai waangy', NULL, 'en', '2023-10-02 00:37:29', '2023-10-02 00:37:29', NULL),
(1709, 123, 21, 'darawar-lok-dharti-te', 'darawar lok dharti te,\r\nayaman khan akelo Aa\r\nna jin jo ko dharam aahe\r\nna jin jo ko qabeelo Aa\r\nnaki tahakan jaa tollaa hina\r\nnako murkan jo melo Aa\r\nbadan men be badan jeke\r\nwatan men be watan aahin\r\nsamajan je sunjarap aen\r\nna qomun jo kafan aahin\r\nsandai e dil je dharti Aa\r\nute e se dafan aahin\r\nluddan je laash waangy se\r\nna ahre laam e aahe\r\nsunjaato kanhin subuh naahe\r\nna jaato shaam e aahe\r\nrakhe je raees putt te ko\r\nna ahro naam e aahe\r\najoka aib hanra hi\r\nuhe apmaan dharti jaa\r\nkadahn se paarn raajaa haa\r\nkadahn sultaan dharti jaa\r\nna ko bhotaar tin lay ho\r\nna ke e khan dharti jaa\r\nsajjai taaj sooraj jo\r\nsadaa sindhu kinaran te\r\nhannri pararra hawaun jaa\r\nnachnda haa nazaran te\r\nnasheela nainr likhnda huwa\r\nsandai tahzeeb taaran te\r\nuhe e paarn phaarhaa huwa\r\nuhe e sheenh jangal jaa\r\npakhi thi tin de indaa huwa\r\nnawaan ke neehan jangal jaa\r\nsadaain soom rass waangy\r\nwathaa huwa meehan jangal jaa\r\nnaseeban jaa nayaau pahnja\r\nnathaa kan raham raanran te\r\nachan udanda pakhiyan waangy\r\nuhe dokhan je daanran te\r\nwikaman piyaa watan men se\r\nnadoraa neech naanrn te\r\nna ko ajj wanu tin jo Aa\r\nna koi kheet tin jo thiyo\r\nrugo hiku chandu tin jo Aa\r\nna koi chatt tin jo thiyo\r\nrugo hiku bukh tin je Aa\r\nrugo biyo pett tin jo thiyo\r\nsamoondan khan sikayal se\r\nna dhandhun aen na dhorraa hina\r\nchapan te e chethryaan wiya\r\nsandan laadaa aen moraa hina\r\nna koi ang indlath thiyo\r\nlagan kagar ke kora hina\r\nna mendhi ko chatyu hathrro\r\nna kaai akh kajal kaari\r\nayaanra ishiq baazi khan\r\nna deedun aen na daldaari\r\njaddi se jeeu pahinje te\r\nkhanran piyaa bhoon hi bhaari\r\nsamoora wanu seeny te\r\numeedan saan apaaya huwa\r\ndaryah jeke dilian men tin\r\nnazaan saan nipayaa huwa\r\nbukhee hina wakt wapari\r\nkhani se e khapaya huwa\r\nugharaa baaru jin jaa ajj\r\nazal wangur ayaan aahin\r\nsindhoran khan sunjyu seendhoon\r\njugan je juth jiyaan aahin\r\najaan olaad aadam jo\r\nchawe se mun manjhaan aahin\r\nkadahn pandaran pahinje taan\r\nna duniyaa heth aai Aa\r\nbhale tareekh tin je lay\r\npakki thi seth aai Aa\r\nmagar kaa moj sindhu men\r\ndai kaa deeth aai Aa\r\nwari naon ubh aaneendo\r\nsaween ke sij tin je lay\r\nkhiwarn kaai khilee dende\r\nniyapaaa nijj tin je lay\r\nchhade sabh shaah halyaa windaa\r\nbanyun aen bij tin je lay\r\npare moen darre taan dis\r\nwari Aa wok dharti te\r\nzamane je hawaun men\r\nhali Aa hok dharti te\r\nnawaan ke neehan nezan jaan\r\nnachaae nok dharti te\r\nchawe paee chaal duniyaa je\r\ngali aen chok dharti te\r\nsamoora soor peanda ho\r\ndai kaa dooh dharti te\r\naqalmando ! awhan khe je\r\nlagan thaa ttok dharti te\r\nadeenda dees pahnjo se\r\ndarrawar lok dharti te.', NULL, 'en', '2023-10-02 00:37:54', '2023-10-02 00:37:54', NULL),
(1710, 124, 21, 'munkhe-jeewan-je-rirn-men-akelo-chadeen', 'munkhe jeewan je rirn men akelo chadeen\r\ntunhinji chahry te murkan joon makhryun tirran\r\nmunkhe biyo chhaa khape!', NULL, 'en', '2023-10-02 00:38:26', '2023-10-02 00:38:26', NULL),
(1711, 124, 21, 'tunhinjo-aanchal-hi-ubh-khan-utaahun-rahe', 'tunhinjo aanchal hi ubh khan utaahun rahe\r\nbadalan je sadaa chhanw to te hujy\r\nchandu tunhinji karaai jo kanganr hujy\r\naen sitaraa sandai seendh te piyaa chharnan\r\nmunkhe biyo chhaa khape!', NULL, 'en', '2023-10-02 00:38:26', '2023-10-02 00:38:26', NULL),
(1712, 124, 21, 'to-chayo-ho-ta-pahnjo-ko-naatoo-na-Aa', 'to chayo ho ta pahnjo ko naatoo na Aa\r\nmun chayo ho، to munkhe sunjaato na Aa\r\npar je bhulji gharri lay akhiyan men diseen\r\nmuhinji lurkan men sabh rang tuhnja hujan\r\nmunkhe biyo chhaa khape!', NULL, 'en', '2023-10-02 00:38:26', '2023-10-02 00:38:26', NULL),
(1713, 124, 21, 'thi-saghy-tho-munkhe-tun-wisaare-chadeen', 'thi saghy tho munkhe tun wisaare chadeen\r\nmuhnjun yaadun pakhiyan jean udaari chadeen\r\npar jadahin bhe wisanr je koshish karin\r\nkujhh garam saah tunhinji andar maan uthan\r\nmunkhe biyo chhaa khape!\r\nmunkhe biyo chhaa khape!', NULL, 'en', '2023-10-02 00:38:26', '2023-10-02 00:38:26', NULL),
(1714, 125, 21, 'sadiyan-khan-jaa-sindhu-aahe', 'sadiyan khan jaa sindhu aahe\r\nasanji deed je dewi\r\nhaseena hooba ho aahe\r\nasan lay sij saman jekaa\r\nasan lay mahiro aahe\r\nazal aen abad je wich men\r\nkharri jaa khoobru aahe\r\nasan jo jeeyu jiyaapo aen\r\nasan je justaju aahe\r\nasan lay ang ochhanr Aa\r\nasan je aabroo aahe\r\nsadiyan khan jaa sindhu aahe', NULL, 'en', '2023-10-02 00:38:51', '2023-10-02 00:38:51', NULL),
(1715, 126, 21, 'samund-ho-poor-jobhan-te', 'samund ho poor jobhan te,\r\ngajee men peer huwa pahnja,\r\nkinaro kaak jo tulsam,\r\ngharri khe gheir huwa pahnja,\r\nparahaien bett taan sooraj,\r\nlatho paee ho chhade laali,\r\nlago hu jarn haseena je,\r\nkare huii kan maan waali,\r\nghityun baraat banje wayoon,\r\ngunahan gungnayo paee,\r\nHusn jo waar ahro ho,\r\nsawaban sir likaayu paee,\r\nchitaya chandu paee pahnja,\r\nzalyaa paee roop rahan men,\r\njadahin jug mag jawanian jaa,\r\nbbarya fanoos baanhan men,\r\nshahar shaele jiyaan bharrkyo,\r\njale har kaa jawani hui,\r\nkalab، baroon bahana huwa,\r\nzahar men zindagani hui,', NULL, 'en', '2023-10-02 00:40:23', '2023-10-02 00:40:23', NULL),
(1716, 126, 21, 'gilaasan-men-gagan-ote', 'gilaasan men gagan ote,\r\nsitaraa jahin b peeta paee,\r\nsamund seeny manjhaan chhulkyo,\r\nkinara jahin b peeta paee,\r\nna wichharn jo wakhar ho ko,\r\nwisal waparu waangy ho,\r\nnango thi je nacheen haa tun,\r\nbirah baazaar waangy ho,\r\nunhan jaa naaz pahnja, je,\r\nnachi un baahi maan aayaa,\r\npachanr un baah maan aayaa,\r\npachanr kehro pachaai tin,\r\npachi un men aayaa,\r\nuhe kehra badan chaiba,\r\nbache un baah maan aayaa,\r\nmamanr man men mato rahndo,\r\nmachi un baah maan aayaa,\r\nhayati je haqeeqat kaa,\r\npataya, khan pare naahe,\r\njale jeko utaan nikto,\r\nwari hi po tharri naahe,', NULL, 'en', '2023-10-02 00:40:23', '2023-10-02 00:40:23', NULL),
(1717, 127, 21, 'tarri-tareekh-maan-aai-ihaa-sindh-kian-buddi-saghndi', 'tarri tareekh maan aai, ihaa sindh kian buddi saghndi,\r\nbbudda hin gharu, zameenon par, andar pahnjo buddi naahe.\r\nbuddi waattun wayoon chhaa thiyo, andheran maan piyo achbo,\r\nsaweran daanh halyu jeko, safar pahnjo buddi naahe.', NULL, 'en', '2023-10-02 00:41:03', '2023-10-02 00:41:03', NULL),
(1718, 128, 21, 'kalh-puchiyo-kanhin-raste-wende', 'kalh puchiyo kanhin raste wende\r\naakhir kehro dees Aa tunhinjo?\r\nmun ta chayo maan sindhi aahiyan\r\nsindhri jindarri dees Aa muhinjo\r\nhoown ta aadam zaado aahiyan\r\njeko payaan wiyas Aa muhinjo', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1719, 128, 21, 'hina-duniyaa-men-huna-duniyaa-men', 'hina duniyaa men huna duniyaa men\r\nahre kehnji dharti naahe\r\njugg jugan khan aahe muhinji\r\nveeza te mun warti naahe\r\nubhri aahe sabhri aahe\r\nsodagar lai sassti naahe', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1720, 128, 21, 'jahin-te-chandu-mudaami-aashiq', 'jahin te chandu mudaami aashiq\r\njahin jo saathi sij azal khan\r\njahin jaa dard darawar waangy\r\njahin jaa neehan b nijj azal khan\r\njahin khe banyun bbara pahnja\r\njahin khe pahnja bij azal khan', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1721, 128, 21, 'jahin-wati-laar-utar-aen-kaachho', 'jahin wati laar utar aen kaachho\r\njahin wati thar bar ddahar qadeemi\r\nggarhyal gaalhiyun ggott inhi jaa\r\nshalan jahiraa shahar qadeemi\r\njahin wati pahnjun maakhyoon aahin\r\njahin wati pahnja zahar qadeemi', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1722, 128, 21, 'jahin-men-hikro-masat-qalandar', 'jahin men hikro masat qalandar\r\njholee lalanr laal sadaain\r\nun alahq aleenda sachal\r\nsindh men saalaa saal sadaain\r\njahin wati kei shaah inayat\r\nmaqtal malaa maal sadaain', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1723, 128, 21, 'jahin-wati-hikro-sayed-aahe', 'jahin wati hikro sayed aahe\r\njahin wati suhnro shaah bhittai\r\ntand tanoro taanri harko\r\nggaaye wetho tunhinji waai\r\nboli jahin je dode jehri\r\nloli jahin je bbaghal bbaai', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1724, 128, 21, 'bukh-b-pahinje-unjj-b-pahinje', 'bukh b pahinje unjj b pahinje\r\njahin wati pahnja ddukh b aahin\r\njahin men saalan khan poi indaa\r\nsawanr waangy sukh b aahin\r\npahinjan dhaariyan jaa sabh gadji\r\nteer takeenda kukh b aahin', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1725, 128, 21, 'jahin-wati-ko-meezail-naahe', 'jahin wati ko meezail naahe\r\njahin wati atom bum b naahe\r\njahin wati pahinje foj b naahe\r\njahin jo una khe gham b naahe\r\nbi kaa dharti dhoor karanu jo\r\nuna khe kojho kam b naahe', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1726, 128, 21, 'tun-chau-chhaa-khe-haar-manjeen-tho', 'tun chau chhaa khe haar manjeen tho\r\ntokhe kehry jeet budhayan\r\ndharti khe je maau chawan thaa\r\ntokhe ahraa mayat budhayan\r\ntokhe thaa je nooha ghurjan\r\ntokhe pahnja gita budhayan', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1727, 128, 21, 'yaaran-jo-je-yaar-tun-aahen', 'yaaran jo je yaar tun aahen\r\npoe maan tokhe yaar budhayan\r\npar tokhe gadar wanan je\r\nchau ta khani gadar budhayan\r\npareedsi chau tokhe pahnja\r\nkehra kehra paar budhayan', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
(1728, 128, 21, 'poe-mun-thadhrro-saah-khanyo-ho', 'poe mun thadhrro saah khanyo ho\r\nhuna khan huna jo dees puchhyu ho\r\nthoro murki hu ta halyu wayo\r\nsochayam chhaa mun khes puchhyu ho?\r\nkalh jadahin kanhin wende wende\r\nmun khan muhinjo dees puchhyu ho', NULL, 'en', '2023-10-02 00:41:45', '2023-10-02 00:41:45', NULL),
