(1775, 0, 2, 'watan-lay-tattia-men-thadharri-waau-jiyaan-ho', 'وطن لئہ تَتي ۾ ٿَڌڙي واءُ جيان ھو،\r\nاسان لئہ اسان جو رھبر ماءُ جيان ھو.', '[\"watan\"]', 'sd', '2023-10-02 02:09:57', '2023-10-02 02:09:57', NULL),
(1776, 0, 2, 'watan-lay-tattia-men-thadharri-waau-jiyaan-ho', 'watan lay tattia men thadharri waau jiyaan ho،\r\nasan lay asan jo rahbar maau jiyaan ho.', '[\"watan\"]', 'en', '2023-10-02 02:10:23', '2023-10-02 02:10:23', NULL),
