(1829, 261, 2, 'huna-jo-thadho-jism', 'huna jo thadho jism\r\nkhatt te piyo aahe\r\nkanhin wakt\r\ntharrkndo rahndo ho\r\naen keetryan e dilian khe\r\ndharrkando chhade windo ho', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1830, 261, 2, 'huna-je-peran-je-payal', 'huna je peran je payal\r\njadahin chhamknde hui ta\r\ntara raqas karanu lagnda huwa\r\naen jadahin raqas kande hui ta\r\nchandu jhumryoon wijhndo ho', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1831, 261, 2, 'huna-je-kehry-tareef-kaje', 'huna je kehry tareef kaje\r\njekaa jadahin jawaan hui ta\r\nhar shair ji\r\nshaairia jo mahoor hui', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1832, 261, 2, 'heu-laash-kanhin-jo-aahe', 'heu laash kanhin jo aahe؟\r\nuhe puchhe rahiya aahin\r\njeke raatyun rangeen kandaa huwa\r\nhuna je kothee te\r\naen wahashi banje\r\nhuna je kooanre jism khe\r\nwall jean wakoray windaa huwa', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1833, 261, 2, 'ajj-huna-je-ddoli-khe', 'ajj huna je ddoli khe\r\nchaar kulhaa na thaa milan\r\njahin khe kanhin wakt\r\nhathan te manhun khanrda huwa', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1834, 261, 2, 'ajj-huna-je-janaze-nimaaz', 'ajj huna je janaze nimaaz\r\nparhanr laai\r\nko b tayaar naahe\r\njahin je Husn aado\r\nkadahn،\r\nkei mardu sajda rez honda huwa', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1835, 261, 2, 'heu-laash', 'heu laash\r\nuna raqsa jo aahe\r\njahin je raqas saan\r\nbaazaar jaa\r\nfanoos bbarnda huwa', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1836, 261, 2, 'heu-laash', 'heu laash\r\nuna raqsa jo aahe\r\njahin je dam saan \r\nhiku gharu ji chulh bbarnde hui\r\naen ghanran je jism je baah tharnde hui!', NULL, 'en', '2023-10-02 04:38:34', '2023-10-02 10:02:45', '2023-10-02 10:02:45'),
(1837, 260, 2, 'ahro-chahro-jahin-khe', 'اھڙو چھرو، جنھن کي\r\nڪنولَ جي گُل\r\n۽ چنڊ سان تشبيھہ ڏيڻ کان اڳ\r\nاھو سوچڻو پوندو تہ\r\nڇا ان کان بہ وڌيڪ\r\nڪا اَڇُهي ۽ نڪور تشبيھہ ٿي سگهي ٿي؟', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
(1838, 260, 2, 'huna-je-chapan-te', 'هُن جي چپن تي\r\nجهڙوڪ\r\nمُرڪ جو محل جرڪي رھيو آھي\r\n۽ اکين مان آبشار جو\r\nآخري قطرو ڪري رھيو آھي', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
(1839, 260, 2, 'sandas-ggal-te', 'سندس ڳلَ تي\r\nوارن جِي چڳ\r\nھينئر بہ پھرو ڏئي رھي آھي', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
