(1842, 260, 2, 'jadahin-hooa-jawaan-hui-ta', 'جڏھن هُوءُ جوان هئي تہ\r\nمُڙسالن جي گهيري ۾\r\n۽ ٽهڪن جي طرف هئي', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
(1843, 260, 2, 'huna-jo-thadho-jism', 'هُن جو ٿڌو جسم\r\nکٽ تي پيو آھي\r\nڪنھن وقت\r\nٿڙڪندو رھندو ھو\r\n۽ ڪيترين ئي دلين کي\r\nڌڙڪندو ڇڏي ويندو ھو', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
(1844, 260, 2, 'huna-je-peran-je-payal', 'هُن جي پيرن جي پايل\r\nجڏھن ڇمڪندي هئي تہ\r\nتارا رقص ڪرڻ لڳندا هئا\r\n۽ جڏھن رقص ڪندي هئي تہ\r\nچنڊ جهمريون وجهندو ھو', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
(1845, 260, 2, 'huna-je-kehry-tareef-kaje', 'هُن جي ڪھڙي تعريف ڪجي\r\nجيڪا جڏھن جوان هئي تہ\r\nھر شاعرَ جِي\r\nشاعريءَ جو محور هئي', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
(1846, 260, 2, 'heu-laash-kanhin-jo-aahe', 'هيءُ لاش ڪنھن جو آھي؟\r\nاُهي پُڇي رهيا آھن\r\nجيڪي راتيون رنگين ڪندا هئا\r\nهُن جي ڪوٺي تي\r\n۽ وحشي بڻجي\r\nهُن جي ڪُونئري جسم کي\r\nوَل جئن وڪوڙي ويندا هئا', NULL, 'sd', '2023-10-02 04:42:03', '2023-10-02 04:42:03', NULL),
