(2091, 20, 6, 'hiku-dars-Hassan-aahe-chae-tho-rugo-muhabat-de', 'ھڪ درس ”حسن“ آھي، چئي ٿو رڳو محبت ڏي،\r\nدنيا تہ الاھي ڇا ڇا، ڪم ڪار ڪري ٿي پئي.', NULL, 'sd', '2023-10-02 21:25:48', '2023-10-02 21:25:48', NULL),
(2092, 20, 6, 'sandal-joon-barran-kaathyun-ean-dil-b-bbarri-thi-paee', 'sandal joon barran kaathyun, ean dil b bbarri thi paee,\r\nmuhinje ta siwaa tunhinje kaa saait b sari thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2093, 20, 6, 'waaria-je-darran-waangy-aaon-b-udaanmaan-piyo', 'waaria je darran waangy, aaon b udaanmaan piyo,\r\nthaa nainr thakan thar jaa aen raat tharri thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2094, 20, 6, 'meelap-judaai-jo-hi-ajeeb-tamasho-Aa', 'meelap judaai jo, hi ajeeb tamasho Aa,\r\njekaa Aa andar wethe, saa chho ta pare thi paee?', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2095, 20, 6, 'waaran-men-wichorray-je-piyo-yaar-phanri-pheryaan', 'waaran men wichorray je, piyo yaar phanri pheryaan,\r\nchupp chaap ache chaandi, kaaran saan kare thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2096, 20, 6, 'piyaa-pan-chharnan-paanrhen-kharkan-b-ta-nathaa-khaali', 'piyaa pan chharnan paanrhen, kharkan b ta nathaa khaali,\r\nhiku shakhs je hiku duniyaa, khamosh mari thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2097, 20, 6, 'mun-saan-je-mileen-haa-tun-ta-maan-budhayan-haa', 'mun saan je mileen haa tun, ta maan budhayan haa,\r\nhaai wakt waji tho piyo, aen mahal tarri thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2098, 20, 6, 'hiku-akh-rahe-aali-khanu-pal-na-rahe-khaali', 'hiku akh rahe aali, khanu pal na rahe khaali,\r\nhiku menr batti aahe, deenhan raat bbarri thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2099, 20, 6, 'hiku-dars-Hassan-aahe-chae-tho-rugo-muhabat-de', 'hiku dars \"Hassan\" aahe، chae tho rugo muhabat de،\r\nduniyaa ta ilhaai chhaa chhaa, kam kaar kare thi paee.', NULL, 'en', '2023-10-02 21:26:09', '2023-10-02 21:26:09', NULL),
(2100, 46, 6, 'tunhinjaa-lab-sindhua-jaa-kap', 'tunhinjaa lab sindhua jaa kap,\r\nchumanr de tun pahnja chup.', NULL, 'en', '2023-10-02 21:27:40', '2023-10-02 21:27:40', NULL),
(2101, 46, 6, 'jekaa-mayaa-chaheen-wariyaan', 'jekaa mayaa chaheen wariyaan,\r\nmun khan wath tun motiyaan lap.', NULL, 'en', '2023-10-02 21:27:40', '2023-10-02 21:27:40', NULL),
(2102, 46, 6, 'wejho-aaon-orray-theeia', 'wejho aaon, orray theeia\r\ntahhi keen pare de tapp.', NULL, 'en', '2023-10-02 21:27:40', '2023-10-02 21:27:40', NULL),
(2103, 46, 6, 'dil-waaran-khan-kehrryoon-dooryun', 'dil waaran khan kehrryoon dooryun,\r\nkehra dejjaa, kehra dap.', NULL, 'en', '2023-10-02 21:27:40', '2023-10-02 21:27:40', NULL),
(2104, 46, 6, 'haai-Hassan-b-habbachhi-kehro', 'haai Hassan b habbachhi kehro,\r\ntunhinje jawani munhinjaa jhap', NULL, 'en', '2023-10-02 21:27:40', '2023-10-02 21:27:40', NULL),
(2105, 293, 19, 'abid-tokhaan-chhaa-chhaa-na-ghuryo', 'عابد توکان\r\nساري سَڀُ عبادت عيوض\r\nزاھد توکان\r\nساري سَڀُ رياضيت عيوض\r\nڇا ڇا نہ گهريو\r\nمنھنجا مولى\r\nجنت طلبي\r\nحُور مڱي\r\nغلمان گهريو', NULL, 'sd', '2023-10-02 21:36:21', '2023-10-02 21:36:21', NULL),
(2106, 293, 19, 'munhinjaa-mola', 'منھنجا مولى\r\nمون توکان\r\nڪجهه ڪونہ گهريو\r\nحورَ،\r\nنہ ڪو غلمان گهريو\r\nمون تہ رڳو\r\nھڪ گيت گهريو\r\nمن ميت گهريو', NULL, 'sd', '2023-10-02 21:36:21', '2023-10-02 21:36:21', NULL),
(2107, 293, 19, 'aen-tunhinje-dar-taan', '۽ تنھنجي در تان\r\nپالهو پانڌ کڻي\r\nڪو بہ نہ موٽيو!', NULL, 'sd', '2023-10-02 21:36:21', '2023-10-02 21:36:21', NULL),
(2108, 294, 19, 'sindhrri-surr-sangeet-sughandh', 'سِنڌڙي سُر سنگيت سُڳنڌ\r\nمون کي سائينءَ جو سوڳنڌ', NULL, 'sd', '2023-10-02 21:40:09', '2023-10-02 21:40:09', NULL),
(2109, 294, 19, 'mushahido-manranr-chaheen', 'مشاھدو ماڻڻ چاھين\r\nڪور ڪُلھن تان پھرين ڪنڌ', NULL, 'sd', '2023-10-02 21:40:09', '2023-10-02 21:40:09', NULL),
(2110, 294, 19, 'sindhrri-jindrri-saah-pasah', 'سِنڌڙي جِندڙي ساھہ پساھ\r\nسُرھو آھي ھر ڪو سَنڌ', NULL, 'sd', '2023-10-02 21:40:09', '2023-10-02 21:40:09', NULL),
(2111, 294, 19, 'latif-hoondy-chhaa-jaa-bhaw', 'لطيف ھوندي ڇا جا ڀَو\r\nسورج ھوندي ڪھڙو اَنڌ', NULL, 'sd', '2023-10-02 21:40:09', '2023-10-02 21:40:09', NULL),
(2112, 294, 19, 'karoonjhar-theejan-imdad', 'ڪارونجهر ٿيجان امداد\r\nجُهڪي وڃي سو ڪھڙو ڪنڌ!', NULL, 'sd', '2023-10-02 21:40:09', '2023-10-02 21:40:09', NULL),
(2113, 287, 7, 'rahandi-sindh-na-sadaa-azaaran-men', 'rahandi sindh na sadaa azaaran men hin dhaariyan je takrarn men', NULL, 'en', '2023-10-02 21:40:33', '2023-10-02 21:40:33', NULL),
(2114, 287, 7, 'rahandi-na-sutal-kaa-qoam-kadahn', 'rahandi na sutal kaa qoam kadahn akh khole uthi jaa qoam jadahin\r\npawandi bhaaj tadahn ta ghadaran men.', NULL, 'en', '2023-10-02 21:40:33', '2023-10-02 21:40:33', NULL),
(2115, 287, 7, 'hi-koondhar-sabh-kar-morray-uthyaa', 'hi koondhar sabh kar morray uthyaa sabh zor zanjeeran ta ttorray uthyaa\r\npaee galh hali ta ganrwaran men.', NULL, 'en', '2023-10-02 21:40:33', '2023-10-02 21:40:33', NULL),
(2116, 287, 7, 'windaa-ddaadh-jabar-jaa-kot-bhurray', 'windaa ddaadh jabar jaa kotu bhurray ajhay ajj yaa subuh kaa heer churre\r\npawanda gul tirri gulzaran men.', NULL, 'en', '2023-10-02 21:40:33', '2023-10-02 21:40:33', NULL),
(2117, 287, 7, 'ajj-juman-sindh-azaad-kariyun-dei-khoon-wari-aabad-kariyun', 'ajj juman sindh azaad kariyun dei khoon wari aabad kariyun\r\nthiyun shaamil pahinje piyaran men.', NULL, 'en', '2023-10-02 21:40:33', '2023-10-02 21:40:33', NULL),
(2118, 289, 19, 'mile-veengas-hui-waseeng-waaran-saan-shikaago-men', 'mili veengas hui waseeng waaran saan shikaago men\r\nalaay chhaa chai wei isharan saan shikaago men', NULL, 'en', '2023-10-02 21:41:00', '2023-10-02 21:41:00', NULL),
(2119, 289, 19, 'takan-saan-gaj-parhyal-saawnri-jo-samhun-aai', 'takan saan gaj parhyal saawnri jo samhun aai\r\nlathee jarn shaam، west - un te sitaran saan shikaago men', NULL, 'en', '2023-10-02 21:41:00', '2023-10-02 21:41:00', NULL),
(2120, 289, 19, 'milyaa-je-aamrika-men-khulyal-dil-saan-milyaa-sindhi', 'milyaa je aamrika men khulyal dil saan milyaa sindhi\r\nkayaaon warr sawarr ”imdad“ paarn saan shikaago men', NULL, 'en', '2023-10-02 21:41:00', '2023-10-02 21:41:00', NULL),
(2121, 289, 19, 'pasaan-tho-aaon-un-men-olrro-hiku-chandu-chahry-jo', 'pasaan tho aaon un men olrro hiku chandu chahry jo\r\nnadi thi taar wahndi wai kinaran saan shikaago men', NULL, 'en', '2023-10-02 21:41:00', '2023-10-02 21:41:00', NULL),
