(2127, 291, 19, 'neehan-hikro-e-thyee-tho-sach-chayee-sav-sekrro', '\"neehan hikro e thyee tho\" sach chayee sav sekrro\r\npar uho hiku neehan thyee tho ketran neehan khan poi!', NULL, 'en', '2023-10-02 21:41:19', '2023-10-02 21:41:19', NULL),
(2128, 293, 19, 'abid-tokhaan-chhaa-chhaa-na-ghuryo', 'abid tokhaan\r\nsaari sabh ibadat ewaz\r\nzahid tokhaan\r\nsaari sabh riyaziyat ewaz\r\nchhaa chhaa na ghuryo\r\nmunhinjaa mola\r\njannat talbi\r\nhoor mangnee\r\nghulman ghuryo', NULL, 'en', '2023-10-02 21:41:41', '2023-10-04 06:07:20', '2023-10-04 06:07:20'),
(2129, 293, 19, 'munhinjaa-mola', 'munhinjaa mola\r\nmun tokhaan\r\nkujh konn ghuryo\r\nhoor,\r\nna ko ghulman ghuryo\r\nmun ta rugo\r\nhiku gita ghuryo\r\nman mayat ghuryo', NULL, 'en', '2023-10-02 21:41:41', '2023-10-04 06:07:20', '2023-10-04 06:07:20'),
(2130, 293, 19, 'aen-tunhinje-dar-taan', 'aen tunhinje dar taan\r\npalho paandh khani\r\nko b na mottoyo!', NULL, 'en', '2023-10-02 21:41:41', '2023-10-04 06:07:20', '2023-10-04 06:07:20'),
(2131, 294, 19, 'sindhrri-surr-sangeet-sughandh', 'sindhrri surr sangeet sughandh\r\nmun khe saain jo sogghandh', NULL, 'en', '2023-10-02 21:41:56', '2023-10-04 06:07:38', '2023-10-04 06:07:38'),
(2132, 294, 19, 'mushahido-manranr-chaheen', 'mushahido manranr chaheen\r\nkor kulhan taan pehreen kandhu', NULL, 'en', '2023-10-02 21:41:56', '2023-10-04 06:07:38', '2023-10-04 06:07:38'),
(2133, 294, 19, 'sindhrri-jindrri-saah-pasah', 'sindhrri jindrri saah pasah\r\nsurho aahe har ko sindh', NULL, 'en', '2023-10-02 21:41:56', '2023-10-04 06:07:38', '2023-10-04 06:07:38'),
(2134, 294, 19, 'latif-hoondy-chhaa-jaa-bhaw', 'latif hoondy chhaa jaa bhaw\r\nsooraj hoondy kehro andh', NULL, 'en', '2023-10-02 21:41:56', '2023-10-04 06:07:38', '2023-10-04 06:07:38'),
(2135, 294, 19, 'karoonjhar-theejan-imdad', 'karoonjhar theejan imdad\r\njhuki wanjay so kehro kandhu!', NULL, 'en', '2023-10-02 21:41:56', '2023-10-04 06:07:38', '2023-10-04 06:07:38'),
(2136, 297, 19, 'nimaa-shaam-ghar-ghar-jean-ddiya', 'نِما شام گهر گهر جئن ڏيا\r\nکُليا ياد جا ريشمي تاڪيا', NULL, 'sd', '2023-10-02 21:58:47', '2023-10-02 21:58:47', NULL),
(2137, 297, 19, 'wari-yaad-aai-uhaai-ghitti', 'وري ياد آئي اُھائي گهٽي\r\nاسان سون ڀانئي ٿي جنھن جي مٽي', NULL, 'sd', '2023-10-02 21:58:47', '2023-10-02 21:58:47', NULL),
(2138, 297, 19, 'khilee-thi-milyaa-aen-mile-thi-khulyaa', 'کِلي ٿي مليا ۽ ملي ٿي کليا\r\nسڀئي ٽورَ تي مورَ واري ٽِليا\r\nاُھي قرب واريون ڪھاڻيون الا\r\nاُھي روح رچنديون رھاڻيون الا\r\nاَلا چوڪ اُھي، ڳوٺ ڳليون اُھي\r\nبريون ڇيلڙيون، شام، ٽليون اُھي\r\nاُھي کيڏندا کيڏندا ٻارڙا\r\nبُکيا اڌ ننگا ڪوجهڙا ڪارڙا\r\nاُھا چھچ ساون وڻن جي قطار\r\nھو ” جُونَ “ جو ڪَپ پکين جي پڪار\r\nاُھي پَل پکيئڙن جيان پَر ڪري\r\nالائي تہ ڪاڏي اُڏامي ويا', NULL, 'sd', '2023-10-02 21:58:47', '2023-10-02 21:58:47', NULL),
(2139, 297, 19, 'nimashaam-ghar-ghar-bbarya-jian-diya', 'نماشام گهر گهر ٻريا جيئن ڏِيا\r\nکُليا ياد جا ريشمي تاڪِيا !', NULL, 'sd', '2023-10-02 21:58:47', '2023-10-02 21:58:47', NULL),
