(2311, 323, 10, 'bbah-tte-gita-wariyam-bbah-tte-deep-bariyam', 'bbah tte gita wariyam, bbah tte deep bariyam,\r\nbbah tte phool haryam tunhinji aajiyan men.', NULL, 'en', '2023-10-04 11:45:50', '2023-10-04 11:45:50', NULL),
(2312, 325, 10, 'croran-je-akh-men-khutal-kaan-jian', 'ڪروڙن جي اک ۾ کتل ڪان جيئن،\r\nاسان جي محبت بہ نيشان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2313, 325, 10, 'array-dil-wari-totay-hi-daagh-daagh', 'اڙي دل وري توتي ھي داغ داغ،\r\nڪو اڄلهہ ٽڪيل آھي مھمان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2314, 325, 10, 'harrai-ham-safar-pandh-piyaa-dag-laga', 'ھڙئي ھم سفر پنڌ پيا دڳ لڳا،\r\nاُڏائي دڳن ڌوڙ طوفان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(2315, 325, 10, 'sharara-sharara-thi-pawando-jahaan', 'شرارا شرارا ٿي پوندو جھان،\r\nاشارا اشارا ڪنھن امڪان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2316, 325, 10, 'na-meenhyun-mewan-aen-na-mehar-suto', 'نہ مينهيون ميون ۽ نہ ميھر ستو،\r\nاجهي گهنڊ گڙيا ڪنھن گهمسان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2317, 325, 10, 'achhaai-jeeyu-hiku-laat-je-lay-asan', 'اڇائيءَ جيءَ ھڪ لاٽ جي لئہ اسان،\r\nڏنارا تارا نيڻن جا تاوان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2318, 325, 10, 'musaafir-mataan-mahal-wende-rahe', 'مسافر! متان مھل ويندي رھي،\r\nرھي پيار پنھنجو پشيمان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2319, 325, 10, 'bbah-tte-pahar-Aa-raat-chandu-je-chariyaa', 'ٻہ ٽي پھر آ رات چنڊ جي چريا،\r\nوري ڪين ملندو ھي ديھہ دان جيئن.', NULL, 'sd', '2023-10-04 11:49:48', '2023-10-04 11:49:48', NULL),
(2320, 325, 10, 'croran-je-akh-men-khutal-kaan-jian', 'croran je akh men khutal kaan jian,\r\nasan je muhabat b neeshan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2321, 325, 10, 'array-dil-wari-totay-hi-daagh-daagh', 'array dil wari totay hi daagh daagh,\r\nko ajkalh takiyal aahe mehmaan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2322, 325, 10, 'harrai-ham-safar-pandh-piyaa-dag-laga', 'harrai ham safar pandh piyaa dag laga,\r\nuddaai dagan dhoor toofaan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2323, 325, 10, 'sharara-sharara-thi-pawando-jahaan', 'sharara sharara thi pawando jahaan,\r\nishara ishara kanhin imkaan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2324, 325, 10, 'na-meenhyun-mewan-aen-na-mehar-suto', 'na meenhyun mewan aen na mehar suto,\r\najhay ghind gharya kanhin ghumsan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2325, 325, 10, 'achhaai-jeeyu-hiku-laat-je-lay-asan', 'achhaai jeeyu hiku laat je lay asan,\r\nddanara tara neenan jaa tawaan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2326, 325, 10, 'musaafir-mataan-mahal-wende-rahe', 'musaafir! mataan mahal wende rahe,\r\nrahe piyaar pahnjo pasheeman jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2327, 325, 10, 'bbah-tte-pahar-Aa-raat-chandu-je-chariyaa', 'bbah tte pahar Aa raat chandu je chariyaa,\r\nwari keen milando hi dehh daan jian.', NULL, 'en', '2023-10-04 11:50:48', '2023-10-04 11:50:48', NULL),
(2328, 327, 10, 'inhi-dar-je-daasay-array-dil-array-dil', 'انھي در جي داسي، اڙي دل! اڙي دل!\r\nتون ايڏي اڻاسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2329, 327, 10, 'ajaan-kujh-na-kujh-raat-te-ghaat-aahe', 'اڃان ڪجهه نہ ڪجهه رات تي گهات آھي،\r\nکڻي چنڊ اماسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2330, 327, 10, 'na-ko-chilwali-tunhinji-choorri-manjath-chatt', 'نہ ڪو چلولي! تنهنجي چوڙي مڃٺ چٽ،\r\nنہ ڪا سينڌ واسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2331, 327, 10, 'kadahn-ubh-jo-oh-wasndi-na-gasyu', 'ڪڏھن اڀ جو اوھ وسندي نہ گسيو،\r\nتون پيئندي پياسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2332, 327, 10, 'hia-raat-walh-men-awhan-waar-chhoriya', 'هيءَ رات ولهہ ۾ اوهان وار ڇوڙيا،\r\nوسئن ڄڻ وماسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2333, 327, 10, 'ho-aayaa-aen-waraa-niyara-kare-wiya', 'ھو آيا ۽ وارا نيارا ڪري ويا،\r\nتون اڄ تائين آسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2334, 327, 10, 'muhabat-na-thi-jarn-ta-kaa-runjh-je-unjh', 'محبت نہ ٿي ڄڻ تہ ڪا رڃ جي اڃ،\r\nتون ڪنھن جي پياسي، اڙي دل! اڙي دل!', NULL, 'sd', '2023-10-04 11:53:29', '2024-03-21 19:22:14', '2024-03-21 19:22:14'),
(2335, 327, 10, 'inhi-dar-je-daasay-array-dil-array-dil', 'inhi dar je daasay, array dil! array dil!\r\ntun aidi annasi, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2336, 327, 10, 'ajaan-kujh-na-kujh-raat-te-ghaat-aahe', 'ajaan kujh na kujh raat te ghaat aahe,\r\nkhani chandu umaasi, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2337, 327, 10, 'na-ko-chilwali-tunhinji-choorri-manjath-chatt', 'na ko chilwali! tunhinji choorri manjath chatt,\r\nna kaa seendh waasi, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2338, 327, 10, 'kadahn-ubh-jo-oh-wasndi-na-gasyu', 'kadahn ubh jo oh wasndi na gasyu,\r\ntun peeayndi piyaasi, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2339, 327, 10, 'hia-raat-walh-men-awhan-waar-chhoriya', 'hia raat walh men awhan waar chhoriya,\r\nwasann jarn wasaami, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2340, 327, 10, 'ho-aayaa-aen-waraa-niyara-kare-wiya', 'ho aayaa aen waraa niyara kare wiya,\r\ntun ajj taaen aasi, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2341, 327, 10, 'muhabat-na-thi-jarn-ta-kaa-runjh-je-unjh', 'muhabat na thi jarn ta kaa runjh je unjh,\r\ntun kanhin je piyaasi, array dil! array dil!', NULL, 'en', '2023-10-04 11:54:19', '2023-10-05 11:23:08', '2023-10-05 11:23:08'),
(2342, 329, 10, 'asan-khe-sobh-jo-yaa-haar-jo-ko-bharwaso-nahey', 'اسان کي سوڀ جو يا ھار جو ڪو ڀروسو ناھي،\r\nسدائين سوڀ لاءِ سوچي سو ماڻهو ڇا ڇسو ناھي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2343, 329, 10, 'array-zindaan-men-warryal-sarhan-phooralji-wai-Aa', 'اڙي زندان ۾ واڙيل سرنھن ڦولارجي وئي آ،\r\n۽ تنھنجي ڪنٺ ۾ ڦاٿل ڪو نغمو ئي ڦٽو ناھي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2344, 329, 10, 'kasar-nasarn-khunbhal-wakhran-je-ratt-nirali-Aa', 'ڪسر نسرن کنڀل وکرن جي رت نرالي آ،\r\nگهڙي ڪا بي وڙي ناهي ۽ ڪوئي ڏينھن ڪسو ناهي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2345, 329, 10, 'alaa!-maalhanr-je-malhaa-jean-wayoon-hin-poonji-laamun', 'الا! مالهڻ جي مالها جئين ويون ھن پوئجي لامون،\r\nتو کي واهوندي جي رت جو ڇا سچ پچ تو کي ڪو پتو ناھي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2346, 329, 10, 'mile-jo-mahal-moocharo-ta-poi-to-khe-disndasin', 'ملي جو مھل موچاري تہ پوءِ تو کي ڏسينداسين،\r\nاسان جي دل بہ گهر ڪنھن جل پريءَ جو آدڙو ناھي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2347, 329, 10, 'kanheen-tunhinji-kathaa-chori-bhulyal-saarornyun-uthlyun', 'ڪنهين تنهنجي ڪٿا چوري ڀليل ساروڻيون اٿليون،\r\nڪڏھن ڪي پور پلٽيا تنھنجو ڇڙيو ڪٿ قصو ناهي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2348, 329, 10, 'rotti-je-ranjaai-men-jalayu-seen-sajjo-jobhan', 'روٽي جي رنجائي ۾ جلايو سين سڄو جوڀن،\r\nغم جانان برابر پاڻ ۾ سو ولولو ناهي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2349, 329, 10, 'wari-thaa-kasht-kanhin-khan-kaattjn-aen-raatyun-nabran', 'وري ٿا ڪشٽ ڪنھن کان ڪاٽجن ۽ راتيون نبرن،\r\nسڌن جي رڃ ۾ ڀوريءَ دل ڀلا ڇا ڇا سٺو ناهي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2350, 329, 10, 'uhe-kora-panaa-aakhir-budhae-chhaa-budhainda', 'اھي ڪورا پنا آخر ٻڌائي ڇا ٻڌائيندا،\r\nجو ڪاري رات ۾ جن ڪجھ سٺو ناھي ڏٺو ناھي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2351, 329, 10, 'sangeenan-khan-puchho-jin-te-asan-jo-khoon-lathyal-Aa', 'سنگينن کان پڇو جن تي اسان جو خون لٿيل آ،\r\n۽ تيرن کان پڇو جن مان ڪوئي دل کان گٿو ناهي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2352, 329, 10, 'charri-dil-tunhinji-taaran-te-bhalaa-hi-kehry-thar-thar-Aa', 'چري دل! تنهنجي تارن تي، ڀلا ھي ڪھڙي ٿر ٿر آ،\r\nنئين ڪنھن سور سانڍڻ جو، اسان ۾ حوصلو ناهي.', NULL, 'sd', '2023-10-04 11:57:41', '2023-10-04 11:57:41', NULL),
(2353, 330, 10, 'aayo-keru-asan-khan-agg', 'آيو ڪير اسان کان اڳ،\r\nدڳ تي ڦيري ويو جو پڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2354, 330, 10, 'karrki-dil-te-roz-kithaan', 'ڪڙڪي دل تي روز ڪٿان،\r\nسانجهيءَ ويلي سورن سڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2355, 330, 10, 'daphorran-saan-dharti-chor', 'داڦوڙن سان ڌرتي چور،\r\nخاڪي ورديون، اوٺا وڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2356, 330, 10, 'kaaro-kanrchho-jaan-na-kuchhi', 'ڪارو ڪڻڇو جان نہ ڪڇي،\r\nهينئڙا! تارن تي ئي تڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2357, 330, 10, 'tunhinji-sir-je-saar-ta-poi', 'تنهنجي سر جي سار تہ پوءِ،\r\nرت ۾ ٻڏندي ويئي رڳ رڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2358, 330, 10, 'haai!-asan-je-raah-na-rok', 'ھاءِ! اسان جي راھ نہ روڪ،\r\nھاءِ! پرين جي چنچل چڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2359, 330, 10, 'keru-asan-je-laai-ache', 'ڪير اسان جي لاءِ اچي،\r\nتپندڙ سورج دکندڙ دڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2360, 330, 10, 'taar!-asan-je-haar-na-manj', 'تار! اسان جي ھار نہ مڃ،\r\nجک ٿو ماري جُوٺو جڳ.', NULL, 'sd', '2023-10-04 12:00:30', '2023-10-04 12:00:30', NULL),
(2361, 331, 10, 'hi-sangharan-je-mosam-sadain-je-mosam', 'ھي سنگھرن جي موسم، صدائن جي موسم،\r\nھي تعزير جي رُت، وفائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2362, 331, 10, 'uhe-e-jaam-jaa-dhuk-uhe-chagg-warkaa-', 'اھي ئي جام جا ڍڪ، اھي چڳ وراڪا ،\r\nڪجي ڇا کڙي آ قضائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2363, 331, 10, 'asan-je-e-ratt-saan-kirraho-kakoriyaan', 'اسان جي ئي رت سان ڪرِڙَهو ڪڪوريا،\r\nوري آ، وري آ، بهائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2364, 331, 10, 'je-jalndo-ta-jhoonaa-garrh-haanr-jalndo', 'جي جلندو تہ جهونا ڳڙهہ ھاڻ جلندو،\r\nآ چارڻ جي چت ۾ چِتائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2365, 331, 10, 'jaa-halndi-e-badle-jaa-khilandy-e-mattji', 'جا ھلندي ئي بدلي، جا کِلندي ئي مٽجي،\r\nسا تنهنجي اکين جي، ادائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2366, 331, 10, 'awhan-je-akhiyan-khan-wathee-daar-taaen', 'اوهان جي اکين کان وٺي دار تائين،\r\nاسان لئہ خطائن خطائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2367, 331, 10, 'hi-chuhbuk-je-karrkarr-hi-beeriyan-je-kharrkharr', 'ھي چھبڪ جي ڪڙڪڙ ھي ٻيڙين جي کڙکڙ،\r\nھي دڙڪن دٻائن پڇائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2368, 331, 10, 'alaa!-ko-na-jaarni-pireen-keru!-maanni', 'الا! ڪو نہ ڄاڻي پرين ڪير! ماڻي،\r\nگلابي گلن جي فضائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
(2369, 331, 10, 'baharan-je-mand-pahinje-ware-te-ende', 'بهارن جي مند پنھنجي واري تي ايندي،\r\nلٽين جي ھلي آءُ سزائن جي موسم.', NULL, 'sd', '2023-10-04 21:17:18', '2023-10-04 21:17:53', '2023-10-04 21:17:53'),
