(3598, 337, 3, 'gharryal-waggo', 'gharryal waggo\r\nyaa wakt bhago\r\nropoosh dohaari\'a wangur', NULL, 'en', '2023-10-30 04:32:19', '2023-10-30 04:32:19', NULL),
(3599, 337, 3, 'andher-nagaru', 'andher nagar\'u\r\nsunsaan dagar\'u\r\nmustqabal waari\'a wangur', NULL, 'en', '2023-10-30 04:32:19', '2023-10-30 04:32:19', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(3600, 337, 3, 'ae-khushboou!', 'ae khushboou!\r\nAa dharti chho\r\nkanhin khakhi\'a khaari\'a wangur', NULL, 'en', '2023-10-30 04:32:19', '2023-10-30 04:32:19', NULL),
(3601, 337, 3, 'ae-aadarsho!', 'ae aadarsho!\r\nkanhin wakt acho\r\nmun laai bahari\'a wangur!\r\nae aadarsho!\r\nae aadarsho!', NULL, 'en', '2023-10-30 04:32:19', '2023-10-30 04:32:19', NULL),
(3602, 338, 3, 'jhoomandarr-hawaun-men', 'jhoomandarr hawaun men\r\nraat jjaam shori je\r\nkanhin rabab wangur Aa;\r\numar ean udaman thi\r\njjnr pakhi paran te Aa\r\nwakt khawaab wangur Aa.', NULL, 'en', '2023-10-30 04:32:56', '2023-10-30 04:32:56', NULL),
(3603, 339, 3, 'beehaji-wai-baarish', 'beehaji wai baarish\r\nraat je saraahi\'a men\r\nchandani sharaab aahe;\r\nmaan aen muhnje be khuwabi\r\nto jiyaan hawaun men\r\nchandd\'u mahoo khawaab aahe.', NULL, 'en', '2023-10-30 04:33:25', '2023-10-30 04:33:25', NULL),
(3604, 340, 3, 'kaweelyoon-piyun-kookan', 'kaweelyoon piyun kookan\r\nmund ahh jhurr pharr je\r\nubh te lurrat aahe;\r\npiyaar peengh wangur Aa\r\njjnr Hawaa je jhootte te\r\ntunhinjo muhinjo gaatt aahe.', NULL, 'en', '2023-10-30 04:33:45', '2023-10-30 04:33:45', NULL),
(3605, 341, 3, 'dil-khe-ean-wikorryo-thee', 'dil khe ean wikorryo thee\r\njjnr ta wall chanbeelia je\r\nphelji wai aahe!\r\nketri na khushboo Aa\r\njjnr ta raat sawanr je\r\nmasat thi wai aahe!', NULL, 'en', '2023-10-30 04:33:59', '2023-10-30 04:33:59', NULL),
(3606, 342, 3, 'mun-bbah-phool-champa-jaa', 'mun bbah phool champa jaa\r\ntodday moklyaa aahin\r\ntunhinje tin badan jahirraa;\r\nphool tunhinje chhattia te\r\nwan waas men aahin\r\nmoklyal gulan jahirraa.', NULL, 'en', '2023-10-30 04:34:32', '2023-10-30 04:34:32', NULL),
(3607, 344, 3, 'piyaar-khe-paleen-thi-chho', 'piyaar khe paleen thi chho?\r\nbanwari chakori aaon\r\nchandani kharri aahe!\r\naaon, raat je raarni\r\nto jiyaan tirri aahe\r\nto jiyaan tirri aahe!', NULL, 'en', '2023-10-30 04:34:58', '2023-10-30 04:34:58', NULL),
(3608, 345, 3, 'aaon-muhan-andheri-men', 'aaon muhan – andheri men\r\ntunhinje dar te aayo haan\r\npahar piyaar jo aayo aahe;\r\nnindd\'a tunhinje akhryan maan\r\nmun chumi watti aahe,\r\nchandd\'u waairro aahe.', NULL, 'en', '2023-10-30 04:35:29', '2023-10-30 04:35:29', NULL),
(3609, 346, 3, NULL, 'sawaa gaunch leemay jaa\r\njjnr nawan umangan jean\r\nmeehan moriya aahin,\r\nmuhnje sang men sarti\r\ntuhnja ang\'u tin wangur\r\nneehan moriya aahin.', NULL, 'en', '2023-10-30 04:36:16', '2023-10-30 04:36:16', NULL),
(3610, 347, 3, 'raat-kujh-bhini-aahe', 'raat kujh bhini aahe\r\nwaau men nimee aahe\r\nmeehan kujh piyo aahe;\r\naaon ko muswar haan?\r\nchandd\'u muhnje dar te chho\r\nbeehaji wayo aahe!', NULL, 'en', '2023-10-30 04:36:41', '2023-10-30 04:36:41', NULL),
(3611, 348, 3, NULL, 'chandani wasi thi pai\r\nanab men likal koyal\r\nsanat thi wai aahe,\r\nmaan ajan suto nahyan\r\nnindd\'a to jiyaan mun khan\r\najj rusee wai aahe.', NULL, 'en', '2023-10-30 04:37:03', '2023-10-30 04:37:03', NULL),
(3612, 349, 3, 'jarn-gidamaray-jo-wanu', 'jjnr gidamaray jo wanr\'u\r\nasmaan je hethyaan\r\nchandni\'a men wehnje tho;\r\nraat bhar sutal totaa\r\nkhawaab thaa ddisan jeke\r\nhi unhan men udray tho.', NULL, 'en', '2023-10-30 04:37:35', '2023-10-30 04:37:35', NULL),
(3613, 350, 3, 'sunr-heea-perr-puraarni-wooe', 'sunr hee\'a perr puraarni, wooe,!\r\narti ot akhiyan men jahin khe، jeeyu men kei jall.', NULL, 'en', '2023-10-30 04:39:41', '2023-10-30 04:39:41', NULL),
(3614, 350, 3, 'kanhan-kumanra-bheeng-thiyaa-sabh-pahnja-bhanraa-wooe.', 'kanhan kumanra, bheeng thiyaa sabh pahnja bhanraa, wooe.!\r\nwasti sari wenr, asan jahin men wegarna, wooe,!\r\nchhati pahinje chhanri, wooe,!\r\njahin men dukhrra rahjay windaa, chharnda rahnda pal.', NULL, 'en', '2023-10-30 04:39:41', '2023-10-30 04:39:41', NULL),
(3615, 350, 3, 'ho-ranjhoa-saan-ramtyoon-raatyun-ho-saajan-jo-sang', 'ho ranjho\'a saan ramtyoon raatyun, ho saajan jo sang,\r\nheer satti wai jahin je kaaranr sukh je seej palang,:\r\naadhia raat uddanri, wooe,!\r\npohti pohti paar pireen dde, retti rang mahal.', NULL, 'en', '2023-10-30 04:39:41', '2023-10-30 04:39:41', NULL),
(3616, 350, 3, 'war-saa-jjaai-jahin-jo-ddukhrro-samjhe-piu-na-maau', 'war saa jjaai, jahin jo ddukhrro samjhe peei\'u na maau,\r\njahin je man je maam na jjaanre, bhar men beehi bhau,:\r\nsoorat herat haanrre, wooe,!\r\nmuhan te naah musaag, na tahan khe akh\'u men rekh kajal.', NULL, 'en', '2023-10-30 04:39:41', '2023-10-30 04:39:41', NULL),
(3617, 350, 3, 'roz-pukaray-tokhe-pahinje-man-mohan-jo-piyaar', 'roz pukaray tokhe pahinje man mohan jo piyaar,\r\njeewan,! totay kehrry sapnay kayo Aa kaari waar, :\r\nddindaa wadh waranri, wooe,!\r\nghaaw\'a ghaw men goonj inahi\'a je, jahin je chott ajhal.', NULL, 'en', '2023-10-30 04:39:41', '2023-10-30 04:39:41', NULL),
(3618, 350, 3, 'sanr-heea-perr-puraarni-wooe', 'sanr hee\'a perr puraarni, wooe,!\r\narti ot akhiyan men jahin khai,jee\'a men kei jall.', NULL, 'en', '2023-10-30 04:39:41', '2023-10-30 04:39:41', NULL),
(3619, 351, 3, 'ddolao-dei', 'ddolao ddei\r\njahin tarrie mun perarra\r\nmurri saa wei.', NULL, 'en', '2023-10-30 04:44:34', '2023-10-30 04:44:34', NULL),
(3620, 352, 3, 'keddo-ddanwa-ddol', 'keddo ddanwa ddol\r\nbhaw – sagar je bher men\r\nmanikh\'u janam amol!', NULL, 'en', '2023-10-30 04:44:54', '2023-10-30 04:44:54', NULL),
(3621, 353, 6, 'rugo-baah-ttimaki-rugo-bahrri', 'ruggo baah ttimaki ruggo bahrri,\r\nuhaai uhaai athas sahrri,\r\nadaa! hun adia jo atham ddukh ddaadho,\r\nkai wakt weeran aen wairri,\r\ntabaahia lai teeli b kaafi hui,\r\nlaggae awhan aen asan lairri.', NULL, 'en', '2023-10-30 04:45:18', '2023-10-30 04:45:18', NULL),
(3622, 354, 6, 'bin-men-galihyun-aaon-na-aahyan', 'bin men ggalihyun aaon na aahyan,\r\nman men golheen, aaon na aahyan,\r\nwanr\'u wanr\'u te wechariyan wetho,\r\npan men golheen, aaon na aahyan,\r\nulraan koh kuharri\'a waangy,\r\nggan men golheen، aaon na aahyan.', NULL, 'en', '2023-10-30 04:45:57', '2023-10-30 04:45:57', NULL),
(3623, 355, 6, 'chandani!-ropay-pare', 'chandani! ropay pare\r\nsamund\' kanhin jo tho puchi؟', NULL, 'en', '2023-10-30 04:48:34', '2023-10-30 04:48:34', NULL),
(3624, 355, 6, 'sahilan-te-peer-khorray-yaad-kanhin-je-Aa-kharri', 'sahilan te peer khorray, yaad kanhin je Aa kharri,\r\ndees jehri dalbari, samundd kanhin jo tho puchi?', NULL, 'en', '2023-10-30 04:48:34', '2023-10-30 04:48:34', NULL),
(3625, 355, 6, 'ninda-jehri-neer-men-har-sami-har-weer-men', 'nindd\'a jehri neer men, har sami har weer men,\r\njagndi jairi mari, samund, kanhin jo tho puchi?', NULL, 'en', '2023-10-30 04:48:34', '2023-10-30 04:48:34', NULL),
(3626, 355, 6, 'dairan-men-doar-koi-piyo-pachaai-poor-koi', 'dairan men doar koi, piyo pachaai poor koi,\r\nsaar men kanhin je sari, samundd kanhin jo tho puchi?', NULL, 'en', '2023-10-30 04:48:34', '2023-10-30 04:48:34', NULL),
(3627, 356, 6, 'phaasi-je-jholee-men', 'phaasi je jholee men,\r\nhin khe nindd\'a ache wai,', NULL, 'en', '2023-10-30 04:49:27', '2023-10-30 04:49:27', NULL),
(3628, 356, 6, 'shokh-Hawaa-je-shookaran-men', 'shokh Hawaa je shookaran men,\r\nkanhin ta mororryo ggall gichi\'a jo,\r\nkhushboo kaa ta rachi wai,', NULL, 'en', '2023-10-30 04:49:27', '2023-10-30 04:49:27', NULL),
(3629, 356, 6, 'us-samoora-ang-phitta-kayaa', 'us samoora ang\'u phitta kayaa,\r\nsij\'u qatal thi wayo sindhua te,\r\nkaari raat pachi wai.', NULL, 'en', '2023-10-30 04:49:27', '2023-10-30 04:49:27', NULL),
(3630, 356, 6, 'mosam-pahnjo-maanro-maanryo', 'mosam pahnjo maanro maanryo,\r\nhin b kaai manah paati,\r\nnagari neth nachi wai.', NULL, 'en', '2023-10-30 04:49:27', '2023-10-30 04:49:27', NULL),
(3631, 357, 6, 'roz-thi-ddaurn-wijheen', 'roz thi ddaurn wijheen,\r\nroz tho phaasi pawaan', NULL, 'en', '2023-10-30 04:50:11', '2023-10-30 04:50:11', NULL),
(3632, 357, 6, 'baanhan-tunhinje-je-dhighi', 'bbahan tunhinje je dhighi,\r\nlachakandar tarrie mathan,\r\nroz laalai lanwaan,\r\nroz tho phaasi pawaan', NULL, 'en', '2023-10-30 04:50:11', '2023-10-30 04:50:11', NULL),
(3633, 357, 6, 'roz-tunhinjo-roop-naon-Aa', 'roz tunhinjo roop naon Aa,\r\nroz mun khe thaa lagan,\r\nneehan tunhinjaa jjnr nawaan,\r\nroz tho phaasi pawaan', NULL, 'en', '2023-10-30 04:50:11', '2023-10-30 04:50:11', NULL),
(3634, 357, 6, 'samund-tho-mun-khe-sadde', 'samundd tho mun khe sadde,\r\nmaan halyu weendus hitaan,\r\nroz tho soche chawaan,\r\nroz tho phaasi pawaan', NULL, 'en', '2023-10-30 04:50:11', '2023-10-30 04:50:11', NULL),
(3635, 358, 12, 'o-rahzan-qaatil-raajj-dharni', 'o rahzan qaatil raajj dharni,\r\nchho karoonjhar khe koreen tho.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3636, 358, 12, 'hi-pathar-pathar-nahey-rray', 'hi pathar pathar nahey rray,\r\ntun jeeu asan jo jhoreen tho.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3637, 358, 12, 'kalh-karoonjhar-je-kor-huii', 'kalh karoonjhar je kor huii,\r\n kalh mor tahookan tor huii.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3638, 358, 12, 'ajj-uzzwo-uzzwo-kian-katte', 'ajj uzzwo uzzwo kian katte,\r\ntun tarazi men toreen tho.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3639, 358, 12, 'kanhin-raat-samabara-gaalh-kadhi', 'kanhin raat samabara gaalh kadhi,\r\nkian raat alora gaalh chhirri.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3640, 358, 12, 'hoo-wanu-wanu-windo-neth-barre', 'hoo wanr\'u wanr\'u windo neth barre,\r\njee haal asan jo oreen tho.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3641, 358, 12, 'ean-shaal-bhurray-tun-naas\'u-thieen', 'ean shaal bhurray tun naas\'u thieen,\r\nean shaal jhurri tun naas\'u thieen.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3642, 358, 12, 'hin-dhartia-jo-har-bheend\'u-pathar', 'hin dharti\'a jo har bheend\'u pathar,\r\njian be dardi saan bhoreen tho.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3643, 358, 12, 'ajj-to-saan-hikray-leek-kadhi', 'ajj to saan hikray leek kadhi,\r\naen tunhinje ajj tareekh kadhi.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3644, 358, 12, 'se-tuhnja-per-kattay-chadjan', 'se tuhnja per kattay chadjan,\r\nje per asan dde soreen tho.', NULL, 'en', '2023-10-30 04:51:05', '2023-10-30 04:51:05', NULL),
(3645, 359, 6, 'adee-hi-aajiyan-kehri', 'uhaai aajiyan thi hui,\r\nuhaa e alwida thi wai.\r\nadee hi aajiyan kehrry,\r\nwai jaa moklarni thi!\r\ngadyaa tunhinje jagah te saao,\r\nghulaban jaa sathaa aahin.\r\nsabhan jaa paak thiyaa seena,\r\nughara sabh mathaa aahin.\r\nkusaran je qoal te qaaim,\r\nsabhai potyun khatta aahin.\r\nsamuri waatt te wapas,\r\nwahyu Aa khoon paanri thi.\r\nmilan joon mosmoon matam,\r\nnimanian khe naseeban men,\r\ntaddo dil men wichhyal Aa,\r\nazeezan aen qareeban men.\r\ntalashayun thaa sabhai tokhe\r\nwari gaddji gareeban men.\r\nsamuro sogg samjheen thi,\r\nsamuro rog jaarni thi.\r\nadee hi aajiyan kehrry,\r\nwai jaa moklarni thi!\r\nqissa je kott kaari je.\r\nwari saaggi kahanri thi.\r\ntun e dharti jiyaan aahen.\r\nasanjo bbio watan kehro?\r\nshaheedan aen ghulaban khe,\r\nbhalaa endo kafan kehro?\r\nsadaain jaa hujy jairi,\r\nkabo tahan khe dafan kehro.\r\nhusan har dam hussaini hui.\r\nwanjnay saa sindh raarni thi.\r\nsakayal soor waaran khe,\r\nwadho Aa wakt loosati,\r\nakhiyan je karabla aakhir,\r\nsamuri raat kian kattay?\r\nqissa je kott kaari je.\r\nwari saaggi kahanri thi.\r\nache haami hussaini thi,\r\nsindhian saan karabla thi wai\r\nuhaa e aajiyan thi hui.\r\nuhaa e alwida thi wai.', NULL, 'en', '2023-10-30 04:52:08', '2023-10-30 04:52:08', NULL),
(3646, 360, 6, 'raqasa-qalandar-te-beethi-nachi', 'raqasa qalandar te beethi nachi,\r\nchawaan kian ta mujro kare thi paee\r\nnako nazia jaa namoona nawaan,\r\nnako nazia jaa ishara nawaan,\r\nnadi Aa puraarni, uhe wahakra,\r\nnako nazia jaa kinara nawaan.\r\nuhe e purana athas pechra,\r\nuhe e padmra sandas perarra,\r\nuhaa e sandas chher aahe jite,\r\nsadiyan khan jaa saaggi wajjay thi paee\r\nmagar tun qalandar wetho diseen,', NULL, 'en', '2023-10-30 04:53:32', '2023-10-30 04:53:32', NULL),
(3647, 360, 6, 'muhinje-wikh-to-daanh-khajjay-thi-paee', 'muhinje wikh to daanh khajjay thi paee,\r\nlaggay jang jedi, aman\'u otro ho,\r\nayaman khan aaon achaan tho piyo.\r\nlikki men likayam badan jo zarro\r\njabal manjh jhaati b paae ditham,\r\nandar manjh kaati wehami ditham,\r\nruggo garam chashmo b kaafi na Aa,\r\nruggo auratun men talafi na Aa,', NULL, 'en', '2023-10-30 04:53:32', '2023-10-30 04:53:32', NULL),
(3648, 360, 6, 'ghumiyas-ghat-saaraa-ghumai-ditham', 'ghumiyas ghatt saaraa, ghumai ditham,\r\nchumiyun ddai aen khud khe chumaai ditham,\r\nmuhabat jaa mazdoor waangy thaki,\r\nmuhabat jaa jherni thyee paee jhuki,\r\nmiyaan mawrndi! tokhe chhaa chhaa chawaan\r\nsandai gas nahn sidhaa aen sawan,\r\nsafa ubh kapai taan achaan tho piyo,\r\njaddahin aahe oondah wakoray wai\r\nbadan je batti te achaan tho piyo,', NULL, 'en', '2023-10-30 04:53:32', '2023-10-30 04:53:32', NULL),
(3649, 360, 6, 'wari-sij-sarri-rakhiyo-Aa-sajjo', 'wari sij sarri rakhiyo Aa sajjo,\r\ntadahn bhe tatti te achaan tho piyo.\r\nsajjai rakhan tho maan sehwanr sajjo,\r\nraqasa qalandar te beethi ddisi,\r\naaon qalandar te bitho nachan,\r\nchawe kian ta mujro kariyaan tho piyo', NULL, 'en', '2023-10-30 04:53:32', '2023-10-30 04:53:32', NULL),
(3650, 361, 6, 'dilkash-badan-sumandar-andaaz-auratun-jaa', 'dilkash badan sumandar, andaaz auratun jaa,\r\nchamkan chitta kinara deedar aen nizara,\r\npaanri mathan jism jo tarndo rahe tarnam,\r\nchapran te chahtan jo tarsi piyal tabsam,\r\nmithra madhur ta aahin je tun wajjaae jaarni,\r\nsabh saaz auratun jaa!', NULL, 'en', '2023-10-30 04:55:13', '2023-10-30 04:55:13', NULL),
(3651, 361, 6, 'hi-us-men-ugharap-kedi-haseen-aahe!', 'hi us men ugharap kedi haseen aahe!\r\nchholian je phern chhoriyan chhatti nasheen aahe\r\naryaan asmaan aen hethyaan zameen aahe\r\npal jo rahiyo na pardo, paanri\'a kayaa paee padhra\r\nrang raaz auratun jaa!', NULL, 'en', '2023-10-30 04:55:13', '2023-10-30 04:55:13', NULL),
(3652, 361, 6, 'ware-b-waasji-paee-reeti-b-rangji-paee', 'ware b waasji paee, reeti b rangji paee,\r\njekaa ache chhuhi thi, chholi saan dangji paee,\r\nhi zahar Aa zaroort, ddaadho nasho mitho Aa,\r\njahin jahin b hi dditho sooi saghe tho samjhe,\r\nhi naaz auratun jaa!', NULL, 'en', '2023-10-30 04:55:13', '2023-10-30 04:55:13', NULL),
(3653, 361, 6, 'naarel-je-wanan-khe-aahe-disarn-je-adat', 'naarel je wanran khe aahe ddisanr je adat\r\naen mard\'u khe museebat aahe chhuhanr je adat\r\nchokrian chhitian khe, aahe kuhanr je adat\r\nkanhin te na pawan karrki, se mard\'u sabh mari wiya,\r\nnaraz auratun jaa,', NULL, 'en', '2023-10-30 04:55:13', '2023-10-30 04:55:13', NULL),
(3654, 361, 6, 'hi-miyarn-je-wagan-maan-talwar-jism-nikta', 'hi miyarn je wagan maan, talwar jism nikta\r\naen wai khe wadharn laai, khanjar jaa qasam nikta\r\nnangi khizaan b nikti, aen wanr\'u thiyaa ugharaa,\r\nae be libaas mosam nanga budhha suaasun,\r\naawaz auratun jaa!', NULL, 'en', '2023-10-30 04:55:13', '2023-10-30 04:55:13', NULL),
(3655, 361, 6, 'hi-jism-khud-waggo-Aa-pahnjo-asan-khe-patal', 'hi jism khud waggo Aa, pahnjo asan khe patal,\r\nhi raaz aahe parkhyal, hee\'a raaz aahe jjatal,\r\nhi jism khud purarno aahe libaas laathal\r\npaae badan jo beethyun, saahil khe samjh aai,\r\nanjaam ishiq tunhinjo, aghaas auratun jaa.', NULL, 'en', '2023-10-30 04:55:13', '2023-10-30 04:55:13', NULL),
(3656, 362, 6, 'ajaan-taaen-akhrot-jo-wanu-ute-e', 'ajaan taaen akhrot jo wanr\'u ute e,\r\nkhayalan men aahyoon kharra paanr beeai,\r\njudayan wisalan je kailash waadi.', NULL, 'en', '2023-10-30 04:56:30', '2023-10-30 04:56:30', NULL),
(3657, 362, 6, 'ihaa-wall-ta-kaafir-haseena-je-ghar-je', 'ihaa wall ta kaafir haseena je ghar je,\r\nputhiyaan e, angoori sharaban je matkan men,\r\nmadfoon aahe.', NULL, 'en', '2023-10-30 04:56:30', '2023-10-30 04:56:30', NULL),
(3658, 362, 6, 'ihaa-dil-ta-italy-je-adhrott-aurat-je', 'ihaa dil ta italy je adhrott aurat je,\r\nseeny je poyaan b nahey salaamat.', NULL, 'en', '2023-10-30 04:56:30', '2023-10-30 04:56:30', NULL),
