(3795, 390, 22, 'zamane-buta-ruqbat-jaa-pathar-waangy-gharrya-aahin', 'zamane but\'a ruqbat jaa pathar waangy gharrya aahin,\r\nasan kanhin piyaar jaa peera khazanan men khanyaa aahin.', NULL, 'en', '2023-10-31 13:20:43', '2023-10-31 13:20:43', NULL),
(3796, 390, 22, 'wanjay-ko-khawaab-waaran-je-werrhotti-men-liko-aahe!', 'wanjnay ko khawaab waaran je werrhotti men liko aahe!\r\ntirri ahsaas terro thi andheran men bbarya aahin.', NULL, 'en', '2023-10-31 13:20:43', '2023-10-31 13:20:43', NULL),
(3797, 390, 22, 'dagaa-je-booa-rastan-te-ubhi-Aa-paggalan-waangy', 'dagaa je booa rastan te ubhi Aa paggalan waangy,\r\nhusan jaa gham hawaun men kafan paae gaddya aahin!', NULL, 'en', '2023-10-31 13:20:43', '2023-10-31 13:20:43', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(3798, 390, 22, 'hittay-har-pal-tamashan-jo-yazeedi-rooh-jaagy-tho', 'hittay har pal tamashan jo yazeedi rooh jaaggy tho,\r\nhussaini hosalan waangy wachan unjj men warrhiya aahin!', NULL, 'en', '2023-10-31 13:20:43', '2023-10-31 13:20:43', NULL),
(3799, 390, 22, 'kachaa-jobhan-udaasi-dis-rarran-thaa-maath-je-hanj-men', 'kachaa jobhan udaasi ddis, rarran thaa maath je hanj men,\r\ndinal garhiyan akhiyan te hull rakhyaa garrha akhiyaa aahin.', NULL, 'en', '2023-10-31 13:20:43', '2023-10-31 13:20:43', NULL),
(3800, 390, 22, 'mandir-men-ghindd-Aa-goonjyo-mimbar-taan-baang-Aa-ubhri', 'mandir men ghindd Aa goonjyo, mimbar taan baang Aa ubhri,\r\nasan chehra ghulaban jaa fajar wely parhyaa aahin!', NULL, 'en', '2023-10-31 13:20:43', '2023-10-31 13:20:43', NULL),
(3801, 391, 22, 'barsaat-je-rim-jhim-men-murkyo-to-wari-aahe', 'barsaat je rim jhim men murkyo to wari aahe,\r\nacharj men ache wijj kaa kachhay te kirri aahe!', NULL, 'en', '2023-10-31 13:21:21', '2023-10-31 13:21:21', NULL),
(3802, 391, 22, 'mahboob-jawani-jaa-israr-ajeeb-aahin', 'mahboob jawani jaa israr ajeeb aahin,\r\nsurmee saan dise tokhe mosam hee phiry aahe!', NULL, 'en', '2023-10-31 13:21:21', '2023-10-31 13:21:21', NULL),
(3803, 391, 22, 'obhar-maan-uthyal-sukhee-bhaaniyaan-tho-ean-janam', 'obhar maan uthyal sukhee bhaaniyaan tho ean janam,\r\nmeendi men ratal tunhinje hath je ta tirri aahe.', NULL, 'en', '2023-10-31 13:21:21', '2023-10-31 13:21:21', NULL),
(3804, 391, 22, 'zulfan-khe-chhandiyan-thee-toon-bhaunra-thaa-bhariyo-bhuljan', 'zulfan khe chhandiyan thee toon bhaunra thaa bhariyo bhuljan,\r\nchorriyan khe budhanr laai sanjhee wai hirri aahe!', NULL, 'en', '2023-10-31 13:21:21', '2023-10-31 13:21:21', NULL),
(3805, 391, 22, 'sareen-khan-chuni-tuhnji-ahwaal-puchhya-aahin', 'sareen khan chuni tuhnji ahwaal puchhya aahin,\r\nbanbhor taan weeranay kedi thee dhari aahe!', NULL, 'en', '2023-10-31 13:21:21', '2023-10-31 13:21:21', NULL),
(3806, 391, 22, 'maan-ddiyaj-je-dartay-jo-ko-ishiq-naon-aahiyan', 'maan ddiyaj je dartay jo ko ishiq naon aahiyan,\r\nae husan hali Aa tun hath te paee sirri aahe!', NULL, 'en', '2023-10-31 13:21:21', '2023-10-31 13:21:21', NULL),
(3807, 392, 22, 'kadahn-inkaar-likhrayo-kadahn-iqrar-likhrayo', 'kadahn inkaar likhrayo kadahn iqrar likhrayo,\r\nqasam sindhua kinaran jo asan khan piyaar likhrayo', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3808, 392, 22, 'nazam-nikta-niharn-maan-ghazal-ubhryaa-adaain-maan', 'nazam nikta niharn maan ghazal ubhryaa adaain maan,\r\nsitaraa shahidyoon aahin husan har baar likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3809, 392, 22, 'nasheela-peer-ghatan-te-hatheeli-lodd-hatharran-je', 'nasheela peer ghatan te hatheeli lodd hatharran je,\r\nkakoriyal kainatan je thadhi mahkar likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3810, 392, 22, 'ghatthan-je-gandh-har-kanhin-men-huaa-thanr-lafuzu-kean-qaaboo', 'ghatthan je gandh har kanhin men huaa thanr lafuz\'u kean qaaboo,\r\nkhulyaa jian zulf hin karaa ajab israr likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3811, 392, 22, 'kadahn-phalrre-lathee-kaai-kadahn-ko-tilak-ghayo-wayo', 'kadahn phalrre lathee kaai، kadahn ko tilak ghayo wayo,\r\nqatal gaahan je raste te sutal mathaar likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3812, 392, 22, 'khilanr-bhe-khof-men-weerhyal-milan-jo-ewaz-goli', 'khilanr bhe khof men weerhyal milan jo ewaz goli,\r\ntatal behas zamane je ddinal har gaar likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3813, 392, 22, 'sarran-piyaa-khawaab-gothan-jaa-suttaa-naahyoon-akhiyun-botti', 'sarran piyaa khawaab ggothan jaa suttaa naahyoon akhiyun botti,\r\nshaheedan je naen rat je naen hubbkar likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3814, 392, 22, 'miyaani-bhogg-ko-naahe-kahanri-Aa-ghulaban-je', 'miyaani bhogg ko naahe kahanri Aa ghulaban je,\r\nsindhua laai ajrkoon ujrryoon unhan je saar likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3815, 392, 22, 'asan-mitti-jaa-ke-manhun-asan-dharti-je-dil-jaanron', 'asan mitti jaa ke manhun asan dharti je dil jaanron,\r\nasan khe ashiqee ghohyo sadiyan je chhaa likhrayo.', NULL, 'en', '2023-10-31 13:22:16', '2023-10-31 13:22:16', NULL),
(3816, 393, 6, 'jeke-jeeyu-muhinje-khe-jorry-wiya', 'jeke jeeyu muhinje khe jorry wiya,\r\nse e tand tarron ttorray wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3817, 393, 6, 'suti-lok-sapnan-men-dai-peer-peer-men', 'suti lok sapnan men, ddai peer peer men,\r\nkhuwaban te b khanjar khorray wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3818, 393, 6, 'udaasian-jo-achaar-weechar-ta-chhadd', 'udaasian jo achaar weechar ta chhadd,\r\nbuddia dil khe wetar borray wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3819, 393, 6, 'aggai-raat-kaari-umaas-andhaari', 'aggai raat kaari umaas andhaari,\r\nmathan waar karaa b chhorray wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3820, 393, 6, 'dai-bbutt-bbatt-je-muhan-te-makari', 'ddai bbutt bbatt je muhan te makari,\r\nsadaa chandd\'u chahra b rorray wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3821, 393, 6, 'jeean-sangn-pahnja-na-sindhi-sunjata', 'jeean sangn pahnja na sindhi sunjata,\r\nluchhay lingn\'a dhun jaa b lorri wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3822, 393, 6, 'huaa-gad-halnda-je-har-dam-Hassan-saan', 'huaa gadd\'u halnda je har dam ”hassan“ saan,\r\nuhe b aggain jian muhan morray wiya.', NULL, 'en', '2023-10-31 13:22:56', '2023-10-31 13:22:56', NULL),
(3823, 394, 3, 'miyaan-maan-janraan', 'miyaan! maan janraan,\r\nthindo chhaa gull mahar saan\r\npan\'a chhanr pujanran.', NULL, 'en', '2023-10-31 13:25:10', '2023-10-31 13:25:10', NULL),
(3824, 395, 3, 'ean-na-marnde-koonj', 'ean na marnde koonj,\r\njeseeen poreen rooh men\r\nun\'a je poori goonj.', NULL, 'en', '2023-10-31 13:25:29', '2023-10-31 13:25:29', NULL),
(3825, 396, 3, 'rahjay-wei-pat', 'rahjay wei pat,\r\nhail piyaa jo meehnrra\r\njhuggi\'a neeray chhat.', NULL, 'en', '2023-10-31 13:26:13', '2023-10-31 13:26:13', NULL),
(3826, 397, 10, 'ghorray-ghorray-na-ddisbi-haanr-chandd-je-tiki', 'ghorray ghorray na ddisbi haanr chandd\'u je tiki,\r\nott maan jjnr leeiya tun thi paain liki.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3827, 397, 10, 'ddor-saahil-ddisi-thi-na-ae-dil-wase', 'ddor saahil ddisi thi na ae dil! wase,\r\nberryata! asan naau kedi dhiki.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3828, 397, 10, 'tho-jiyaan-to-jiyaan-kanhin-dithi-Aa-miyaan', 'tho jiyaan to jiyaan kanhin dithi Aa miyaan!\r\nshaam je jaam te murk muhnje phakki.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3829, 397, 10, 'pett-porhiyo-b-Aa-neehan-je-sheenh-jiyaan', 'pett porhiyo b Aa neehan je sheenh jiyaan,\r\nwei windre Aa dil kanhin ta dhang je dhhiki.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3830, 397, 10, 'othiya-gothiya-hi-mayaaroon-athaoon', 'othiya! gothiya! hi mayaaroon athaoon,\r\nmuhinjo dhaarro haneeo jahook muhnje jhukee.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3831, 397, 10, 'tun-pareshaan-na-theeu-gul-jahirraa-pireen', 'tun pareshaan na theeu, gul jahirraa pireen,\r\ndil jaddahin bhe chhikay, tunhinje kaaranr siki.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3832, 397, 10, 'to-wari-chahatan-je-aah-chori-kathaa', 'to wari chahatan je aah chori kathaa,\r\nto dil naa tawan! rahe khahi sagh wiki.', NULL, 'en', '2023-10-31 13:26:59', '2023-10-31 13:26:59', NULL),
(3833, 398, 10, 'wari-dard-tuhnja-b-darmaan-thi-wiya', 'wari dard tuhnja b darmaan thi wiya,\r\nwari sahaj jaa kujh ta saamaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3834, 398, 10, 'asan-je-umeedan-b-kujh-rang-laato', 'asan je umeedan b kujh rang laato,\r\nawhan jaa b mosam te ahsaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3835, 398, 10, 'jite-kandh-looyi-kuthee-kalhh-konjee', 'jite kandh\'u looyi kuthee kalhh konjee,\r\nute git\'a muhinjaa b weeran thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3836, 398, 10, 'ddisi-tahnje-chottie-men-gaindde-sando-gul', 'ddisi tahnje chottie men gaindde sando gul,\r\nsitaraa wechara pasheeman thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3837, 398, 10, 'jiyalan-jaddahin-tunhinjo-naalo-uchaaryo', 'jiyalan jaddahin tunhinjo naalo uchaaryo,\r\nta sir taan o sindhu! se be dhiyaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3838, 398, 10, 'jhuki-jot-kathay-agar-ot-kathay', 'jhuki jot kathay agar ot kathay,\r\nta saami chhahe muth  b sunsaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3839, 398, 10, 'asan-je-ke-thahya-asan-jin-khe-chahiyo', 'asan je ke thahya, asan jin khe chahiyo,\r\nuhe e pireen dil jaa mehmaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3840, 398, 10, 'hi-heeranrr-jeewan-tarryun-hi-harli-jaa-reengatt', 'hi heeranrr jeewan tarryun, hi harli jaa reengatt,\r\nbbah tte nazbo\'e bbiyaa jhulee jawaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3841, 398, 10, 'asan-ishiq-khe-dil-khan-hataainr-jo-sochiyo', 'asan ishiq khe dil khan hataainr jo sochiyo,\r\nwari zulf tahnja pareshaan thi wiya.', NULL, 'en', '2023-10-31 13:29:32', '2023-10-31 13:29:32', NULL),
(3842, 399, 10, 'asan-bin-men-jeke-b-raatyun-luttayoon', 'asan bin men jeke b raatyun luttayoon,\r\npirah mukh te jhoomak jiyaan se sajayoon.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3843, 399, 10, 'na-paati-asan-kaa-b-malhaa-na-paati', 'na paati asan kaa b malhaa na paati,\r\nchanbaili walyun har annag te lagaayun.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3844, 399, 10, 'ihaa-maath-makli!-chae-dde-chae-dde', 'ihaa maath makli! chae dde chae dde,\r\nasan to men keedyun kaharniyun likaayoon.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3845, 399, 10, 'tun-ajj-bainch-keenjhar-maan-banrjaan-tho-bairri', 'tun ajj bainch keenjhar, maan banrjaan tho bairri,\r\nwari chholian men samund\' ko samayoon.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3846, 399, 10, 'paee-chandd-tarrpyo-sitaraa-paee-machhraya', 'paee chandd\'u tarrpyo sitaraa paee machhraya,\r\nasan tuhnjyoon palkoon chapan te ghumayoon.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3847, 399, 10, 'hi-lurkan-jaa-moti-hi-aasan-jaa-khandar', 'hi lurkan jaa moti hi aasan jaa khandar,\r\nhi pahnjyun kamayoon hi pahnjyun lattayoon.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3848, 399, 10, 'aseen-joggi-neere-nagar-je-kandhi-te', 'aseen joggi neere\'a nagar je kandhi\'a te,\r\npiyaa joonrr kanhin chandd\'u chumanr laai mattayoon.', NULL, 'en', '2023-10-31 13:30:29', '2023-10-31 13:30:29', NULL),
(3849, 400, 10, 'ratt-dees-tunhinje-raah-men-phurro-phurro-thi-wahhi-wanjnay', 'ratt dees tunhinje raah men phurro phurro thi wahhi wanjnay,\r\nmuhinjo naseeb thahe wanjnay tunhinjo b qarz lahay wanjnay.', NULL, 'en', '2023-10-31 13:31:15', '2023-10-31 13:31:15', NULL),
(3850, 400, 10, 'udaas-raat-jo-maan-chandd-ghor-ghor-piyo-peeiyaan', 'udaas raat jo maan chandd\'u ghor ghor piyo peeiyaan,\r\noondahien jaa charbbtto! mataan ko deep thahe wanjnay.', NULL, 'en', '2023-10-31 13:31:15', '2023-10-31 13:31:15', NULL),
(3851, 400, 10, 'gullan-jaa-sir-kattay-chhaddiyo-wanran-jaa-thurr-satti-chhaddiyo', 'gullan jaa sir kattay chhaddiyo, wanran jaa thurr satti chhaddiyo,\r\nkali kali latarryo mataan kasar kaa rahe wanjnay.', NULL, 'en', '2023-10-31 13:31:15', '2023-10-31 13:31:15', NULL),
(3852, 400, 10, 'soohan-mitti-je-mar-na-Aa-jahin-gair-je-na-jjibh-patti', 'soohan mitti\'a je mar na Aa jahin gair je na jjibh patti,\r\nasan jo jjnr aen kandh\'u zaray ajogga wenr sahi wanjnay.', NULL, 'en', '2023-10-31 13:31:15', '2023-10-31 13:31:15', NULL),
(3853, 400, 10, 'mubarkoon-wadheendrro-kuhanr-je-rut-ache-wai', 'mubarkoon wadheendrro! kuhanr je rut ache wai,\r\nhittay piyo ko kusee wanjnay hitaan piyo ko kuhee wanjnay.', NULL, 'en', '2023-10-31 13:31:15', '2023-10-31 13:31:15', NULL),
(3854, 400, 10, 'udaai-dhoor-raah-men-ajj-haloon-ta-heean-haloon', 'udaai dhoor raah men ajj haloon ta heean haloon,\r\njo asan je shahar dde waranr je waatt e ddahi wanjnay.', NULL, 'en', '2023-10-31 13:31:15', '2023-10-31 13:31:15', NULL),
(3855, 401, 10, 'jjnr-nazara-laggay-wei-hin-ghar-je-baharan-khe', 'jjnr nazara laggay wei hin ghar je baharan khe,\r\nko naag saraapy wayo nat neehan nazaran khe.', NULL, 'en', '2023-10-31 13:38:53', '2023-10-31 13:38:53', NULL),
(3856, 401, 10, 'hin-raah-te-je-ramya-rolak-e-samjhan-thaa', 'hin raah te je ramya rolak e samjhan thaa,\r\nggari raat mathan guzri saa saaakh sitaran khe.', NULL, 'en', '2023-10-31 13:38:53', '2023-10-31 13:38:53', NULL),
(3857, 401, 10, 'nat-neere-saanjhi-jo-ajj-rooh-men-rirn-jhnrn-Aa', 'nat neere\'a saanjhi jo ajj rooh men rirn jhnrn Aa,\r\nko saang khan kian rokay konjan je qataran khe.', NULL, 'en', '2023-10-31 13:38:53', '2023-10-31 13:38:53', NULL),
