(4172, 762, 24, 'tuhnja-ruggo-tokhe-ghuran-duniyaa-uqbaa-na-thaa-ghuran', 'tuhnja ruggo tokhe ghuran, duniyaa, uqbaa na thaa ghuran\r\ntalab thiyaa toheed jaa, wanjnay werhh wahadat je warrhiya\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4173, 762, 24, 'duniyaa-uqbaa-muhinjaa-ghuran-sat-jahnam-jageer-atham', 'duniyaa, uqbaa, muhinjaa ghuran, sat jahnam jageer atham\r\nwandde wirhaai mun dinan, jian ttupan bathh men chanraa\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4174, 762, 24, 'mulk\'u-jo-maalik-tun-waddo-imtihaan-athai-muhinjo-phiddo', 'mulk\'u jo maalik tun waddo, imtihaan athai muhinjo phiddo\r\nuchhle wadho maan haddo, ache gadd\'u gadah te moonsaa charhya\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4175, 762, 24, 'tosaan-mun-voting-kai-muhinjaa-ilim-waraa-agent-huwa', 'tosaan mun voting kai, muhinjaa ilim waraa agent huwa\r\nkhanri quraan kachhan men ache, sajhar lalach te sarrya\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4176, 762, 24, 'tunhinje-muhnje-ramaz-khe-ke-ishiq-jaa-janran-aseer', 'tunhinje muhnje ramaz khe, ke ishiq jaa janran aseer\r\nuhe muhnje ware keen wariya, sir suhaai dinaaun parra\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4177, 762, 24, 'galh-samuri-Aa-sachi-koorroo-khilaaf-naahe-kache', 'galh samuri Aa sachi, koorroo, khilaaf, naahe kache\r\nkarabla men bahatar, bbiyaa sabhai haq saan larraya\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4178, 762, 24, 'haq-je-bboli-tuhnja-parhan-muhinjaa-tuhnjan-saan-thaa-sarran', 'haq je bboli tuhnja parhan, muhinjaa tuhnjan saan thaa sarran\r\ntuhnja thorira tab na murran, wanjnay sabaq solee\'a jo parhyaa\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4179, 762, 24, 'soofian-sir-saabit-kayo-soorat-khe-sajdo-kayo', 'soofian sir saabit kayo, soorat khe sajdo kayo\r\njanan chan chae jaar saan, wanjnay moj mahdi\'a je marrhaya\r\niblees chayo allah khe “aayaa todday thorra mun dde ghanraa”', NULL, 'en', '2024-03-22 02:50:52', '2024-03-22 02:50:52', NULL),
(4180, 763, 24, 'jeke-ahad-khe-abad-chawan-uhe-keen-dissan', 'jeke “ahad” khe “abad” chawan, uhe keen dissan\r\nsaddai “dost” kan dushmani, munafiq masan\r\njanan chan chawe uhe kian pasan, mushahido mahboob jo', NULL, 'en', '2024-03-22 02:53:57', '2024-03-22 02:53:57', NULL),
(4181, 764, 3, 'chau-chau-cheehi-khe', 'chau, chau cheehi khe,\r\nmitti\'a hanra perarra\r\ntanigr te na rakhe!', NULL, 'en', '2024-03-22 02:54:22', '2024-03-22 02:54:22', NULL),
(4182, 765, 3, 'jamrrau-jo-tarr', 'jamrrau jo tarr\'u\r\nrayoon paanri peeayndi\r\nbhar te ghaato barr', NULL, 'en', '2024-03-22 02:54:45', '2024-03-22 02:54:45', NULL),
(4183, 766, 3, 'dde-dde-dde-dde-gaarhi-git', 'dde dde, dde dde gaarhi git\'a,\r\npal pal ahh pyaalo oe!\r\npeei\'u peei\'u، peei\'u peei\'u، kehrry poi?', NULL, 'en', '2024-03-22 02:55:43', '2024-03-22 02:55:43', NULL),
(4184, 766, 3, 'andhyari-te-madh-je-jeet', 'andhyari te madh je jeet,\r\naadhia raat aujaalo oe\r\nbhar bhar, bhar bhar, kehrry poi?', NULL, 'en', '2024-03-22 02:55:43', '2024-03-22 02:55:43', NULL),
(4185, 766, 3, 'chhan-chhan-chhan-chhan-gul-gul-git', 'chhan chhan, chhan chhan, gul gul git\'a,\r\nmand kayo matwalo oe\r\nchhan chhan, chhan chhan, kehrry poi', NULL, 'en', '2024-03-22 02:55:43', '2024-03-22 02:55:43', NULL),
(4186, 766, 3, 'jindari-jehro-muhinjaa-mayat', 'jindari\'a jehro muhinjaa mayat\r\nnaah kob shawalo poi,\r\njeeu jeeu, jeeu jeeu, kehrry poi\r\npeei\'u peei\'u, peei\'u peei\'u, kehrry poi?', NULL, 'en', '2024-03-22 02:55:43', '2024-03-22 02:55:43', NULL),
(4187, 767, 35, 'kon-bhaanyoon-bbio-ker-sabh-soorat-men-saaggiyo-aahiyan', 'kon bhaanyoon bbio ker\'u، sabh soorat men saaggiyo aahiyan\r\nparnaan wanjnay parhaaon، allah khe warrolarn\r\nkhil jehri galh aahe، jian chhokar kulhay aen golhanr', NULL, 'en', '2024-03-22 02:56:06', '2024-03-22 02:56:50', '2024-03-22 02:56:50'),
(4188, 767, 35, 'kon-bhaanyoon-bbio-ker-sabh-soorat-men-saaggiyo-aahiyan', 'kon bhaanyoon bbio ker\'u, sabh soorat men saaggiyo aahiyan\r\nparnaan wanjnay parhaaon, allah khe warrolarn\r\nkhil jehri galh aahe, jian chhokar kulhay aen golhanr', NULL, 'en', '2024-03-22 02:56:50', '2024-03-22 02:56:50', NULL),
(4189, 768, 35, 'mas-mas-naseeb-thiyo-Aa-darshan-to-dilruba-jo', 'mas mas naseeb thiyo Aa darshan to dilruba jo,\r\nbehad aahiyan barbar mamnoon ajj mola jo', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL),
(4190, 768, 35, 'soorat-dithhi-mun-tuhnji-ddukh-soor-sabh-lahay-wiya', 'soorat dithhi mun tuhnji, ddukh soor sabh lahay wiya,\r\nhanre zaroor kehrro darmaan bee dawa jo', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL);
INSERT INTO `poetry_couplets` (`id`, `poetry_id`, `poet_id`, `couplet_slug`, `couplet_text`, `couplet_tags`, `lang`, `created_at`, `updated_at`, `deleted_at`) VALUES
(4191, 768, 35, 'marnhan-pakan-budhaai-tareef-moonkhe-tuhnji', 'marnhan pakan budhaai tareef moonkhe tuhnji,\r\nhoohal warnan tirran men, lalanr tuhnji laqau jo,', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL),
(4192, 768, 35, 'ahrro-warneen-waieen--dil-khe-jo-hoondien-tun-yaad-hardam', 'ahrro warneen waieen dil khe jo hoondien tun yaad hardam,\r\naashiq wadhui banaai pahnje ajab adaa jo,', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL),
(4193, 768, 35, 'taaseer-bhanyaan-hi-yaa-tabeer-bhaaniyaan-hi', 'taaseer bhanyaan hi yaa tabeer bhaaniyaan hi,\r\nkahan mard haq raseed darwesh je duaa jo', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL),
(4194, 768, 35, 'muhnji-mushahde-aen-muhnji-fikar-mutabiq', 'muhnji mushahde aen muhnji fikar mutabiq\r\naahen husan men suhnra mazhar ta tun khudaa jo', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL),
(4195, 768, 35, 'aijaz-saan-milyeen-tun-maape-natho-khushia-men', 'aijaz saan milyeen tun maape natho khushi\'a men\r\naajj\'u Aa buland sitaaro hin khaak paa gadaa jo.', NULL, 'en', '2024-03-22 02:58:57', '2024-03-22 02:58:57', NULL),
(4196, 769, 35, 'daaroon-ahh-daaroon-sadaa-dard-mundan-je', 'daaroon ahh daaroon, sadaa dard mundan je\r\nwanjnay watt kalal khan, kare arz aaroon\r\ncafe qataaroon, jin peetoon se parsan thaa', NULL, 'en', '2024-03-22 02:59:28', '2024-03-22 02:59:28', NULL),
(4197, 770, 35, 'neehan-sandai-nasoor-je-bbi-kaa-dawa-naah', 'daaroon daaroon ahh, sadaa dard mundan jo\r\nneehan sandai nasoor je, bbi kaa dawa naah\r\nsanbhai sir aijaz chae, hatt kalalki kaah\r\naashiq ahre aghah, kian kandaa wiya ketraa', NULL, 'en', '2024-03-22 03:00:04', '2024-03-22 03:00:04', NULL),
(4198, 771, 35, 'subah-shaam-sharaab-pee-ta-pakeezo-raheen', 'subah shaam sharaab, pee ta pakeezo raheen\r\npatti paarr pleeti\'a je, kandai gul gulab\r\najab ahh aijaz chae, iho aab naab\r\nkharabn laggay kharab, ashiqan amarat thiyo.', NULL, 'en', '2024-03-22 03:00:33', '2024-03-22 03:00:33', NULL),
(4199, 772, 35, 'saaqi-dde-sharaab-so-jeko-hujy-hasti-ttor', 'saaqi dde sharaab so, jeko hujy hasti ttor\r\npian saan juzwi aqul khe, wijhe nak men norr\r\ndde ko teeli borr, jeko nashaa chaarhi neehan jaa', NULL, 'en', '2024-03-22 03:00:58', '2024-03-22 03:00:58', NULL),
(4200, 773, 8, 'amarr-jaddahin-loli-ddende-Aa', 'amarr jaddahin loli ddende Aa\r\nnindr pare aaddri ende Aa\r\nbhakur manjhar bhare hoa mun khe\r\nddaadho ddor wathee wende Aa', NULL, 'en', '2024-03-22 03:01:48', '2024-03-22 03:01:48', NULL),
(4201, 773, 8, 'jaa-thi-gull-e-gull-honda-hin', 'jaa thi gull e gull honda hin\r\nhubbkaran jaa hull honda hin\r\nsuhnra suhnra, piyaraa sapna\r\nhonda aahin baag\'u, baagheecha\r\npeengh te maan jholeendo aahiyan\r\nlikndo aahiyan, chhupndo aahiyan\r\nnachndo, ttanpndo, kudndo aahiyan\r\npopat ke pakrreendo aahiyan\r\nkeddo poi khush thindo aahiyan\r\nmaan e maan bas hoondo aahiyan', NULL, 'en', '2024-03-22 03:01:48', '2024-03-22 03:01:48', NULL),
(4202, 773, 8, 'jaa-thi-ko-iskool-na-aahe', 'jaa thi ko iskool na aahe\r\nko ustaad, ko roll na aahe\r\nkanhin saan bhe ko jhugarro naahe\r\nrandeekan te raggarro naahe', NULL, 'en', '2024-03-22 03:01:48', '2024-03-22 03:01:48', NULL),
(4203, 773, 8, 'thadhri-heer-paee-ghalndi-Aa', 'thadhri heer paee ghalndi Aa\r\nmuhnje tadaheen akh\'u khilndi Aa\r\nnindd\'a pare udrri wende Aa\r\ngalh sajhee wisri wende Aa.', NULL, 'en', '2024-03-22 03:01:48', '2024-03-22 03:01:48', NULL),
(4204, 774, 8, 'lahrne-to-khe-laal-Aa-sindhua-sandai-saar', 'lahrne to khe laal Aa, sindhua sandai saar\r\ngulan jahirraa baaru, sagho wadhi thi waddo.', NULL, 'en', '2024-03-22 03:02:54', '2024-03-22 03:02:54', NULL),
(4205, 774, 8, 'mahaky-tunhinjo-mukh-makhryun-jian-maak-men', 'mahaky tunhinjo mukh, makhryun jian maak men,\r\nmilai mithra! keenaki, kithaan koi ddukh,\r\nsadaa marneen sukh, sindhua jaa seengar tun.', NULL, 'en', '2024-03-22 03:02:54', '2024-03-22 03:02:54', NULL),
(4206, 774, 8, 'diseen-mithaa-ddeeh-men-henna-jeean-haal', 'diseen mithaa!, ddeeh men, henna jeean haal,\r\njugg jugg jian shaal, sandhej tin khe saah men', NULL, 'en', '2024-03-22 03:02:54', '2024-03-22 03:02:54', NULL),
(4207, 774, 8, 'bhare-watho-jhol-gullan-jehryun-galhriyoon', 'bhare watho jhol, gullan jehryun galhriyoon,\r\nsit sit men sik Aa, sach samayal dhhol!,\r\nbarnarnaa hi boll, heean manjh handairn jaa.', NULL, 'en', '2024-03-22 03:02:54', '2024-03-22 03:02:54', NULL),
(4208, 774, 8, 'kaawar-keeno-je-kadhiyee-chhadiyee-lalach-lobh', 'kaawar keeno je kadhiyee, chhadiyee lalach lobh\r\nsaathi!, to khe sobh, milndi har kanhin maagg\'a te.', NULL, 'en', '2024-03-22 03:02:54', '2024-03-22 03:02:54', NULL),
(4209, 775, 24, 'chho-tho-paanr-lakaain-chhadd-aashiq-saan-attkal', 'chho tho paanr lakaain, chhadd aashiq saan attkal,\r\nathai talban je tokal', NULL, 'en', '2024-03-22 03:03:51', '2024-03-22 03:03:51', NULL),
(4210, 775, 24, 'andar-wetho-tun-aahen-kian-chawaan-ta-naheen', 'andar wetho tun aahen, kian chawaan ta naheen\r\ndam dam hukum halain, waah wanryaa tuhnja wall chhal', NULL, 'en', '2024-03-22 03:03:51', '2024-03-22 03:03:51', NULL),
(4211, 775, 24, 'ashiqan-andar-men-saabit-kayierro-sar-men', 'ashiqan andar men saabit kayierro sar men,\r\nzahir zer na zabar men, thieen raaz saanr ramal,', NULL, 'en', '2024-03-22 03:03:51', '2024-03-22 03:03:51', NULL),
(4212, 775, 24, 'kal-te-aahen-qadar-zalamn-te-zaabr', 'kal te aahen qadar, zalamn te zaabr,\r\nzaarbn te saabr, jahat putt dhakeen tho jhal jhal,', NULL, 'en', '2024-03-22 03:03:51', '2024-03-22 03:03:51', NULL),
(4213, 775, 24, 'ache-kei-izhaari-beerang-baikh-bahari', 'ache kei izhaari، beerang baikh bahari,\r\n“mehdi” je mahndari, bhagaa bhw mirhai bhal bhal', NULL, 'en', '2024-03-22 03:03:51', '2024-03-22 03:03:51', NULL),
(4214, 775, 24, 'chho-tho-paanr-lakaain-chhadd-aashiq-saan-attkal', 'ڇو ٿو پاڻ لڪائين، ڇڏ عاشق سان اٽڪل،\r\nاٿئي طالبن جي توڪل', NULL, 'sd', '2024-03-22 03:04:04', '2024-03-22 03:04:04', NULL),
(4215, 775, 24, 'andar-wetho-tun-aahen-kian-chawaan-ta-naheen', 'اندر ويٺو تون آھين، ڪيئن چوان تہ ناهين\r\nدم دم حڪم هلائين، واھ وڻيا تنهنجا وَل ڇَل', NULL, 'sd', '2024-03-22 03:04:04', '2024-03-22 03:04:04', NULL),
(4216, 775, 24, 'ashiqan-andar-men-saabit-kayierro-sar-men', 'عاشقن اندر ۾ ثابت ڪَيڙوءُ سرَ ۾،\r\nظاھر زير نہ زَبرَ ۾، ٿئين رَاض ساڻ رَلمل،', NULL, 'sd', '2024-03-22 03:04:04', '2024-03-22 03:04:04', NULL),
(4217, 775, 24, 'kal-te-aahen-qadar-zalamn-te-zaabr', 'ڪَلَ تي آھين قادر، ظالمن تي ضابرَ،\r\nصابرن تي صابر، جهٽ پٽ ڍڪين ٿو جهل جهل،', NULL, 'sd', '2024-03-22 03:04:04', '2024-03-22 03:04:04', NULL),
(4218, 775, 24, 'ache-kei-izhaari-beerang-baikh-bahari', 'اچي ڪيئي اظهاري، بيرنگ بيک بَهاري،\r\n“مهدي” جي مهنداري، ڀڳا ڀوَ مڙئي ڀَل ڀَل', NULL, 'sd', '2024-03-22 03:04:04', '2024-03-22 03:04:04', NULL),
(4219, 776, 24, 'hoondy-quraan-kulalan-khe-milyo-keen-allah', 'hoondy quraan kulalan khe milyo keen allah,\r\nroz ddisan roz parhan، kayaaon na oodaahn kaah\r\n“fadam, dam alehum rabham” iho rirhi wartaoon na raah\r\namar manjyaaun na aage jo athan nafas saanr nikkah\r\njanan “chan” chawe pasah, parrayaun keen pireen saan', NULL, 'en', '2024-03-22 03:04:40', '2024-03-22 03:04:40', NULL),
(4220, 777, 24, 'ya-khuda-tun-ishiq-pahinje-men-munkhe-bharpur-kar', 'ya khuda tun ishiq pahinje men munkhe bharpur kar\r\nkhayaal thi hik\'u haal saan roshan qalab khe noor kar\r\nmun hujaan majnoon misl totay khudaa tahan bando,\r\narz muhinjo hi buddi dargaah men manzoor kar\r\nunhee bishat dde mun na neeyan jaathe na tunhinjo kar zaroor\r\ndozakh bhalo mun kaan iho jaate sabhai dukh doar kar\r\nraham je monte nazara sadqai pahinje huzoor kar\r\nchan janan saan ahro sarkaar ko dastoor kar', NULL, 'en', '2024-03-22 03:05:01', '2024-03-22 03:05:01', NULL),
(4221, 778, 24, 'hikrra-sachaa-soofi-bbiyaa-phoosi-tteeyan-aahin-ttooshi', 'hikrra sachaa soofi bbiyaa phoosi tteeyan aahin ttooshi\r\naaon unhan manjh aahiyan bhangiyanri boonsi\r\njanan chan pahinje jeeyu men wanjnay thiyo Aa jasoosi\r\nkhooe jekaa khaasee, saa munkhe manjhaan maloom thi', NULL, 'en', '2024-03-22 03:07:36', '2024-03-22 03:07:36', NULL),
(4222, 779, 24, 'saddai-soofi-mazhab-rakhe-man-men-uho-kazab-Aa-koofi', 'saddai “soofi” mazhab rakhe man men uho kazab Aa koofi\r\nchamche baazi chit men luggarr Aa loosi\r\njanan chan chawe lamazhab soofi, croran men ko labhe', NULL, 'en', '2024-03-22 03:08:18', '2024-03-22 03:08:18', NULL),
(4223, 780, 24, 'sufi-kothhain-ta-safa-thi-na-ta-miyaan-kar-tun-muaaf', '“sufi” kothhain ta safa thi na ta miyaan! kar tun muaaf\r\nsoofian sahan safa kayaa sameeyan saafa saaf\r\ntun rakhiyo gand ghar men lambi hannri tho laaf\r\ndooi je dil men paleen tho pooraf\r\n“lanhet allah ali alkazbeen” iho koorran te Aa qaaf\r\njanan chan na tho chawe, chayo molaa manjh maskhf\r\nhar ko jjaanre aap, tun kakh na rakhjaan kund men', NULL, 'en', '2024-03-22 03:09:11', '2024-03-22 03:09:11', NULL),
(4224, 781, 24, 'sufi-aahe-sach-lakh-men-labhe-kon-croran-ko', '“soofi” aahe “sach” lakh men labhe kon croran ko\r\nmootoo “qabal anat mootu” joggi jo thiyo\r\njanan chan chawe sacho soofi so, marna aggai jo muau', NULL, 'en', '2024-03-22 03:09:44', '2024-03-22 03:09:44', NULL),
(4225, 782, 26, 'oonhi-men-aasanr-kare-wanjnay-maagg-ladhaaun-morr', 'oonhi men aasanr kare, wanjnay maagg\'a ladhaaun morr,\r\ntin te jaa atthai pahar ukandd men، thaa kan tanwaroon toor,\r\nut ”rohail“ raat na ddeenhan ko, nako sees na soor,\r\nwanjnay noori gadyaa noor, langhi piyaa lahoot khan.', NULL, 'en', '2024-03-22 03:10:21', '2024-03-22 03:10:21', NULL),
(4226, 783, 26, 'naai-kandh-kamaah-khan-jin-nihaaryu-nasoot', 'naai kandh\'u kamaah khan, jin nihaaryu nasoot,\r\nratyaan deehaan, ”rohal“ chawe, uhe marn thaa malkoot,\r\npeer sunjaanry pir jo, jata men jabroot,\r\njoggi jafaaun kare, langhi piyaa lahoot,\r\nse adesi adhoot, wanjnay nanga gadyaa naath khe.', NULL, 'en', '2024-03-22 03:11:05', '2024-03-22 03:11:05', NULL),
(4227, 784, 26, 'nanga-gaddji-naath-saan-wari-bbio-pahh-piyoon', 'nanga gaddji naath saan, wari bbio pahh piyoon,\r\nsaa garnyayun galharri, jo gur das\'u dinoon,\r\nlaa ”hijaab beeni o beenuk“ , wichoon sabh wiyoon,\r\ntehoon poi piyoon, wanjnay piyoond paich pireen saan.', NULL, 'en', '2024-03-22 03:11:44', '2024-03-22 03:11:44', NULL),
(4228, 785, 26, 'ko-pahh-piyo-jooggian-tin-khanyu-kharraun', 'ko pahh piyo jooggian, tin khanyu kharraun,\r\njaatra jogg jhaar je, mor na manjyaaun,\r\nsaa bhaun sojhayaun, jahin men jogg na bhogg ko.', NULL, 'en', '2024-03-22 03:12:12', '2024-03-22 03:12:12', NULL),
(4229, 786, 26, 'ko-pahh-piyo-jooggian-puryaan-tinheen-paar', 'ko pahh piyo jooggian, puryaan tinheen paar,\r\njatt ganga na gomti, nako jogg jhaar,\r\nna ko zikar na fikar ko, na ko sadd tanwaar,\r\nna kaa raat na ddeenhan ko, na kaa jeet na haar,\r\nlaggo rang ”rohal“ chawe, ut adeesian upaar,\r\njahin jo aar na paar, wanjnay tit kayaaon takyo.', NULL, 'en', '2024-03-22 03:13:00', '2024-03-22 03:13:00', NULL),
(4230, 786, 26, 'ko-pahh-piyo-jooggian-puryaan-tinheen-paar', 'ڪو پھہ پيو جوڳئين، پُريا تنھين پارِ،\r\nجت گنگا نہ گومتي، نڪو جوڳ جهار،\r\nنہ ڪو ذڪر نہ فڪر ڪو، نہ ڪو سڏ تنوار،\r\nنہ ڪا رات نہ ڏينھن ڪو، نہ ڪا جيتِ نہ ھار،\r\nلڳو رنگ ”روحل“ چوي، اُت آديسين اَپار،\r\nجنھن جو آرُ نہ پارُ، وڃي تِت ڪيائون تَڪيو.', NULL, 'sd', '2024-03-22 03:13:25', '2024-03-22 03:13:25', NULL),
(4231, 787, 26, 'bireh-aen-beeragg-je-jin-jarr-laggay-manjh-jeeyu', 'bireh aen beeragg je, jin jarr laggay manjh jeeyu,\r\nsojhee sach sareer moon, tin preman paato peea,\r\nmaranr\'u jeyarn jo man moon, tin sameeyan latho seeu,\r\nnaath nimanra thi, wanjnay ”rohal“ rataa raam seen.', NULL, 'en', '2024-03-22 03:13:58', '2024-03-22 03:13:58', NULL),
(4232, 788, 26, 'je-rang-rachi-raam-je-lahooti-thiyaa-laal', 'je rang rachi raam je, lahooti thiyaa laal,\r\nsir dinaaun satt men, kayaa, mayaa, maal,\r\nwiya tin khan wisri, khotta sabh khayaal,\r\nratyaan deehaan, ”rohal“ chawe, waali\'a saanr wisaal,\r\nse nanga sadaa nehaal, jin darsan paato dos jo.', NULL, 'en', '2024-03-22 03:14:40', '2024-03-22 03:14:40', NULL),
(4233, 789, 26, 'aashiq-ilhaai-ubhaa-je-qissa-keen-kareen', 'aashiq ilhaai ubhaa, je qissa keen kareen,\r\nnaai kandh\'u qaloob taan, nimaazon neetyan,\r\nddyan tawaafoon tin khe, mako e manjheen,\r\n”rohal“ rooh sandiyen, har dam hoondo hajj men.', NULL, 'en', '2024-03-22 03:15:21', '2024-03-22 03:15:21', NULL),
(4234, 790, 36, 'lurrak-hikro-laarreo-ta-lorrheendo', 'lurrak hikro laarreo ta lorrheendo,\r\nddukh charyu Aa, jahaan borrendo!', NULL, 'en', '2024-03-22 03:16:20', '2024-03-22 03:16:20', NULL),
