(4303, 876, 34, 'asan-je-muaasin-safar-men-o-saathi', 'asan je muaasin safar men o saathi!\r\nta marreenda manzil pahnja dhhol dhhatti.', NULL, 'en', '2024-03-22 03:48:05', '2024-03-22 03:48:05', NULL),
(4304, 876, 34, 'asan-jo-jaddheen-khoon-khushboo-thi-endo', 'asan jo jaddheen khoon khushboo thi endo,\r\nta werrha watan jaa b waasi chadeendo,\r\njaddheen bararre kanhin, kai bboli bbaati,\r\nta marreenda manzil pahnja dhhol dhhatti.', NULL, 'en', '2024-03-22 03:48:05', '2024-03-22 03:48:05', NULL),
(4305, 876, 34, 'asaanju-hi-banho-asan-jo-nighaaun', 'asaanju hi banho, asan jo nighaaun,\r\nazal khan locheen thiyun ehryoon raahun,\r\ninahi\'a kartt men je kusi wiyasin kaati,\r\nta marreenda manzil pahnja dhhol dhhatti.', NULL, 'en', '2024-03-22 03:48:05', '2024-03-22 03:48:05', NULL),
(4306, 876, 34, 'jaddahin-munhinjaa-jhaangi-na-jhoori-men-jhurnda', 'jaddahin munhinjaa jhaangi, na jhoori\'a men jhurnda,\r\nbbari men na bakrar jaa baaru bbarnda,\r\nsuki sakhnri thindi na jeejal je chhatti,\r\nta marreenda manzil pahnja dhhol dhhatti.', NULL, 'en', '2024-03-22 03:48:05', '2024-03-22 03:48:05', NULL),
(4307, 876, 34, 'jaddheen-wadd-phurro-ko-walahran-te-wasndo', 'jaddheen wadd phurro ko walahran te wasndo,\r\nmathan parkar je panoharan te wasndo,\r\nna rahndi kaa maarai, maran laai aati,\r\nta marreenda manzil pahnja dhhol dhhatti.', NULL, 'en', '2024-03-22 03:48:05', '2024-03-22 03:48:05', NULL),
(4308, 878, 34, 'jaddahin-b', 'جڏھن بہ،\r\nتوکي وسارڻ چاھيم\r\nتنھنجي ھر ياد،\r\nسُڪل ٻير جي ٽاريءَ وانگر،\r\nذھن ۽ روح جي ريشم سان وچڙي پيئي آ،\r\nمنھنجي دل،\r\nمون سان وڙهي پيئي آ.', NULL, 'sd', '2024-03-22 03:51:03', '2024-03-22 03:51:03', NULL),
(4309, 878, 34, 'jaddahin-b', 'جڏھن بہ،\r\nتوکي وسارڻ چاھيم\r\nتنھنجي ھر ياد،\r\nڪنھن ڏاج جي جوڙي ۾ رچيل،\r\nسدا ملوڪ، شوخ، شرميلي،\r\nخمار گاڏھين خوشبوءِ وانگر،\r\nچؤطرف من ۾ ٽڙي پيئي آ،\r\nمنھنجي دل،\r\nمونسان وڙهي پيئي آ.', NULL, 'sd', '2024-03-22 03:51:03', '2024-03-22 03:51:03', NULL),
