(4687, 952, 42, 'منشي-hitrre-mot-je-muhn-men', '”منشي“ ھتڙي، موت جي مُنھن ۾،\r\nھر ھڪ ھاري ناري آ،\r\nڪيڏي رات انڌاري آ.', NULL, 'sd', '2024-03-24 10:21:18', '2024-03-24 10:21:18', NULL),
(4688, 952, 42, 'kedi-raat-andhaari-Aa', 'kedi raat andhaari Aa،\r\nkedi raat andhaari Aa.', NULL, 'en', '2024-03-24 10:21:40', '2024-03-24 10:21:40', NULL),
(4689, 952, 42, 'barrchhi-bum-bandooq-saan-bitho', 'barrchhi bum bandooq saan bitho،\r\ntarr tarr te tatari Aa،\r\nkedi raat andhaari Aa.', NULL, 'en', '2024-03-24 10:21:40', '2024-03-24 10:21:40', NULL),
(4690, 952, 42, 'sip-wayo-soonghe-saath-sajjo-jjrn', 'sip wayo soonghe saath sajjo jjrn،\r\nbhooan te bhoo\'a bhaari Aa،\r\nkedi raat andhaari Aa.', NULL, 'en', '2024-03-24 10:21:40', '2024-03-24 10:21:40', NULL),
(4691, 952, 42, 'to-lay-mun-lay-dhart-ti-hi', 'to lay mun lay dhart\'ti hi،\r\ndharti saari andhaari Aa،\r\nkedi raat andhaari Aa.', NULL, 'en', '2024-03-24 10:21:40', '2024-03-24 10:21:40', NULL),
(4692, 952, 42, 'sindh-sindh-sindh-jo-kian-na-sarry-piyo', 'sindh sindh، sindh jo kian na sarry piyo،\r\nbbaranr ddei kanhin bbarri Aa،\r\nkedi raat andhaari Aa.', NULL, 'en', '2024-03-24 10:21:40', '2024-03-24 10:21:40', NULL),
(4693, 952, 42, 'منشي-hitrre-mot-je-muhn-men', '”munshi“ hitrre، mot je muhn men،\r\nhar hik\'u haari naari Aa،\r\nkedi raat andhaari Aa.', NULL, 'en', '2024-03-24 10:21:40', '2024-03-24 10:21:40', NULL),
(4694, 954, 42, 'dilaaso-chandd\'u-jo-ddei', 'دلاسو چنڊ جو ڏيئي، اسان کان ويا ڦُري تارا،\r\nاسان کي انب ڏيکاري، ڏنائون ٽوھ زھر کارا،\r\nاسان ھر وقت توپن جي، رھيا آھيون نشاني ۾،\r\nڪڏھن ڪنھن بھاني ۾، ڪڏھن ڪھڙي بھاني ۾.', NULL, 'sd', '2024-03-24 10:28:21', '2024-03-24 10:30:30', '2024-03-24 10:30:30'),
(4695, 954, 42, 'dilaaso-chanddu-jo-ddei', 'dilaaso chandd\'u jo ddei، asan khan wiya phure tara،\r\nasan khe anab dekharay، dinaaun ttoh zahar khara،\r\nasan har wakt topan je، rahiyaa aahyoon nishaane men،\r\nkadahn kanhin bahane men، kadahn kehrry bahane men.', NULL, 'en', '2024-03-24 10:30:12', '2024-03-24 10:32:01', '2024-03-24 10:32:01'),
(4696, 954, 42, 'dilaaso-chanddu-jo-ddei', 'دلاسو چنڊ جو ڏيئي، اسان کان ويا ڦُري تارا،\r\nاسان کي انب ڏيکاري، ڏنائون ٽوھ زھر کارا،\r\nاسان ھر وقت توپن جي، رھيا آھيون نشاني ۾،\r\nڪڏھن ڪنھن بھاني ۾، ڪڏھن ڪھڙي بھاني ۾.', NULL, 'sd', '2024-03-24 10:30:30', '2024-03-24 10:30:30', NULL),
(4697, 954, 42, 'dilaaso-chanddu-jo-ddei', 'dilaaso chandd\'u jo ddei، asan khan wiya phure tara،\r\nasan khe anab dekharay، dinaaun ttoh zahar khara،\r\nasan har wakt topan je، rahiyaa aahyoon nishaane men،\r\nkadahn kanhin bahane men، kadahn kehrry bahane men.', NULL, 'en', '2024-03-24 10:32:01', '2024-03-24 10:32:01', NULL),
(4698, 0, 42, 'ddisi-weah-khan-dakanri-wathee-jin-wei', 'ڏسي وِھ کان ڏڪڻي، وٺي جن ويئي،\r\nسَڌون ڪن سيئي، اچيو ڪيئن شراب جون.', '[\"sharaab\",\"mokhi\"]', 'sd', '2024-03-24 10:39:26', '2024-03-24 10:39:26', NULL),
(4699, 0, 42, 'ddisi-weah-khan-dakanri-wathee-jin-wei', 'ddisi we\'ah khan dakanri، wathee jin wei،\r\nsadhoon kan se\'ee، achyo kian sharaab joon.', '[\"sharaab\",\"mokhi\"]', 'en', '2024-03-24 10:39:26', '2024-03-24 10:39:26', NULL),
(4700, 0, 42, 'bhalaa-taneen-bhaag-jeke-kandhan-kachh-men', 'ڀلا تنين ڀاڳ، جيڪي ڪانڌن ڪڇ ۾،\r\nسَرتيون مون سھاڳ، ڪيو سڱ سنڌوءَ سان.', '[\"sindh\"]', 'sd', '2024-03-24 10:44:55', '2024-03-24 10:44:55', NULL),
(4701, 0, 42, 'bhalaa-taneen-bhaag-jeke-kandhan-kachh-men', 'bhalaa taneen bhaag، jeke kandhan kachh men،\r\nsartyoon mun suhaag، kayo sang sindhua saan.', '[\"sindh\"]', 'en', '2024-03-24 10:44:55', '2024-03-24 10:44:55', NULL),
(4702, 0, 42, 'poochhyam-paae-paandh-kanhin-lay-kaatten-qed-tho', 'پُڇيم پائي پانڌ، ڪنھن لئہ ڪَٽين قيد ٿو؟\r\nڪنڌ ورائي ڪانڌ، ڪئي ڳالھه ڪُڙم جي.', '[\"qaid\"]', 'sd', '2024-03-24 10:49:18', '2024-03-24 10:49:18', NULL),
(4703, 0, 42, 'poochhyam-paae-paandh-kanhin-lay-kaatten-qed-tho', 'poochhyam paae paandh، kanhin lay kaatt\'en qed tho?\r\nkandh\'u waraai kandh، kai gaalah kurram je.', '[\"qaid\"]', 'en', '2024-03-24 10:49:18', '2024-03-24 10:49:18', NULL),
(4704, 956, 27, 'aayo-pireen-andar-men-baaqi-hijaab-kehro.', 'آيو پرين اندر ۾، باقي حجاب ڪھڙو.\r\nپردو پيو پڌر ۾، باقي نقاب ڪھڙو.', NULL, 'sd', '2024-03-24 11:01:52', '2024-03-24 11:01:52', NULL),
(4705, 956, 27, 'pedaa-thiyo-Aa-aadam-soorat-manjhaan-ilhaai', 'پيدا ٿيو آ آدم، صورت منجهان الاھي،\r\nسمايو سڀني ۾، باقي خراب ڪھڙو.', NULL, 'sd', '2024-03-24 11:01:52', '2024-03-24 11:01:52', NULL),
(4706, 956, 27, 'dilber-jaa-daabb-darrkaa-jetar-aahe-judaai', 'دلبر جا داٻ دڙڪا، جيتر آھي جدائي،\r\nدم دم پرين نظر ۾، باقي عذاب ڪھڙو.', NULL, 'sd', '2024-03-24 11:01:52', '2024-03-24 11:01:52', NULL),
(4707, 956, 27, 'heeran-thiyaa-hooran-uqba-sanda-je-talab', 'حيران ٿيا حورن عقبيٰ سندا جي طالب،\r\nپر ديد مون دلبر ۾، زيادہ ثواب ڪھڙو.', NULL, 'sd', '2024-03-24 11:01:52', '2024-03-24 11:01:52', NULL),
(4708, 956, 27, 'mardu-mola-jaa-maiyal-arranda-na-kanhin-men-aashiq', 'مرد موليٰ جا مائل، اڙندا نہ ڪنھن ۾ عاشق،\r\nجن جي صفائي گهر ۾، تن سان حساب ڪھڙو.', NULL, 'sd', '2024-03-24 11:01:52', '2024-03-24 11:01:52', NULL),
(4709, 956, 27, 'shahi-Aa-shah-wilayat-ware-wajjaae-wahadat', 'شاھي آ شاھہ“ ولايت” ، واري وڄائي وحدت،\r\nبالا پرين بشر ۾، زيادہ نواب ڪھڙو.', NULL, 'sd', '2024-03-24 11:01:52', '2024-03-24 11:01:52', NULL),
(4710, 957, 27, 'aaon-yaar-punhal-pireen-tokhe-parchayan', 'آءُ يار پنهل پرين توکي پرچايان،\r\nتوکي سھڻا سرچايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4711, 957, 27, 'gul-phal-choonday-piyaar-poea', 'گل ڦل چونڊي پيار پوئي\r\nھار ڳل پايان تنهنجي سيج سجايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4712, 957, 27, 'tasawur-men-Aa-soorat-tunhinje', 'تصور ۾ آ صورت تنهنجي،\r\nپلپل پچايان توکي سيني لايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4713, 957, 27, 'aaon-annag-te-yaar-piyaraa', 'آءُ اڱڻ تي يار پيارا\r\nدرشن پايان پنھنجو روح ريجهايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4714, 957, 27, 'galliea-galliea-men-joggi-banje', 'ڳليءَ ڳليءَ ۾ جوڳي بڻجي،\r\nڳڻ تنهنجا ڳايان آءُ الک جاڳايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4715, 957, 27, 'dar-hijar-joon-ghujhyoon-ggaalhiyun', 'در ھجر جون ڳجهيون ڳالهيون،\r\nسور سڻايان توکي حال ٻڌايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4716, 957, 27, 'je-tun-rusanr-jo-rakheen-raayo', 'جي تون رسڻ جو رکين رايو،\r\nپاند ڳل پايان سڀئي سرتيون سڏايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4717, 957, 27, 'shah-wilayat-roz-azal-khaun', 'شاھہ ولايت روز ازل کئون،\r\nعاشق آهيان تنھنجو ناتو نڀايان', NULL, 'sd', '2024-03-24 11:13:07', '2024-03-24 11:56:28', '2024-03-24 11:56:28'),
(4718, 956, 27, 'aayo-pireen-andar-men-baaqi-hijaab-kehro.', 'aayo pireen andar men, baaqi hijaab kehro.\r\npardo piyo padhar men, baaqi niqab kehro.', NULL, 'en', '2024-03-24 11:13:51', '2024-03-24 11:13:51', NULL),
(4719, 956, 27, 'pedaa-thiyo-Aa-aadam-soorat-manjhaan-ilhaai', 'pedaa thiyo Aa aadam, soorat manjhaan ilhaai,\r\nsamayu sabhni men, baaqi kharab kehro.', NULL, 'en', '2024-03-24 11:13:51', '2024-03-24 11:13:51', NULL),
(4720, 956, 27, 'dilber-jaa-daabb-darrkaa-jetar-aahe-judaai', 'dilber jaa daabb darrkaa, jetar aahe judaai,\r\ndam dam pireen nazara men, baaqi azaab kehro.', NULL, 'en', '2024-03-24 11:13:51', '2024-03-24 11:13:51', NULL),
(4721, 956, 27, 'heeran-thiyaa-hooran-uqba-sanda-je-talab', 'heeran thiyaa hooran uqba sanda je talab,\r\npar deed mun dilber men, ziyadah sawab kehro.', NULL, 'en', '2024-03-24 11:13:51', '2024-03-24 11:13:51', NULL),
(4722, 956, 27, 'mardu-mola-jaa-maiyal-arranda-na-kanhin-men-aashiq', 'mard\'u mola jaa maiyal, arranda na kanhin men aashiq,\r\njin je safai ghar men, tin saan hisaab kehro.', NULL, 'en', '2024-03-24 11:13:51', '2024-03-24 11:13:51', NULL),
(4723, 956, 27, 'shahi-Aa-shah-wilayat-ware-wajjaae-wahadat', 'shahi Aa shah“ wilayat” , ware wajjaae wahadat,\r\nbala pireen bashar men, ziyadah nawaab kehro.', NULL, 'en', '2024-03-24 11:13:51', '2024-03-24 11:13:51', NULL),
(4724, 957, 27, 'aaon-yaar-punhal-pireen-tokhe-parchayan', 'aaon yaar punhal pireen tokhe parchayan,\r\ntokhe suhnra sarchayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4725, 957, 27, 'gul-phal-choonday-piyaar-poea', 'gul phal choonday piyaar poea\r\nhaar ggall payaan tunhinje seej sajayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4726, 957, 27, 'tasawur-men-Aa-soorat-tunhinje', 'tasawur men Aa soorat tunhinje,\r\npalpal pachayan tokhe seeny layaan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4727, 957, 27, 'aaon-annag-te-yaar-piyaraa', 'aaon annag te yaar piyaraa\r\ndarshan payaan pahnjo rooh reejhayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4728, 957, 27, 'galliea-galliea-men-joggi-banje', 'galliea galliea men joggi banje,\r\ngganrn tuhnja gayaan aaon alakh jagayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4729, 957, 27, 'dar-hijar-joon-ghujhyoon-ggaalhiyun', 'dar hijar joon ghujhyoon ggaalhiyun,\r\nsoor sunrnayan tokhe haal budhayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4730, 957, 27, 'je-tun-rusanr-jo-rakheen-raayo', 'je tun rusanr jo rakheen raayo,\r\npaand ggall payaan sabhai sartyoon saddayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4731, 957, 27, 'shah-wilayat-roz-azal-khaun', 'shah wilayat roz azal khaun,\r\naashiq aahiyan tunhinjo naatoo nibhayan', NULL, 'en', '2024-03-24 11:14:27', '2024-03-24 12:02:59', '2024-03-24 12:02:59'),
(4732, 960, 27, 'uhe-asal-jo-iqrar-suhnra-tosaan-tosaan', 'اھي اصل جو اقرار سھڻا توسان، توسان،\r\nآهيون دم نہ ھڪڙو ڌار سھڻا توسان، توسان.', NULL, 'sd', '2024-03-24 11:33:32', '2024-03-24 11:55:57', '2024-03-24 11:55:57'),
(4733, 960, 27, 'peer-asan-je-aaas-pujaanri-waah-wahadat-je-tand-wajjaae', 'پير اسان جي آس پڄائي، واھ وحدت جي تند وڄائي،\r\nآھي لونءَ لونءَ ۾ لغار، سھڻا توسان توسان.', NULL, 'sd', '2024-03-24 11:33:32', '2024-03-24 11:55:57', '2024-03-24 11:55:57'),
(4734, 960, 27, 'seendh-sanwary-surma-paayun-haar-ghulaabi-gul-sabayoon', 'سينڌ سنواري سرما پايون، ھار گلابي گل سبايون،\r\nسڀئي ٺھن ٿا سينگار سھڻا توسان، توسان.', NULL, 'sd', '2024-03-24 11:33:32', '2024-03-24 11:55:57', '2024-03-24 11:55:57');
