(4885, 973, 42, 'dharti-dinu-dharam', 'ڌرتي - دين - ڌرم\r\nڌرتي دين ڌرم، ڌرتي دين ڌرم،\r\nڌرتي ڪعبو، ڌرتي ڪاشي، ڌرتي ديرو حرم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4886, 973, 42, 'dinu-dharam-sabh-dharti-jjaya-dharti-aahe-utam', 'دين ڌرم سڀ ڌرتي ڄايا، ڌرتي آھي اتم،\r\nانسانن جي آجائيءَ جو، آھي ڀونءَ ڀرم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4887, 973, 42, 'sindh-dhartia-daanh-maeri-man-khe-bhagiya-kan-bhasam', 'سنڌ ڌرتيءَ ڏانھن ميري من کي، ڀاڳيا ڪن ڀسم،\r\nکاھڙ ساري کنھان کوندا، کن نہ ڪن سي کم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4888, 973, 42, 'bharyaa-tariya-daryah-hin-men-banryal-baagu-eram', 'ڀريا تريا درياھ ھن ۾، بڻيل باغ ارم،\r\nٿڌڙيون ٿڌڙيون ھيرون جنھن جون، شام وسي شبنم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4889, 973, 42, 'shah-bilawal-doda-hoshu-dharti-jjaya-jamm', 'شاھ، بلاول، دودا، ھوشو، ڌرتي ڄايا ڄم،\r\nسوڳنڌ ساٿ سچو سنڌوءَ سان، ڪوڙو ڪن نہ قسم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4890, 973, 42, 'hin-dhartia-je-har-baanke-jaa-baazo-aahin-pam', 'ھن ڌرتيءَ جي ھر بانڪي جا، بازو آھن پم،\r\nرڪ پگهاري راڻن ڇڏيا، گڙي ٿيا جي گرم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4891, 973, 42, 'mittia-saan-Aa-mahnat-jaaiz-sharaa-kehro-sharam', 'مٽيءَ سان آ محنت جائز، شرعاً ڪھڙو شرم،\r\nسنڌو ديش لئہ تيسين وڙھبو، جيسين دم ۾ دم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4892, 973, 42, 'maarai-machaya-mulku-mittia-lay-maarryan-men-matam', 'مارئي مچايا ملڪ مٽيءَ لئہ، ماڙين ۾ ماتم،\r\nڌوراڻن کي ڌوڙ وجهي ھن، ڳايا ڳاڱيون ڳم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4893, 973, 42, 'waahanr-waahanr-waharr-waahi-kare-tarayoon-tam', 'واھڻ واھڻ واھڙ واھي، ڪري ترايون تم،\r\nسنڌ درياھ جو ٻنهي ڪنڌين سان، سھڻو آھي سنگم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4894, 973, 42, 'munshi-mehkoon-sindh-mittia-joon-uthlyun-arba-o-ajaam', '”منشي“ مھڪون سنڌ مٽيءَ جون، اٿليون عرب و عجم،\r\nھن جون ھاڪون ھن جون ڌاڪون، پھتيون پورب پڇم،\r\nڌرتي دين ڌرم', NULL, 'sd', '2024-03-26 16:13:11', '2024-03-26 16:13:11', NULL),
(4895, 973, 42, 'dharti-dinu-dharam', 'dharti - dinu - dharam\r\ndharti dinu dharam، dharti dinu dharam،\r\ndharti kaabo، dharti kaashi، dharti dero haram،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4896, 973, 42, 'dinu-dharam-sabh-dharti-jjaya-dharti-aahe-utam', 'dinu dharam sabh dharti jjaya، dharti aahe utam،\r\ninsaan je aajaae jo، aahe bhoo\'an bharm،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4897, 973, 42, 'sindh-dhartia-daanh-maeri-man-khe-bhagiya-kan-bhasam', 'sindh dharti\'a daanh maeri man khe، bhagiya kan bhasam،\r\nkhaharr saari khin\'han khonda، khan\'u na kan se kham،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4898, 973, 42, 'bharyaa-tariya-daryah-hin-men-banryal-baagu-eram', 'bharyaa tariya daryah hin men، banryal baag\'u eram،\r\nthadhrriyon thadhrriyon heeroon jahin joon، shaam wasi shabnam،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4899, 973, 42, 'shah-bilawal-doda-hoshu-dharti-jjaya-jamm', 'shah، bilawal، doda، hoshu، dharti jjaya jamm،\r\nsogghandh saath sacho sindhua saan، korro kan na qasam،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4900, 973, 42, 'hin-dhartia-je-har-baanke-jaa-baazo-aahin-pam', 'hin dharti\'a je har baan\'ke jaa، baazo aahin pa\'m،\r\nrook paghaare raanran chhaddiya، garri thiyaa je garam،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4901, 973, 42, 'mittia-saan-Aa-mahnat-jaaiz-sharaa-kehro-sharam', 'mitti\'a saan Aa mahnat jaaiz، shar\'aa kehro sharam،\r\nsindhu desh lay teseen wirrhabo، jeseeen dam men dam،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4902, 973, 42, 'maarai-machaya-mulku-mittia-lay-maarryan-men-matam', 'maarai machaya mulk\'u mitti\'a lay، maarryan men matam،\r\ndhoo\'rarn khe dhoor wijhe hin، ggaya ggangnyoon ggim،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4903, 973, 42, 'waahanr-waahanr-waharr-waahi-kare-tarayoon-tam', 'waahanr waahanr waharr waahi، kare tarayoon t\'am،\r\nsindh daryah jo binhi kandhiyaan saan، suhnro aahe sangam،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4904, 973, 42, 'munshi-mehkoon-sindh-mittia-joon-uthlyun-arba-o-ajaam', '”munshi“ mehkoon sindh mitti\'a joon، uthlyun arba o ajaam،\r\nhin joon haakon hin joon dhakoon، puhtiyoon poorab pachham،\r\ndharti dinu dharam', NULL, 'en', '2024-03-26 16:15:21', '2024-03-26 16:15:21', NULL),
(4905, 0, 42, 'har-hiku-rooh-je-laai-rahamt-haari-aen-mazdoor-je-mahnat', 'ھر ھڪ روح جي لاءِ رحمت، ھاري ۽ مزدور جي محنت،\r\nپاڻ پتوڙي لُنڊيون لتوڙي، جوڙي جڳ جي لاءِ جنت.', '[\"hari\"]', 'sd', '2024-03-26 16:17:51', '2024-03-26 16:17:51', NULL),
(4906, 0, 42, 'har-hiku-rooh-je-laai-rahamt-haari-aen-mazdoor-je-mahnat', 'har hik\'u rooh je laai rahamt، haari aen mazdoor je mahnat،\r\npaarn patorre loonddiyon latoorre، jorry jug je laai jannat.', '[\"hari\"]', 'en', '2024-03-26 16:17:51', '2024-03-26 16:17:51', NULL),
(4907, 975, 42, 'kamnri-akhu-kajal', 'ڪامڻي اک ڪجل،\r\nمنھن تہ ڪيئن مھڪي پيو،\r\nٽَنگون ٻانھون ڀڳل.', NULL, 'sd', '2024-03-26 16:26:06', '2024-03-26 16:26:06', NULL),
(4908, 975, 42, 'kamnri-akhu-kajal', 'kamnri akh\'u kajal،\r\nmuhan ta kian mahaky piyo،\r\nta\'ngoon banho bhagal.', NULL, 'en', '2024-03-26 16:26:47', '2024-03-26 16:27:11', '2024-03-26 16:27:11'),
