(4963, 991, 43, 'piyaar-joon-galhriyun-nim-je-chhanu-men', 'پيار جون ڳالھڙيون نم جي ڇانءُ ۾،\r\nڪي گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4964, 991, 43, 'raat-jo-chanddu-saan-chahtun-chilwilan', 'رات جو چنڊ سان چاهتون چلوَلن،\r\nڏور آڪاس ۾ محبتون ماپجن،\r\nڏينھن جو کيٽ ۾، سنگ سونا ڪجن،\r\nسنڌ ۽ سونھن سان سالڙا گهارجن،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4965, 991, 43, 'bas-hujan-roshanyun-nim-je-chhanu-men', 'بس ھجن روشنيون نم جي ڇانءُ ۾،\r\nڪي گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4966, 991, 43, 'naaz-saan-nazninyoon-nazkiyoon-naaz-kan', 'ناز سان نازنيون، نازڪيون ناز ڪن،\r\nسورما سنڌ جا سي جَتي سان سونھن،\r\nٻار جَي ٻاتڙيون، ٻوليون ٿا ٻُرن،\r\n۽ ننڍيون نينگريون جي نوان ويس ڪن،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4967, 991, 43, 'shal-rahan-murkandyoon-nim-je-chhanu-men', 'شل رھن مُرڪنديون نم جي ڇانءُ ۾،\r\nڪي گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4968, 991, 43, 'maak-men-mahknda-kheet-khilnda-rahan', 'ماڪ ۾ مهڪندا کيٽ کِلندا رھن،\r\nباک ۾ مُرڪندا، ڍول پيا گاھ ڪن،\r\nھي مکڻ، ھي لسيون، کير ڌونئرا ھجن،\r\nساڳ ۽ ھي پلا، پيا سدائين پچن،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4969, 991, 43, 'daagg-joon-khushbyoon-nim-je-chhanu-men', 'داڳ جون خوشبيون، نم جي ڇانءُ ۾،\r\nڪي گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4970, 991, 43, 'ratt-je-raand-har-dor-men-Aa-rachi', 'رت جي راند ھر دور ۾ آ رچي،\r\n۽ لٽيرن ھتي، حق جي ھاڪ ھنئين،\r\nڀل مياڻي ھجي، يا تہ مڪلي ھجي،\r\nخون ۾ لال ٿي آھي ڌرتي سڄي،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4971, 991, 43, 'ratt-thiyun-ajrkoon-nim-je-chhanu-men', 'رت ٿيون اجرڪون نم جي ڇانءُ ۾،\r\nڪيئن گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4972, 991, 43, 'ajj-mohan-jo-darro-luk-men-piyo-bbari', 'اڄ موھن جو دڙو، لُڪ ۾ پيو ٻَري،\r\nسنڌ جو ماڻهپو ڌو‌ڙ ۾ پيو سڙي،\r\nڄڻ تہ تاريخ آ، ڪو ڪفن ويڙهيو،\r\nلاش سنڌوءَ‌ ۾، پيو ٻُڏي پيو تَري،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4973, 991, 43, 'bas-ruggo-turbatun-nim-je-chhanu-men', 'بس رڳو تُربتون نم جي ڇانءُ ۾،\r\nڪيئن گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4974, 991, 43, 'sindh-saan-sukh-je-saath-jo-silsilo', 'سنڌ سان سُک جي، ساٿ جو سلسلو،\r\n۽ وري درد جي رات جو سلسلو،\r\nتنھن پڄاڻا وري، باک جو سلسلو،\r\nآھي سلمان ھڪ، سوچ جو سلسلو،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4975, 991, 43, 'soch-saan-jorroyun-nim-je-chhanu-men', 'سوچ سان جوڙيون، نم جي ڇانءُ ۾،\r\nپوءِ گذاريون گهڙيون نم جي ڇانءُ ۾،', NULL, 'sd', '2024-03-27 17:45:02', '2024-03-27 17:45:02', NULL),
(4976, 991, 43, 'piyaar-joon-galhriyun-nim-je-chhanu-men', 'piyaar joon galhriyun nim je chhan\'u men,\r\nke guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4977, 991, 43, 'raat-jo-chanddu-saan-chahtun-chilwilan', 'raat jo chandd\'u saan chahtun chilwilan,\r\nddor akaas men mohabbatu mapjan,\r\nddeenhan jo kheet men, sang sonaa kajan,\r\nsindh aen soohan saan salrra gharjan,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4978, 991, 43, 'bas-hujan-roshanyun-nim-je-chhanu-men', 'bas hujan roshanyun nim je chhan\'u men,\r\nke guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4979, 991, 43, 'naaz-saan-nazninyoon-nazkiyoon-naaz-kan', 'naaz saan nazninyoon, nazkiyoon naaz kan,\r\nsoorma sindh jaa se jite saan soohan,\r\nbaaru jee battrryoon, bolyun thaa bburan,\r\naen nandhyun neengryon je nawaan wayis kan,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4980, 991, 43, 'shal-rahan-murkandyoon-nim-je-chhanu-men', 'shal rahan murkandyoon nim je chhan\'u men,\r\nke guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4981, 991, 43, 'maak-men-mahknda-kheet-khilnda-rahan', 'maak men mahknda kheet khilnda rahan,\r\nbaakh men murknda, dhhol piyaa gaah kan,\r\nhi makhanr, hi lasyoon, kheer\'u dhaunra hujan,\r\nsaag aen hi palaa, piyaa sadaain pachan,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4982, 991, 43, 'daagg-joon-khushbyoon-nim-je-chhanu-men', 'daagg joon khushbyoon, nim je chhan\'u men,\r\nke guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4983, 991, 43, 'ratt-je-raand-har-dor-men-Aa-rachi', 'ratt je raand har dor men Aa rachi,\r\naen luttern hittay, haq je haak heeyan,\r\nbhill miyaani hujy, yaa ta makli hujy,\r\nkhoon men laal thi aahe dharti sajji,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4984, 991, 43, 'ratt-thiyun-ajrkoon-nim-je-chhanu-men', 'ratt thiyun ajrkoon nim je chhan\'u men,\r\nkian guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4985, 991, 43, 'ajj-mohan-jo-darro-luk-men-piyo-bbari', 'ajj mohan jo darro, luk men piyo bbari,\r\nsindh jo maanrhpo dhoorr men piyo sarry,\r\njjrn ta tareekh Aa, ko kafan werrhyo,\r\nlaash sindhu\'a men, piyo buddi piyo tarri,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4986, 991, 43, 'bas-ruggo-turbatun-nim-je-chhanu-men', 'bas ruggo turbatun nim je chhan\'u men,\r\nkian guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4987, 991, 43, 'sindh-saan-sukh-je-saath-jo-silsilo', 'sindh saan sukh je, saath jo silsilo,\r\naen wari dard je raat jo silsilo,\r\ntahan pujjanra wari, baakh jo silsilo,\r\naahe salman hik\'u, soch jo silsilo,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4988, 991, 43, 'soch-saan-jorroyun-nim-je-chhanu-men', 'soch saan jorroyun, nim je chhan\'u men,\r\npoi guzarayun gharryun nim je chhan\'u men,', NULL, 'en', '2024-03-27 17:47:15', '2024-03-27 17:47:15', NULL),
(4989, 993, 21, 'ketro-mun-saanr-halndi-keru-aahe-zindagi', 'ڪيترو مون ساڻ ھلندي، ڪير آھي زندگي،\r\nڄڻ تہ واري تي پکي جو پير آھي زندگي،\r\nڪا تہ  دل کي ڌوڻ ڏي ۽ ڇاڻ جو، ڇاڻي سگھين.', NULL, 'sd', '2024-03-27 17:58:32', '2024-03-27 17:58:32', NULL),
(4990, 993, 21, 'jo-b-jholee-men-kariyo-so-bair-aahe-zindagi', 'جو بہ جهولي ۾ ڪريو سو ٻير آھي زندگي،\r\nعشق تون مان اڄ وري ٿا ڪيچ ڏانھن ڪاھي هلون،\r\nآ پنهل پنھنجو اتي، جت ڏير آ زندگي.', NULL, 'sd', '2024-03-27 17:58:32', '2024-03-27 17:58:32', NULL),
(4991, 993, 21, 'ketro-mun-saanr-halndi-keru-aahe-zindagi', 'ketro mun saanr halndi, ker\'u aahe zindagi,\r\njjrn ta ware te pakhi jo peer aahe zindagi,\r\nkaa ta  dil khe dhonr dde aen chhaanr jo, chhanri sagheen.', NULL, 'en', '2024-03-27 17:59:00', '2024-03-27 17:59:00', NULL),
(4992, 993, 21, 'jo-b-jholee-men-kariyo-so-bair-aahe-zindagi', 'jo b jholee men kariyo so bair aahe zindagi,\r\nishiq tun maan ajj wari thaa kaich daanh kahe haloon,\r\nAa punhal pahnjo ute, jatt dder Aa zindagi.', NULL, 'en', '2024-03-27 17:59:00', '2024-03-27 17:59:00', NULL),
