(5134, 1016, 34, 'tun-sa-aamina-je-akhiyan-jo-taaro', 'تون صہ آمنہ جي اکين جو تارو،\r\nتون فاطمہ جو پدر پيارو!\r\nنبي ڀلارو!\r\nاسين تہ تنھنجي اٻوجھہ امت،\r\nاسان تي تنھنجو عظيم ٿورو.\r\nاسان لئہ تنھنجي مُلڪ جو ماڻهو،\r\nپُريان ئي پوتر، ”عرب سڳورو“', NULL, 'sd', '2024-03-29 16:17:45', '2024-03-29 16:17:45', NULL),
(5135, 1016, 34, 'magar-o-mursal', 'مگر او مرسل (صہ)\r\nمعاف رکجان.\r\nجڏھن بہ ڪوئي،\r\nحجاج يوسف جو ھم شڪل ۽\r\nشمر، يزيدن جو ھم نسل ڪو\r\nعرب سڳورو\r\nوري اسان تي ڪري ٿورو.', NULL, 'sd', '2024-03-29 16:17:45', '2024-03-29 16:17:45', NULL),
(5136, 1016, 34, 'jaddahin-b-koi', 'جڏھن بہ ڪوئي،\r\nابوجھل جو عمامون اوڍي\r\nڪو اِبن ملجم،\r\nعرب سڳورو\r\nسخي لٽيرو\r\nاٻوجھہ امت جي،\r\nڪنھن بہ آميءَ جي عصمتن جا ارھ پٽي ٿو،\r\n۽ مان ڏسان ٿو،\r\nانھيءَ آميءَ جي ڪنواري ڪک مان،\r\nجو ”ابِن آدم“ جنم وٺي ٿو\r\nسو ابن مريم ڀلي نہ سڏجي،\r\nڀلي نہ مھدي ئي ڪوٺجي ٿو.\r\n\r\nپر ايترو سو ضرور ٿئي ٿو\r\nجو ڪجهہ حديثن، ھدايتن جو،\r\nگھڻن عقيدن ۽ آيتن جو،\r\nڀرم ٽٽي ٿو،\r\n۽ دين جي جنتن مان آدم\r\nوري بہ ساڳيو ثمر پٽي ٿو.!', NULL, 'sd', '2024-03-29 16:17:45', '2024-03-29 16:17:45', NULL),
(5137, 1017, 44, 'naasi-pakhia-jo-paru-aen-neero-hawa-jo-rang', 'ناسي پَکيءَ جو پَرُ ۽ نيرو هَوا جو رنگُ، \r\nاُجرو وَهي پيو جَرُ ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5138, 1017, 44, 'mun-men-piyo-ajaan-bhe-hikray-sadiea-khan-khaddky', 'مون ۾ پيو اڃان ڀي ھڪڙي صديءَ کان کَڙڪي، \r\nھڪڙو پُراڻو دَرُ ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5139, 1017, 44, 'jahin-taraf-ho-shikaari-bandooqa-sanru-bitho', 'جنھن طرف ھو شڪاري بندوقَ ساڻُ بيٺو، \r\nتنھن طرف ھو وَڳرُ ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5140, 1017, 44, 'hikro-pahaadu-jahin-je-chottie-te-chandu-chamky', 'ھڪڙو پھاڙُ جنھن جي چوٽيءَ تي چنڊُ چَمڪي، \r\nڳاڙهو سندس پَٿرُ ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5141, 1017, 44, 'jhangli-warnan-je-wejho-hiku-naangu-ho-likal', 'جهنگلي وَڻن جي ويجهو هڪُ نانگُ ھو لڪل، \r\nهاريءَ ڏٺو پئي هَرُ ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5142, 1017, 44, 'tasweer-men-leeyu-mun-paae-dditho-ho-jahin-khe', 'تصوير ۾ ليئو مون پائي ڏٺو ھو جنھن کي، \r\nننڍپڻ سندو شھر ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5143, 1017, 44, 'neeren-ragan-maan-wahando-neero-kare-wayo-ho', 'نِيرين رڳن مان وھندو نِيرو ڪري ويو ھو، \r\nنيرو هُيو زهرُ ۽ نيرو هَوا جو رنگُ.', NULL, 'sd', '2024-03-29 16:23:54', '2024-03-29 16:23:54', NULL),
(5144, 1017, 44, 'naasi-pakhia-jo-paru-aen-neero-hawa-jo-rang', 'naasi pakhi\'a jo par\'u aen neero hawa jo rang, \r\nujooro wahy piyo jar\'u aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5145, 1017, 44, 'mun-men-piyo-ajaan-bhe-hikray-sadiea-khan-khaddky', 'mun men piyo ajaan bhe hikray sadi\'ea khan khaddky, \r\nhikro puraano dar\'u aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5146, 1017, 44, 'jahin-taraf-ho-shikaari-bandooqa-sanru-bitho', 'jahin taraf ho shikaari bandooq\'a sanr\'u bitho, \r\ntahan taraf ho waggar\'u aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5147, 1017, 44, 'hikro-pahaadu-jahin-je-chottie-te-chandu-chamky', 'hikro pahaad\'u jahin je chottie te chandu chamky, \r\ngarho sandas pathar\'u aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5148, 1017, 44, 'jhangli-warnan-je-wejho-hiku-naangu-ho-likal', 'jhangli warnan je wejho hiku naang\'u ho likal, \r\nhaari\'a dditho paee har\'u aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5149, 1017, 44, 'tasweer-men-leeyu-mun-paae-dditho-ho-jahin-khe', 'tasweer men leeyu mun paae dditho ho jahin khe, \r\nnandhpirn sando shahar aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5150, 1017, 44, 'neeren-ragan-maan-wahando-neero-kare-wayo-ho', 'neeren ragan maan wahando neero kare wayo ho, \r\nneero huyo zahar\'u aen neero hawa jo rang.', NULL, 'en', '2024-03-29 16:24:51', '2024-03-29 16:24:51', NULL),
(5151, 1019, 34, 'jahin-na-mittia-je-khushbooa-khe-jaato-hittay', 'جنھن نہ مٽيءَ جي خوشبوءِ کي ڄاتو ھتي،\r\nجنھن نہ سانگين جي سڱ کي سڃاتو ھتي،\r\nتنھن سان ڪھڙو ڀلا منھنجو ناتو ھتي،\r\nتنھن کي چئو! منھنجي نگريءَ مان نڪري وڃي!', NULL, 'sd', '2024-03-29 16:40:15', '2024-03-29 16:40:15', NULL),
(5152, 1019, 34, 'jo-na-ajrak-je-azmat-jo-qaatal-hujy', 'جو نہ اجرڪ جي عظمت جو قائل ھجي،\r\nمنھنجي ٻولي ڏي جيڪو نہ مائل ھجي،\r\nذھن جنھن جو اڃا ڀي ورھايل ھجي،\r\nتنھن کي چئو! منھنجي نگريءَ مان نڪري وڃي.', NULL, 'sd', '2024-03-29 16:40:15', '2024-03-29 16:40:15', NULL),
(5153, 1019, 34, 'jahin-sangeenan-saan-sangeen-jaa-seena-chathya', 'جنھن سنگينن سان سانگين جا سينا چٿيا،\r\nڄڻ محبت جا مڪا مدينا چٿيا،\r\nعشق عرفان جا آبگينا چٿيا،\r\nتنھن کي چئو! منھنجي نگريءَ مان نڪري وڃي.', NULL, 'sd', '2024-03-29 16:40:15', '2024-03-29 16:40:15', NULL),
(5154, 1019, 34, 'jahin-je-akhu-maruan-daanh-maeri-hujy', 'جنھن جي اک ماروئن ڏانھن ميري ھجي،\r\nجيڪو وانگين جي ٻچڙن جو ويري ھجي،\r\nجيڪو جلاد، قاتل ۽ قھري ھجي،\r\nتنھن کي چئو! منھنجي نگريءَ مان نڪري وڃي.', NULL, 'sd', '2024-03-29 16:40:15', '2024-03-29 16:40:15', NULL),
(5155, 1019, 34, 'jeko-lalanr-liyaran-saan-nafrat-kare', 'جيڪو لالڻ ليارن سان نفرت ڪري،\r\nمنھنجي پورھيت پنھوارن سان نفرت ڪري،\r\nدرد وند ديس وارن سان نفرت ڪري،\r\nتنھن کي چئو! منھنجي نگريءَ مان نڪري وڃي.', NULL, 'sd', '2024-03-29 16:40:15', '2024-03-29 16:40:15', NULL),
(5156, 1020, 34, 'yaadgeeryun-jaa-selaab-indaa-rahiyaa', 'يادگيرين جا سيلاب ايندا رھيا،\r\nبند اکڙين منجهان بَند ڀَڄندا رھيا،', NULL, 'sd', '2024-03-29 16:56:01', '2024-03-29 16:56:01', NULL),
(5157, 1020, 34, 'chnd-chathroon-kayun', 'چند چٿرون ڪيون،\r\nدل جي تنھائي تي،\r\nتنھنجي آڪاش جي،\r\nھن اڪيلائي تي،\r\nطنز سان روز تارا تڪيندا رھيا،\r\nيادگيرين جا سيلاب ايندا رھيا.', NULL, 'sd', '2024-03-29 16:56:01', '2024-03-29 16:56:01', NULL),
(5158, 1020, 34, 'wakt!-tunhinjo-hi-wartaau', 'وقت! تنھنجو ھي ورتاءُ،\r\nواجب نہ ھو،\r\nگھايلن تي نئون گھاءُ،\r\nواجب نہ ھو،\r\nوک وک تي وڇوڙا وڍيندا رھيا،\r\nيادگيرين جا سيلاب ايندا رھيا.', NULL, 'sd', '2024-03-29 16:56:01', '2024-03-29 16:56:01', NULL),
(5159, 1020, 34, 'nainr-neendhoon-huya', 'نيڻ نينڍون ھيا،\r\nويڻ وڻندا ھيا،\r\nتنھنجي ڇڙٻن منجهان،\r\nڦول ڇڻندا رھيا،\r\nھا! مگر اڄ اھي وقت ويندا رھيا،\r\nيادگيرين جا سيلاب ايندا رھيا.', NULL, 'sd', '2024-03-29 16:56:01', '2024-03-29 16:56:01', NULL),
(5160, 1021, 34, 'maan-halyu-chho-wayis', 'مان ھليو ڇو ويس؟\r\nتون پڇين ٿو پرين!\r\nمان ھليو ڇو ويس؟', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5161, 1021, 34, 'jaddheen-shaam-jo-jaam', 'جڏھين شام جو جام،\r\nخالي نہ ھو،\r\nرات رم جهم ھُئي،\r\nتنھنجي ساھن جي،\r\nجھيڻن سُرن سان سُتل،\r\nمنھنجي بستر تي،\r\nمُنڪر نڪير موسم ھئي.', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5162, 1021, 34, 'tunhinje-kajliyan-akhiyan-je', 'تنھنجي ڪجلين اکين جي،\r\nڪنارن مٿي،\r\nمنھنجي چپڙن جي ٻيڙي،\r\nلڳي ئي لڳي،\r\nھا! انھيءَ کان اڳي،\r\nمان ھليو ڇو ويس؟', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5163, 1021, 34, 'sach-pucheen-o-pireen', 'سچ پڇين او پرين!\r\nدل بہ چاھيو گهڻو،\r\nڪا گهڙي تنھنجي،\r\nوارن جي ڪارين گھٽائن منجهان،\r\nواس وٺندو رھان!\r\nٻن گھڙين لئہ سھي،\r\nتنھنجي آغوش جي سانوري شام ۾،\r\nڪنھن روپوش پياسي مسافر جيان،\r\nمان پناھون وٺان،\r\nکن پل ئي سھي،\r\nٿڪ ٿورو ڀڃان.', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5164, 1021, 34, 'dil-ta-chahyu-ghanro', 'دل تہ چاھيو گهڻو،\r\nتنھنجي نيڻن جون،\r\nنرمل ھي چانڊوڪيون،\r\nسدا مست نيندون نياپا کڻي،\r\nروح جي رڻ مٿان،\r\nايئن رقصان رھن.', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5165, 1021, 34, 'dil-ta-chahyu-ghanro', 'دل تہ چاھيو گهڻو،\r\nتنھنجي ٻانهن جون ڪچڙيون،\r\n۽ ڪومل وليون،\r\nسونھن سرھاڻ جا،\r\nسؤ سنديسا کڻي،\r\nمنھنجي تن تي سدا،\r\nايئن وکريل رھن.', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5166, 1021, 34, 'haa-magar-chhaa-kariyun', 'ھا مگر ڇا ڪريون\r\nتنھنجي ڪاڪل جي قيدي،\r\nٿيڻ کان اڳي،\r\nقيدخانن ۾ ڪائي ڪشش ھئي پرين!\r\nجيڪا ويئي ڇڪي.', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5167, 1021, 34, 'dil-ta-manranr-ghuryoon', 'دل تہ ماڻڻ گھُريون،\r\nتنھنجون سڀ دلبريون،\r\nھا مگر ڇا ڪريون،\r\nدار جي درد کي،\r\nپنھنجي لذت ھئي،\r\nجيڪا ويئي ڇڪي،\r\nتون پڇين ٿو پرين!\r\nمان ھليو ڇو ويس.', NULL, 'sd', '2024-03-29 17:11:12', '2024-03-29 17:11:12', NULL),
(5168, 1021, 34, 'maan-halyu-chho-wayis', 'maan halyu chho wayis?\r\ntun pucheen tho pireen!\r\nmaan halyu chho wayis?', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5169, 1021, 34, 'jaddheen-shaam-jo-jaam', 'jaddheen shaam jo jaam,\r\nkhaali na ho,\r\nraat rim jhim hui,\r\ntunhinje sahan je,\r\njheernan suran saan sutal,\r\nmuhinje bistar te,\r\nmunkar nakeer mosam hui.', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5170, 1021, 34, 'tunhinje-kajliyan-akhiyan-je', 'tunhinje kajliyan akhiyan je,\r\nkinaran mathe,\r\nmuhinje chapran je bairri,\r\nlaggay e laggay,\r\nhaa! inahi\'a khan aggai,\r\nmaan halyu chho wayis?', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5171, 1021, 34, 'sach-pucheen-o-pireen', 'sach pucheen o pireen!\r\ndil b chahyu ghanro,\r\nkaa gharri tunhinje,\r\nwaaran je kariyan ghataain manjhaan,\r\nwaas wathndo rahaan!\r\nbbin gharriyan lay sahi,\r\ntunhinje aghosh je saawnri shaam men,\r\nkanhin ropoosh piyaasi musaafir jiyaan,\r\nmaan panahun wathjaan,\r\nkhan\'u pal e sahi,\r\nthak thoro bhanjaan.', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5172, 1021, 34, 'dil-ta-chahyu-ghanro', 'dil ta chahyu ghanro,\r\ntunhinje neenan joon,\r\nnirmal hi chaddokiyoon,\r\nsadaa masat neendoon niyapaaa khanri,\r\nrooh je rirn mathan,\r\nean raqsa rahan.', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5173, 1021, 34, 'dil-ta-chahyu-ghanro', 'dil ta chahyu ghanro,\r\ntunhinje bbanahn joon kachhryon,\r\naen komal walyun,\r\nsoohan surhhanr jaa,\r\nsao sandesa khanri,\r\nmuhinje tin te sadaa,\r\nean wikhryal rahan.', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5174, 1021, 34, 'haa-magar-chhaa-kariyun', 'haa magar chhaa kariyun\r\ntunhinje kakal je qaidi,\r\nthiyarn khan aggai,\r\nqaidkhanan men kaai kashish hui pireen!\r\njekaa wei chhikay.', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5175, 1021, 34, 'dil-ta-manranr-ghuryoon', 'dil ta manranr ghuryoon,\r\ntuhnjoon sabh dilbryoon,\r\nhaa magar chhaa kariyun,\r\ndaar je dard khe,\r\npahinje lizat hui,\r\njekaa wei chhikay,\r\ntun pucheen tho pireen!\r\nmaan halyu chho wayis.', NULL, 'en', '2024-03-29 17:16:50', '2024-03-29 17:16:50', NULL),
(5176, 1020, 34, 'yaadgeeryun-jaa-selaab-indaa-rahiyaa', 'yaadgeeryun jaa selaab indaa rahiyaa,\r\nband akhryan manjhaan band bhajjnda rahiyaa,', NULL, 'en', '2024-03-29 17:18:13', '2024-03-29 17:18:13', NULL),
(5177, 1020, 34, 'chnd-chathroon-kayun', 'chnd chathroon kayun,\r\ndil je tanhai te,\r\ntunhinje aakash je,\r\nhin akelai te,\r\ntanz saan roz tara takeenda rahiyaa,\r\nyaadgeeryun jaa selaab indaa rahiyaa.', NULL, 'en', '2024-03-29 17:18:13', '2024-03-29 17:18:13', NULL),
(5178, 1020, 34, 'wakt!-tunhinjo-hi-wartaau', 'wakt! tunhinjo hi wartaau,\r\nwajab na ho,\r\nghahayaln te naon ghaahu,\r\nwajab na ho,\r\nwikh wikh te wichhorraa wadheenda rahiyaa,\r\nyaadgeeryun jaa selaab indaa rahiyaa.', NULL, 'en', '2024-03-29 17:18:13', '2024-03-29 17:18:13', NULL),
(5179, 1020, 34, 'nainr-neendhoon-huya', 'nainr neendhoon huya,\r\nwenr warnda huya,\r\ntunhinje chhirrbban manjhaan,\r\nphool chharnda rahiyaa,\r\nhaa! magar ajj uhe wakt windaa rahiyaa,\r\nyaadgeeryun jaa selaab indaa rahiyaa.', NULL, 'en', '2024-03-29 17:18:13', '2024-03-29 17:18:13', NULL),
(5180, 1019, 34, 'jahin-na-mittia-je-khushbooa-khe-jaato-hittay', 'jahin na mitti\'a je khushboo\'a khe jaato hittay,\r\njahin na sangeen je sang khe sunjaato hittay,\r\ntahan saan kehro bhalaa munhinjo naatoo hittay,\r\ntahan khe chau! muhinje nangaria maan nikre wanjnay!', NULL, 'en', '2024-03-29 17:19:00', '2024-03-29 17:19:00', NULL),
(5181, 1019, 34, 'jo-na-ajrak-je-azmat-jo-qaatal-hujy', 'jo na ajrak je azmat jo qaatal hujy,\r\nmuhinje bboli dde jeko na maiyal hujy,\r\nzehan jahin jo ajan bhe wirhayal hujy,\r\ntahan khe chau! muhinje nangaria maan nikre wanjnay.', NULL, 'en', '2024-03-29 17:19:00', '2024-03-29 17:19:00', NULL),
(5182, 1019, 34, 'jahin-sangeenan-saan-sangeen-jaa-seena-chathya', 'jahin sangeenan saan sangeen jaa seena chathya,\r\njjrn muhabat jaa maka madina chathya,\r\nishiq irfan jaa abgeena chathya,\r\ntahan khe chau! muhinje nangaria maan nikre wanjnay.', NULL, 'en', '2024-03-29 17:19:00', '2024-03-29 17:19:00', NULL),
(5183, 1019, 34, 'jahin-je-akhu-maruan-daanh-maeri-hujy', 'jahin je akh\'u maruan daanh maeri hujy,\r\njeko wangyaan je bacharran jo veeri hujy,\r\njeko jalaad, qaatil aen qahari hujy,\r\ntahan khe chau! muhinje nangaria maan nikre wanjnay.', NULL, 'en', '2024-03-29 17:19:00', '2024-03-29 17:19:00', NULL),
(5184, 1019, 34, 'jeko-lalanr-liyaran-saan-nafrat-kare', 'jeko lalanr liyaran saan nafrat kare,\r\nmuhinje porhaiyat pahnwaran saan nafrat kare,\r\ndard wand dees waaran saan nafrat kare,\r\ntahan khe chau! muhinje nangaria maan nikre wanjnay.', NULL, 'en', '2024-03-29 17:19:00', '2024-03-29 17:19:00', NULL),
(5185, 1016, 34, 'tun-sa-aamina-je-akhiyan-jo-taaro', 'tun sa aamina je akhiyan jo taaro,\r\ntun fatima jo padar piyaaro!\r\nnabi bhalaro!\r\naseen ta tunhinje abbojh umat,\r\nasan te tunhinjo azim thoro.\r\nasan lay tunhinje mulk jo manhun,\r\npuryaan e potar, arba” saggoro“', NULL, 'en', '2024-03-29 17:21:06', '2024-03-29 17:21:06', NULL),
(5186, 1016, 34, 'magar-o-mursal', 'magar o mursal (صہ)\r\nmuaaf rakhjaan.\r\njaddahin b koi,\r\nhajaj yosuf jo ham shakal aen\r\nshamar, yazeedan jo ham nasal ko\r\narba saggoro\r\nwari asan te kare thoro.', NULL, 'en', '2024-03-29 17:21:06', '2024-03-29 17:21:06', NULL),
(5187, 1016, 34, 'jaddahin-b-koi', 'jaddahin b koi,\r\nabujahal jo imamoon odhy\r\nko iban muljam,\r\narba saggoro\r\nsakhee luttero\r\nabbojh umat je,\r\nkanhin b aami\'a je asmatan jaa urah patti tho,\r\naen maan disaan tho,\r\ninahi\'a aami\'a je kuwanri kakh maan,\r\njo iban” aadam“ janam wathee tho\r\nso aban maryam bhale na saddje,\r\nbhale na mahedi e kothje tho.\r\n\r\npar aetro so zaroor thyee tho\r\njo kujhh hadeesan, hidayatan jo,\r\ngharnan aqeedan aen ayatan jo,\r\nbharm tutti tho,\r\naen dinu je jantan maan aadam\r\nwari b saaggiyo samar patti tho.!', NULL, 'en', '2024-03-29 17:21:06', '2024-03-29 17:21:06', NULL),
(5188, 1015, 34, 'pahnja-bhaag-niyara-nikta', 'pahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5189, 1015, 34, 'motiyan-jahirraa-manhun-saain', 'motiyan jahirraa manhun saain!\r\nkoorra kajj kasara nikta,\r\npahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5190, 1015, 34, 'pahinje-laai-ta-o-pagal-man', 'pahinje laai ta o pagal man,\r\nkattonba bhe khara nikta,\r\npahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5191, 1015, 34, 'ddahan-je-hin-ddeeh-sajji-men', 'ddahan je hin ddeeh sajji men,\r\ndeewana ddil waraa nikta,\r\npahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5192, 1015, 34, 'koi-dil-men-dard-na-poi-b', 'koi dil men dard na poi b,\r\nneenan maan naisaraa nikta,\r\npahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5193, 1015, 34, 'sik-waaran-jo-sogg-malhainr', 'sik waaran jo sogg malhainr,\r\nchandd\'u katyoon aen tara nikta,\r\npahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5194, 1015, 34, 'pahnjo-piyaar-ta-paap-na-ho-poi', 'pahnjo piyaar ta paap na ho poi,\r\ndushman chho jug waraa nikta.\r\npahnja bhaag niyara nikta.', NULL, 'en', '2024-03-29 17:22:16', '2024-03-29 17:22:16', NULL),
(5195, 1014, 34, 'array-ishiq-to-watt-wari-piyo-achaan', 'array ishiq! to watt wari piyo achaan,\r\nkhudhkashi, zindagi\'a khan pare Aa ajaan,', NULL, 'en', '2024-03-29 17:24:04', '2024-03-29 17:24:04', NULL),
(5196, 1014, 34, 'jaddheen-bhe-to-hath-maan-Aa-hathrro-kadhyon', 'jaddheen bhe to hath maan Aa hathrro kadhyon,\r\nhathan je lakeeran saan wirchyo pawaan.', NULL, 'en', '2024-03-29 17:24:04', '2024-03-29 17:24:04', NULL),
(5197, 1014, 34, 'awhan-je-akhiyan-men-ke-argoon-huaa', 'awhan je akhiyan men ke argoon huaa,\r\nasan je hui dil, sindh dharti jiyaan.', NULL, 'en', '2024-03-29 17:24:04', '2024-03-29 17:24:04', NULL),
(5198, 1014, 34, 'jin-je-qismat-men-dozakh-dhuriyan-e-likhiyo', 'jin je qismat men dozakh dhuriyan e likhiyo,\r\no khudaa! kean tokhe chawan mehrbaan.', NULL, 'en', '2024-03-29 17:24:04', '2024-03-29 17:24:04', NULL),
(5199, 1014, 34, 'o-suqrat-hiku-surk-je-haa-bhryan', 'o suqrat! hik\'u surk je haa bhryan,\r\nzindagi\'a je zahar je, manjaan haa miyaan!', NULL, 'en', '2024-03-29 17:24:04', '2024-03-29 17:24:04', NULL),
(5200, 1013, 34, 'wakt-jo-wenr-jeseeen-wihaatte-natho', 'wakt jo wenr jeseeen wihaatte natho,\r\nghaahu gham jo jeseeen ghumatte natho,\r\njeseeen jeewan jo pyaalo hi paltte natho,\r\nzindagi! zahar bhar, zahar bhar, zahar bhar.', NULL, 'en', '2024-03-29 17:24:48', '2024-03-29 17:24:48', NULL),
(5201, 1013, 34, 'un-garniya-aasra-athattoon-annu-mewan', 'un garniya aasra, athattoon ann\'u mewan,\r\nmamra, monjh, manzil joon mahroomyoon,\r\nkainatan wadho jeke kashkol men,\r\nsooi sabh kujhh athai, sooi samjhajh samar,\r\nzindagi! zahar bhar, zahar bhar, zahar bhar.', NULL, 'en', '2024-03-29 17:24:48', '2024-03-29 17:24:48', NULL),
(5202, 1013, 34, 'paarn-khe-jaa-pukhi-men-ruggo-piyaas-hui', 'paarn khe jaa pukhi men, ruggo piyaas hui,\r\nse wirah aen wichhorraa, wadde raas hui,\r\nunjnan jaa umlah daadha, ahsaas athai!\r\njeke maanryo na thyee, tunhinjo matam na kar,\r\nzindagi! zahar bhar, zahar bhar, zahar bhar.', NULL, 'en', '2024-03-29 17:24:48', '2024-03-29 17:24:48', NULL),
(5203, 1013, 34, 'mot-jo-neth-mahsool-pararno-ta-Aa', 'mot jo neth mahsool pararno ta Aa,\r\nhi jo aakash wayo, haanr wararno na Aa,\r\npar chuggan taaen jug khan wisaranro na Aa,\r\nddaat jo ddeeh men keen thindo ddukar,\r\nzindagi! zahar bhar, zahar bhar, zahar bhar.', NULL, 'en', '2024-03-29 17:24:48', '2024-03-29 17:24:48', NULL),
(5204, 1012, 34, 'hin-dees-je-popatan-je-aggyan', 'hin dees je popatan je aggyan,\r\nkandh\'u kehro khiwanr!?\r\nhaam kehrry harno!?', NULL, 'en', '2024-03-29 17:26:01', '2024-03-29 17:26:01', NULL),
(5205, 1012, 34, 'hinan-khe-ddisi-jeeu-jahjjndo-rahiyo', 'hinan khe ddisi jeeu jahjjndo rahiyo,\r\nkeedda piyasa thaa bhattkan uhe pitkarra,\r\ngullan je mithasan khan mahromrra,\r\nabbala rahiyaa thar je baaran jiyaan,\r\ntin mashooqarran khe dyun bhe chhaa?', NULL, 'en', '2024-03-29 17:26:01', '2024-03-29 17:26:01', NULL),
(5206, 1012, 34, 'lukh-harnyoon-leeyoon', 'lukh harnyoon leeyoon!?\r\nthooharn jaa kanda!?\r\nkian aachhyoon unhan khe\r\narrahrr jaa sarryal\r\nugharaa, ugharaa hi kanderrara!?\r\nkotan khe budhae chhadd majraa.', NULL, 'en', '2024-03-29 17:26:01', '2024-03-29 17:26:01', NULL),
(5207, 1012, 34, 'asan-watt-na-indaa-kariyo-pitkarra', 'asan watt na indaa kariyo pitkarra!\r\nasan watt ta sawanr b sadiyan kanaan,\r\nruggo soor jaa tho phulaare sala,\r\nkob mugro na Aa,\r\nkob motyo na Aa,\r\nchanbaili kaddheen\r\nwaas otyoo na Aa,\r\nhee\'a wasti ta wirhaan khan weeran Aa.\r\nmadtan khan hittay mand mataam Aa.\r\npitkarra popttao?\r\nkeen dil men kajo!\r\nasan jaa sabhai baag\'u bhelarr thiyaa,\r\nsaari phulwarr phurji asan khan wai,\r\nphool wikji wiya,\r\nasan khud baharan lay bhattkoon piyaa,\r\nasan khud baharan lay bhattkoon piyaa.', NULL, 'en', '2024-03-29 17:26:01', '2024-03-29 17:26:01', NULL),
(5208, 1011, 34, 'haanray-ta-aakash-je-ott-men', 'haanray ta aakash je ott men,\r\nasmani ddeyaa pirn ajhami wiya.\r\nsumhee piyaa sitaraa, thi jheernaa jhaka,\r\nkaddahankar katiyan kar morray chhaddiya,\r\nmagar intazaran sanda qafla,\r\nthiyaa keen thakji kaddheen choor choor,\r\ntun eendien, tun eendien, tun eendien zaroor.', NULL, 'en', '2024-03-29 17:26:39', '2024-03-29 17:26:39', NULL),
(5209, 1011, 34, 'sagar-je-hin-paar-bairri-men-mallah', 'sagar je hin paar bairri men mallah,\r\nsamoonddi\'a jaa sabh bet ggaaye chhaddiya,\r\naen kinaare je waari\'a je laharan andar,\r\nhawaon b sargam likaay chhaddiya,\r\nwalah warchhi chhaddiyo pahnjo saaro suroor,\r\nmagar dil hi pagal manji keen morr,\r\ntun eendien, tun eendien, tun eendien zaroor.', NULL, 'en', '2024-03-29 17:26:39', '2024-03-29 17:26:39', NULL),
(5210, 1011, 34, 'ko-panchhi-akelo-akelo-udaami', 'ko panchhi akelo akelo udaami,\r\nkeenjhar, kandhi te lahay piyo thaki,\r\nnako chandd\'u chamko na chahaki chakor,\r\nunhan laai pirn raat guzri chukki,\r\naen kanwalan mathan nindd\'a naai wai noor,\r\nmagar dil hi pagal manji keen morr,\r\ntun eendien, tun eendien, tun eendien zaroor.', NULL, 'en', '2024-03-29 17:26:39', '2024-03-29 17:26:39', NULL),
(5211, 1010, 34, 'wuthee-je-walah-raatari', 'wuthee je walah raatari,\r\nthadho saaro thar,\r\nkajliya! tobin kian kariyaan?', NULL, 'en', '2024-03-29 17:28:04', '2024-03-29 17:28:04', NULL),
(5212, 1010, 34, 'marerran-muhan-kadhya', 'marerran muhan kadhya,\r\nleeti piyaa lular,\r\nkajliya! tobin kian kariyaan?', NULL, 'en', '2024-03-29 17:28:04', '2024-03-29 17:28:04', NULL),
(5213, 1010, 34, 'har-kanhin-parro-patt-jo', 'har kanhin parro patt jo,\r\nhar kanhin warr walar,\r\nkajliya! tobin kian kariyaan?', NULL, 'en', '2024-03-29 17:28:04', '2024-03-29 17:28:04', NULL),
(5214, 1010, 34, 'korrhiyo-karoonjhar-laggay', 'korrhiyo karoonjhar laggay,\r\nporho padmasar\r\nkajliya! tobin kian kariyaan?', NULL, 'en', '2024-03-29 17:28:04', '2024-03-29 17:28:04', NULL),
(5215, 1010, 34, 'tun-ddoraaheen-ddeeh-men', 'tun ddoraaheen ddeeh men,\r\nkuttyen road\' pathar,\r\nkajliya! to bin kian kariyaan?', NULL, 'en', '2024-03-29 17:28:04', '2024-03-29 17:28:04', NULL),
(5216, 1009, 34, 'wakt-Aa-jhangli-machhryal-ghorro', 'wakt Aa jhangli machhryal ghorro,\r\njahin je harnkar saan,\r\ndharti sando seeno dharrki.', NULL, 'en', '2024-03-29 17:28:36', '2024-03-29 17:28:36', NULL),
(5217, 1009, 34, 'jahin-je-tezzi-khe', 'jahin je tezzi khe,\r\nhawaaun kaddheen chhuhi na saghan,\r\njahin je chusti aggyan\r\ntoofaan je hasti dharrki,\r\njahin je dahashat aggyan\r\nwahashat jo pirn gardan lurki\r\njahin je nasan maan jjrn\r\nkaa baah zaal je bharrki\r\njahin je riftaar jjrn\r\nselaab men sindhu karrki.', NULL, 'en', '2024-03-29 17:28:36', '2024-03-29 17:28:36', NULL),
(5218, 1009, 34, 'haa-magar-poi-b-aqul-jaa-veeri', 'haa magar, poi b aqul jaa veeri,\r\nfaham, fikar o nazar jaa dushman,\r\naayaa aahin ajj wari saain!\r\npahinje korry anna bachainr lay,\r\nraat je ruth te haswari kare,\r\nddeenhan je sheenh khe kuhairn lay,\r\npahinje khushfahmiyan jaa khara khanri,\r\nwakt je sij khe likainr lay.', NULL, 'en', '2024-03-29 17:28:36', '2024-03-29 17:28:36', NULL),
(5219, 1009, 34, 'dosto-koi-budhae-tin-khe', 'dosto! koi budhae tin khe,\r\nwakt Aa jhangli machhryal ghorro,\r\njahin khe wagarn b ko chahe ta na waagi saghndo,\r\nwakt Aa wahakro\r\ntareekh je samoondan jo,\r\njahin khe jhagarn b ko chahe ta na jhaaggi saghndo,\r\nwakt Aa jhangli machhryal ghorro.', NULL, 'en', '2024-03-29 17:28:36', '2024-03-29 17:28:36', NULL),
(5220, 936, 38, 'fasla-ruggo-khushkia-aen', 'فاصلا رڳو خُشڪيءَ ۽\r\nپاڻيءَ جا ماپي سگهبا آھن \r\nدلين جا نہ\r\nدِليون جيڪي\r\nمخالف ڏِسائن ۾ هلندَي\r\nالئہ ڪِٿ وڃي پُڳيون\r\nگوگل مئپ ۾\r\nتنھنجو پاڙو ڏسي ٿو سگهجي\r\n۽ شايد گهرُ بہ\r\nپر تنهنجي گهر جو درُ تہ کڙڪائي نہ ٿو سگهجي!', NULL, 'sd', '2024-03-31 14:24:48', '2024-03-31 14:24:48', NULL),
(5221, 936, 38, 'fasla-ruggo-khushkia-aen', 'fasla ruggo khushki\'a aen\r\npaarni\'a jaa maape saghba aahin \r\ndilian jaa na\r\ndilyoon jeke\r\nmukhalaf ddisain men halndi\r\nalai kith wanjnay puggiyoon\r\ngoogle maip men\r\ntunhinjo parro ddisi tho saghje\r\naen shayad ghar\'u b\r\npar tunhinje ghar jo dar\'u ta kharkaai na tho saghje!', NULL, 'en', '2024-03-31 14:25:07', '2024-03-31 14:25:07', NULL),
(5222, 412, 27, 'aahe-tokhaan-sawai-ker-kehankhe-haal-sunrnayan', 'آھي توکان سواءِ ڪير ڪنهنکي حال سڻايان،\r\nحيران آهيان آءُ تہ پرين ڳالهہ ٻڌايان.', NULL, 'sd', '2024-04-03 13:10:50', '2024-04-03 13:10:50', NULL),
(5223, 412, 27, 'muhnje-khushi-Aa-tosaan-tun-aan-muhnje-khushi', 'منهنجي خوشي آ توسان، تون آن منهنجي خوشي،\r\nجيڪر نہ جيئان جڳ ۾ جي تون وڃين رُسي،\r\nسرتيون سڏي سڀئي پرين توکي پرچايان،', NULL, 'sd', '2024-04-03 13:10:50', '2024-04-03 13:10:50', NULL),
(5224, 412, 27, 'mushtaaq-sabhai-merray-hik-handh-kayaan-harrai', 'مشتاق سڀئي ميڙي ھڪ ھنڌ ڪيان ھڙئي،\r\nسانگي اوهان جي سھڻا موجان وٺان مڙئي،\r\nخامن کان ٿي خُلاصو عوشاق آڻايان.', NULL, 'sd', '2024-04-03 13:10:50', '2024-04-03 13:10:50', NULL),
(5225, 412, 27, 'muhnje-annag-te-suhnro-sarho-sadaa-huje', 'منهنجي اڱڻ تي سھڻو سرھو سدا هُجي،\r\nتن مال ڌن مڙيوئي تنھن تان فِدا هُجي،\r\nقربان ڪيان جان ساري گهوري گهمايان.', NULL, 'sd', '2024-04-03 13:10:50', '2024-04-03 13:10:50', NULL),
(5226, 412, 27, 'dilber-widhahan-daam-men-sabh-muhabati-merray', 'دلبر وڌاهن دام ۾ سڀ محبتي ميڙي،\r\nرھبر ريجهائي راز سان ٿو ساز کي ڇيڙي،\r\n\"شاھہ ولايت\" عشق کي ناچو ٿي نچايان.', NULL, 'sd', '2024-04-03 13:10:50', '2024-04-03 13:10:50', NULL),
(5227, 489, 8, 'nidorri\'a-saan-neenhan-laai-lade-wain-halyu', 'nidorri\'a saan neenhan, laai lade wain halyu,\r\ntorri\'a ddangan rooh khe, ddenbhu\'a waangy ddeehan,\r\nmilanr sanda meenhn, wasai kei wirah thiyaa.', NULL, 'en', '2024-04-03 13:13:20', '2024-04-03 13:13:20', NULL),
(5228, 1033, NULL, 'hin-je-ghar-taaen-mun-saan-waatt-hali', 'هن جي گهر تائين مون سان واٽ هلي! \r\nپوءِ هڪ دل هلي، ٻي ڪاٽ هلي!', NULL, 'sd', '2024-04-10 12:27:53', '2024-04-10 12:27:53', NULL),
(5229, 1033, NULL, 'der-taaen-na-dar-khulyu-hin-jo', 'دير تائين نہ در کليو هن جو\r\nدير تائين اکين ۾ ماٽ هلي!', NULL, 'sd', '2024-04-10 12:27:53', '2024-04-10 12:27:53', NULL),
(5230, 1033, NULL, 'raat-saari-patang-aayaa-wiya', 'رات ساري پتنگ آيا ويا\r\nرات ساري ڏِيئي جي لاٽ هلي!', NULL, 'sd', '2024-04-10 12:27:53', '2024-04-10 12:27:53', NULL),
(5231, 1033, NULL, 'zindagia-saan-b-tunhinjo-thaah-na-thiyo', 'زندگيءَ سان بہ تنھنجو ٺاھ نہ ٿيو \r\nزندگيءَ سان هلي ته ٻاٽ هلي!', NULL, 'sd', '2024-04-10 12:27:53', '2024-04-10 12:27:53', NULL),
(5232, 1033, NULL, 'dil-thaki-hui-takay-takay-beerryun', 'دل ٿڪي هئي تڪي تڪي ٻيڙيون\r\nدل ٿڪي نيٺ گهاٽ گهاٽ هلي!', NULL, 'sd', '2024-04-10 12:27:53', '2024-04-10 12:27:53', NULL),
(5233, 1034, 36, 'tunhinjo-gaddjanr-huyu-azaaly-jiyaan', 'تنھنجو گڏجڻ ھيو ازالي جيان\r\nزندگيءَ ڪيترو نہ جاليو ھو!', NULL, 'sd', '2024-04-10 12:49:21', '2024-04-10 12:49:21', NULL),
(5234, 1034, 36, 'saath-kanhin-je-jaddahin-b-thukraayo', 'ساٿ ڪنھن جي جڏھن بہ ٺڪرايو\r\nمونکي آوارگيءَ سنڀاليو ھو!', NULL, 'sd', '2024-04-10 12:49:21', '2024-04-10 12:49:21', NULL),
(5235, 1034, 36, 'chanddu-ko-baaru-aahe-najaaiz', 'چنڊ ڪو ٻار آھي ناجائز\r\nجنھن کي تارن جي ڍير پاليو ھو!', NULL, 'sd', '2024-04-10 12:49:21', '2024-04-10 12:49:21', NULL),
(5236, 1035, 36, 'roz-kaa-naen-tuhmat-roz-ko-naon-ilzaam', 'روز ڪا نئين تھمت، روز ڪو نئون الزام،\r\nعشق جي تماشي جو، ٿئي ڀلا تہ ڇا انجام!', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5237, 1035, 36, 'zindagia-je-talkhia-khe-thaa-peeyun-na-haaryoon-thaa', 'زندگيءَ جي تلخيءَ کي ٿا پيئون، نہ ھاريون ٿا،\r\nبس ائين گذاريون ٿا، نينھن کي ڪري ناڪام.', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5238, 1035, 36, 'husan-je-inayat-joon-chhaa-ganrai-ganryun-ggaalhiyun', 'حُسن جي عنايت جون، ڇا ڳڻي ڳڻيون ڳالهيون،\r\nلطف ھو اذيت ۾، درد ۾ ھيو آرام.', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5239, 1035, 36, 'jeet-khe-malhainr-je-reet-ajj-ta-retti-seen', 'جيت کي ملهائڻ جي، رِيت اڄ تہ ريٽي سين،\r\nھارَ جو جشن آھي، جامَ تي پيئون ٿا جام.', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5240, 1035, 36, 'ishiq-dhaku-aeban-jo-par-safar-saleban-jo', 'عشق ڍَڪُ عيبن جو، پر سفر صيلبن جو،\r\nٿو مرِي جيئي ماڻهو، ٿو نچي ڪري ماتام.', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5241, 1035, 36, 'aetro-udaamanr-saan-asmaan-kaawiria', 'ايترو اڏامڻ سان، آسمان ڪاوِڙيا،\r\nدل! ڪٿي تہ ڪر ڪو ديرو، ويھُ، ڪُجهہ تہ ڪر وسرام.', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5242, 1035, 36, 'shaairia-je-soday-men-zindagia-jo-ghaato-thiyo', 'شاعريءَ جي سودي ۾، زندگيءَ جو گهاٽو ٿيو،\r\nشھرتن جي خوشفھمي، لوڪ جي اڳيان بدنام!', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5243, 1035, 36, 'tun-pucheen-taaruf-tho-keru-Aa-iho-ishaq', 'تون پڇين تعارف ٿو، ڪير آ اھو اسحاق؟\r\nشھر ۾ اٿئي مشھور، شاعرن ۾ ٿئي گمنام.', NULL, 'sd', '2024-04-10 14:19:38', '2024-04-10 14:19:38', NULL),
(5247, 1034, 36, 'tunhinjo-gaddjanr-huyu-azaaly-jiyaan', 'tunhinjo gaddjanr huyu azaaly jiyaan\r\nzindagi\'a ketro na jaalio ho!', NULL, 'en', '2024-04-11 08:12:36', '2024-04-23 11:31:06', NULL),
(5248, 1034, 36, 'saath-kanhin-je-jaddahin-b-thukraayo', 'saath kanhin je jaddahin b thukraayo\r\nmunkhe awaargi\'a sanbhalyo ho!', NULL, 'en', '2024-04-11 08:12:36', '2024-04-23 11:30:19', NULL),
(5249, 1034, 36, 'chanddu-ko-baaru-aahe-najaaiz', 'ab chandd\'u ko baaru aahe najaaiz\r\njahin khe taaran je dherr paalio ho!', NULL, 'en', '2024-04-11 08:12:36', '2024-04-23 11:32:08', NULL),
(5250, 1033, NULL, 'hin-je-ghar-taaen-mun-saan-waatt-hali', 'hin je ghar taaen mun saan waatt hali! \r\npoi hiku dil hali, bbi kaatt hali!', NULL, 'en', '2024-04-21 12:34:25', '2024-04-21 14:35:55', NULL),
(5251, 1033, NULL, 'der-taaen-na-dar-khulyu-hin-jo', 'der taaen na dar khulyu hin jo\r\nder taaen akhiyan men maat hali!', NULL, 'en', '2024-04-21 12:34:25', '2024-04-21 14:35:55', NULL),
(5252, 1033, NULL, 'raat-saari-patang-aayaa-wiya', 'raat saari patang aayaa wiya\r\nraat saari diye je laat hali!', NULL, 'en', '2024-04-21 12:34:25', '2024-04-21 14:35:55', NULL),
(5253, 1033, NULL, 'zindagia-saan-b-tunhinjo-thaah-na-thiyo', 'zindagi\'a saan b tunhinjo thaah na thiyo \r\nzindagi\'a saan hali ta bbatt hali!', NULL, 'en', '2024-04-21 12:34:25', '2024-04-21 14:35:55', NULL),
(5254, 1033, NULL, 'dil-thaki-hui-takay-takay-beerryun', 'dil thaki hui takay takay beerryun\r\ndil thaki neth ghaaat ghaaat hali!', NULL, 'en', '2024-04-21 12:34:25', '2024-04-21 14:35:55', NULL);
