(17, 17, 'درد ھڪ ڏينھن ڀيڙ ڪرندو مون منجهہ', '', '', 'sd', NULL, NULL),
(18, 18, 'اک آلاڻ، مان ڏسان چھرو', '', '', 'sd', NULL, NULL),
